INSERT INTO `hadiths` (`id`, `book_id`, `chapter_id`, `section_id`, `narrator`, `body`, `note`, `numberPrimary`, `numberSecondary`, `reference`, `scholarReviewed`, `validity`, `createdAt`, `updatedAt`, `createdBy`, `updatedBy`, `deletedBy`, `explanation`, `crossChecked`) VALUES
(5, 1, 1, 1, '‘আলক্বামাহ ইব্‌নু ওয়াক্কাস আল-লায়সী (রহ.)', 'আমি ‘উমর ইব্‌নুল খাত্তাব (রাঃ)-কে মিম্বারের উপর দাঁড়িয়ে বলতে শুনেছিঃ আমি আল্লাহর রসূল (সাল্লাল্লাহু আলাইহি ওয়া সাল্লাম)-কে বলতে শুনেছিঃ [কাজ (এর প্রাপ্য হবে) নিয়্যাত অনুযায়ী। আর মানুষ তার নিয়্যাত অনুযায়ী প্রতিফল পাবে। তাই যার হিজরাত হবে ইহকাল লাভের অথবা কোন মহিলাকে বিবাহ করার উদ্দেশ্যে- তবে তার হিজরাত সে উদ্দেশ্যেই হবে, যে জন্যে, সে হিজরাত করেছে।] \r\n(৫৪, ২৫২৯, ৩৮৯৮, ৫০৭০, ৬৬৮৯, ৬৯৫৩; মুসলিম ২৩/৪৫ হাঃ ১৯০৭, আহমাদ ১৬৮)', NULL, '1', '1', '১\r\n১', 0, 40, '2013-11-20 21:16:06', '2013-11-21 15:33:52', 'anon.', 'anon.', 'anon.', 'ব্যাখ্যাঃ\r\nশারী’আহর মূল উৎস হচ্ছে ওয়াহী। ওয়াহী দু’ প্রকার। ওয়াহী মাতলু (আল-কুরান) ও ওয়াহী গাইরে মাতলু (সুন্নাহ ও হাদীস), এবং দ্বীনে ইলাহীর ভিত্তি শুধুমাত্র দু’টি জিনিসের উপর প্রতিষ্ঠিত। ইজমা’ ও কিয়াস কোন শার’ঈ দলীল নয়। বরং যে কিয়াস এবং ইজমা ওয়াহীর পক্ষে অর্থাৎ কুরআন ও সুন্নাহ মুতাবিক হবে তা গ্রহণযোগ্য এবং যেটা বিপক্ষে যাবে সেটা পরিত্যাজ্য ও অগ্রহণযোগ্য। এ প্রসংগে আল্লাহ তা’আলার বাণীঃ\r\n(আরবি)\r\nকিন্তু বাতিল ফির্কার লোকেরা ইজমা’ ও কিয়াসকে ওয়াহীর আসনে বসিয়েছে এবং বলে থাকেঃ শারী’আহর ভিত্তি চারটি বিষয়ের উপর। কুরআন, সুন্নাহ, ইজমা’ ও কিয়াস।  বড় আশ্চর্যের বিষয় এই যে, সাহাবায়ে কেরাম যাদের উপর আল্লাহ তা’আলা তাঁর সন্তুষ্টির ঘোষণা দিয়েছেন, তাদেরকে সত্যবাদী বলে স্বীকৃতি দেয়া হয়েছে এবং মুসলিম উম্মাহ এ ব্যাপারে সকলেই একমত। অথচ তারা সাহাবায়ে কেরামকে দু’ ভাগে ভাগ করেছেন। (১) ফকীহ (২) গাইরে ফকীহ। আর বলেছেন যে সকল সাহাবী ফকীহ ছিলেন তাঁরা যদি কিয়াসের বিপরীতে হাদীস বর্ণনা করেন তবে তা গ্রহনযোগ্য কিন্তু যে সকল সাহাবী গাইরে ফকীহ অর্থাৎ ফকীহ নন তাঁরা যদি কিয়াসের খেলাফ হাদীস বর্ণনা করেন তাহলে তা গ্রহণযোগ্য বলে বিবাচিত হবে না। \r\nপ্রকৃতপক্ষে এটা উম্মাতে মুহাম্মাদিয়াহকে সিরাতে মুস্তাকীমের পথ হতে সরিয়ে দেয়ার একটা বড় অস্ত্র এবং পরিকল্পনা। কেননা তাঁরা কিয়াসকে মূল এবং হাদীসকে দ্বিতীয় স্থানে রেখেছেন। সকল সাহাবীর উপর আল্লাহ তা’আলা সন্তুষ্ট কিন্তু তারা খুশী নন। সকল সাহাবীর উপর উম্মতের ঐকমত্য রয়েছে। কিন্তু তাদের নিকট গাইরে ফকীহ সাহাবীগণ ‘আদিল নন।\r\nধোঁকাবাজির কিছু নমুনাঃ তারা বলেন, ফকীহ সাহাবীগণ কিয়াসের খেলাফ হাদীস বর্ণনা করলে তা গ্রহণীয় হবে। কিন্তু গাইরে ফকীহ সাহাবীগণ কিয়াসের খেলাফ হাদীস বর্ণনা করলে তা বাতিল হয়ে যাবে এবং কিয়াসের উপর ‘আমল করতে হবে। বাই’য়ি মুসারাহ এর হাদীস আবূ হুরাইরাহ (রাঃ) হতে বর্ণিত এবং তা কিয়াসের খেলাফ। এই জন্য তা বাতিল। এবং কিয়াসের উপর ‘আমলযোগ্য। অথচ এই হাদীস ‘আবদুল্লাহ ইব্‌নু মাস’ঊদ (রাঃ) হতেও বর্ণিত হয়েছে।   \r\n(দেখুন সহীহ বুখারী ২৮৮ পৃষ্ঠা রশিদিয়া ছাপা)', 0),
(6, 1, 1, 2, 'উম্মুল মু’মিনীন ‘আয়িশাহ (রাঃ)', 'হারিস ইব্‌নু হিশাম (রাঃ) আল্লাহর রসূল (সাল্লাল্লাহু আলাইহি ওয়া সাল্লাম)- কে জিজ্ঞেস করলেন, ‘হে আল্লাহর রসূল! আপনার নিকট ওয়াহী কিরূপে আসে?’ আল্লাহর রসূল (সাল্লাল্লাহু আলাইহি ওয়া সাল্লাম) বললেনঃ [কোন কোন সময় তা ঘন্টা বাজার মত আমার নিকট আসে। আর এটি-ই আমার উপর সবচেয়ে বেদনাদায়ক হয় এবং তা শেষ হতেই মালাক (ফেরেশতা) যা বলেন তা আমি মুখস্ত করে নেই, আবার কখনো মালাক মানুষের রূপ ধারণ করে আমার সাথে কথা বলেন। তিনি যা বলেন আমি তা মুখস্ত করে নেই।] ‘আয়িশাহ (রাঃ) বলেন, আমি তীব্র শীতের সময় ওয়াহী নাযিলরত অবস্থায় তাঁকে দেখেছি। ওয়াহী শেষ হলেই তাঁর ললাট হতে ঘাম ঝরে পড়ত। \r\n(৩২১৫; মুসলিম ৪৩/২৩, হাঃ ২৩৩৩, আহমাদ ২৫৩০৭, ২৬২৫৮) (আ.প্র. ২, ই.ফা. ২)', NULL, '2', '2', NULL, 0, 40, '2013-11-20 21:58:01', '2013-11-20 21:58:01', 'anon.', 'anon.', 'anon.', NULL, 0),
(7, 1, 1, 3, 'উম্মুল মু’মিনীন ‘আয়িশাহ (রাঃ)', 'তিনি বলেন, আল্লাহর রসূল (সাল্লাল্লাহু আলাইহি ওয়া সাল্লাম)- এর নিকট সর্বপ্রথম যে ওয়াহী আসে, তা ছিল নিদ্রাবস্থায় বাস্তব স্বপ্নরূপে। যে স্বপ্নই তিনি দেখতেন তা একেবারে প্রভাতের আলোর ন্যায় প্রকাশিত হতো। অতঃপর তাঁর নিকট নির্জনতা পছন্দনীয় হয়ে দাঁড়ায় এবং তিনি ‘হেরা’র গুহায় নির্জনে অবস্থান করতেন। আপন পরিবারের নিকট ফিরে এসে কিছু খাদ্যসামগ্রী সঙ্গে নিয়ে যাওয়ার পূর্বে- এভাবে সেখানে তিনি এক নাগাড়ে বেশ কয়েক দিন ‘ইবাদাতে মগ্ন থাকতেন। অতঃপর  খাদীজাহ (রাঃ)-এর নিকট  ফিরে এসে আবার একই সময়ের জন্য কিছু খাদ্যদ্রব্য নিয়ে যেতেন। এভাবে ‘হেরা’ গুহায় অবস্থানকালে তাঁর নিকট ওয়াহী আসলো।  তাঁর নিকট ফেরেশতা এসে বললো, ‘পাঠ করুন’। আল্লাহর রসূল (সাল্লাল্লাহু আলাইহি ওয়া সাল্লাম) বলেনঃ [“আমি বললাম, ‘আমি পড়তে জানি না।] তিনি (সাল্লাল্লাহু আলাইহি ওয়া সাল্লাম) বলেনঃ অতঃপর সে আমাকে জড়িয়ে ধরে এমন ভাবে চাপ দিলো যে, আমার খুব কষ্ট হলো। অতঃপর সে আমাকে ছেড়ে দিয়ে বললো, ‘পাঠ করুন’। আমি বললামঃ আমি তো পড়তে জানি না’। সে দ্বিতীয় বার আমাকে জড়িয়ে ধরে এমনভাবে চাপ দিলো যে, আমার খুব কষ্ট হলো। অতঃপর সে আমাকে ছেড়ে দিয়ে  বললো, ‘পাঠ করুন’। আমি উত্তর দিলাম, ‘আমি তো পড়তে জানি না’। আল্লাহর রসূল (সাল্লাল্লাহু আলাইহি ওয়া সাল্লাম) বলেন, অতঃপর তৃতীয়বারে তিনি আমাকে জড়িয়ে ধরে চাপ দিলেন। তারপর ছেড়ে দিয়ে বললেন, “পাঠ করুন আপনার রবের নামে, যিনি সৃষ্টি করেছেন। যিনি সৃষ্টি করেছেন মানুষকে জমাট রক্ত পিন্ড থেকে, পাঠ করুন, আর আপনার রব অতিশয় দয়ালু”- (সূরাহ্‌ আলাক্ব ৯৬/১-৩)। অতঃপর এ আয়াত নিয়ে আল্লাহর রসূল (সাল্লাল্লাহু আলাইহি ওয়া সাল্লাম)- প্রত্যাবর্তন করলেন। তাঁর হৃদয় তখন কাঁপছিল। তিনি খাদীজাহ বিন্‌তু খুওয়ায়লিদের নিকট এসে বললেন, ‘আমাকে চাদর দ্বারা আবৃত কর’, ‘আমাকে চাদর দ্বারা আবৃত কর’। তাঁরা তাঁকে চাদর দ্বারা আবৃত করলেন। এমনকি তাঁর শংকা দূর হলো। তখন তিনি খাদীজাহ (রাঃ) এর নিকট ঘটনাবৃত্তান্ত জানিয়ে তাঁকে বললেন, আমি আমার নিজেকে নিয়ে শংকা বোধ করছি। খাদীজাহ (রাঃ) বললেন, আল্লাহর কসম, কখনই নয়। আল্লাহ আপনাকে কখনো লাঞ্ছিত করবেন না।  আপনি তো আত্মীয়–স্বজনের সঙ্গে সদাচরণ করেন, অসহায় দুস্থদের দায়িত্ব বহন করেন, নিঃস্বকে সহযোগিতা করেন, মেহমানের আপ্যায়ন করেন এবং হক পথের দুর্দশাগ্রস্থকে সাহায্য করেন। অতঃপর তাঁকে নিয়ে খাদীজাহ (রাঃ) তাঁর চাচাত ভাই ওয়ারাকাহ ইব্‌নু নাওফাল ইব্‌নু ‘আবদুল আসাদ ইব্‌নু ‘আবদুল ‘উযযাহ’র নিকট গেলেন, যিনি অন্ধকার যুগে ‘ঈসায়ী ধর্ম গ্রহন করেছিলেন। যিনি ইবরানী ভাষায় লিখতে পারতেন এবং আল্লাহর তাওফীক অনুযায়ী ইবরানী ভাষায় ইনজীল হতে ভাষান্তর করতেন। তিনি ছিলেন অতি বৃদ্ধ এবং অন্ধ হয়ে গিয়েছিলেন। খাদীজা (রাঃ) তাঁকে বললেন, ‘হে চাচাত ভাই! আপনার ভাতিজার কথা শুনুন’। ওয়ারাকাহ তাঁকে জিজ্ঞেস করলেন, ‘ভাতিজা! তুমি কী দেখ?’ আল্লাহর রসূল (সাল্লাল্লাহু আলাইহি ওয়া সাল্লাম) যা দেখেছিলেন, সবই বর্ণনা করলেন। তখন ওয়ারাকাহ তাঁকে বললেন, এটা সেই বার্তাবাহক যাকে আল্লাহ মূসা (‘আ.)- এর নিকট পাঠিয়েছিলেন। আফসোস! আমি যদি সেদিন যুবক থাকতাম। আফসোস ! আমি যদি সেদিন জীবিত থাকতাম, যেদিন তোমার কওম তোমাকে বহিষ্কার করবে’। আল্লাহর রসূল (সাল্লাল্লাহু আলাইহি ওয়া সাল্লাম) বললেন, [‘তারা কি আমাকে বের করে দেবে?’] তিনি বললেন, ‘হ্যাঁ, তুমি যা নিয়ে এসেছো অনুরূপ (ওয়াহী) কিছু যিনিই নিয়ে এসেছেন তাঁর সঙ্গেই বৈরিতাপূর্ণ আচরণ করা হয়েছে। সেদিন যদি আমি থাকি, তবে তোমাকে জোরালোভাবে সাহায্য করব। এর কিছুদিন পর ওয়ারাকাহ (‘আ.) ইন্তিকাল করেন। আর ওয়াহীর বিরতি ঘটে। \r\n(৩৩৯২, ৪৯৫৩, ৪৯৫৫, ৪৯৫৬, ৪৯৫৭। ৬৯৮২; মুসলিম ১/৭৩ হাঃ ১৬০, আহমাদ ২৬০১৮)', NULL, '3', '3', '৩\r\n৩', 0, 40, '2013-11-20 22:05:03', '2013-11-20 22:05:03', 'anon.', 'anon.', 'anon.', NULL, 0),
(8, 1, 1, 3, 'জাবির ইব্‌নু ‘আবদুল্লাহ্‌ আনসারী (রাঃ)', 'ওয়াহী স্থগিত হওয়া প্রসঙ্গে বর্ণনা করেন যে, আল্লাহর রসূল (সাল্লাল্লাহু আলাইহি ওয়া সাল্লাম) বলেছেনঃ একদা আমি হাঁটছি , হঠাৎ আসমান থেকে একটা শব্দ শুনতে পেয়ে আমার দৃষ্টিকে উপরে তুললাম। দেখলাম, সেই ফেরেশতা, যিনি হেরা গুহায় আমার নিকট এসেছিলেন, আসমান ও যমীনের মাঝে একটি আসনে উপবিষ্ট। এতে আমি শংকিত হলাম। অবিলম্বে আমি ফিরে এসে বললাম, ‘আমাকে চাদর দ্বারা আবৃত কর’, ‘আমাকে চাদর দ্বারা আবৃত কর’। অতঃপর আল্লাহ তা’আলা অবতীর্ণ করলেন,  “হে বস্ত্রাবৃত রসূল! (১) উঠুন, সতর্ক করুন; আর আপানার রবের শ্রেষ্ঠত্ব  ঘোষণা করুন; এবং স্বীয় পরিধেয় বস্ত্র পবিত্র রাখুন; (৫) এবং অপবিত্রতা থেকে দূরে থাকুন”। (সূরাহ মুদ‌্দাসসির ৭৪/১-৫) অতঃপর ওয়াহী পুরোদমে ধারাবাহিক অবতীর্ণ হতে লাগল। ‘আবদুল্লাহ ইব্‌নু ইউসূফ (রহ.) ও আবূ সালেহ (রহ.) অনুরূপ বর্ণনা করেছেন। হিলাল ইব্‌নু রাদদাদ (রহ) যুহরী (রহ) থেকেও অনুরূপ বর্ণনা করেছেন। হিলাল ইব্‌নু রাদদাদ (রহ) যুহরী থেকেও অনুরূপ বর্ণনা করেছেন। ইউনুস ও মা’মার ----- এর স্থলে ------ শব্দ উল্লেখ করেছেন। \r\n(৩২৩৮, ৪৯২২, ৪৯২৩, ৪৯২৪, ৪৯২৫, ৪৯২৬, ৪৯৫৪, ৬২১৪; মুসলিম ১/৩৮ হাঃ ১৬১, আহমাদ ১৫০৩৯)', NULL, '4', '4', '৩ শেষাংশ\r\n৩ শেষাংশ', 0, 40, '2013-11-20 22:05:05', '2013-11-20 22:05:05', 'anon.', 'anon.', 'anon.', NULL, 0),
(9, 1, 1, 4, 'ইব্‌নু আব্বাস (রাঃ)', 'মহান আল্লাহর বাণীঃ “ওয়াহী দ্রুত আয়ত্ত করার জন্য আপনি ওয়াহী নাযিল হওয়ার সময় আপনার জিহ্বা নাড়বেন না”। (সূরাহ কিয়ামাহ ৭৫/১৬)-এর ব্যাখ্যায় ইব্‌নু ‘আব্বাস বলেন, আল্লাহর রসূল (সাল্লাল্লাহু আলাইহি ওয়া সাল্লাম) ওয়াহী আবতরণের সময় তা আয়ত্ত করতে বেশ কষ্ট করতেন এবং প্রায়ই তিনি তাঁর উভয় ঠোঁট নড়াতেন’। ইব্‌নু ‘আব্বাস (রাঃ) বলেন, ‘আমি তোমাকে দেখানোর জন্য ঠোঁট দুটি নাড়ছি যেভাবে আল্লাহর রসূল (সাল্লাল্লাহু আলাইহি ওয়া সাল্লাম) তা নড়াতেন’। সা’ঈদ (রহ.) (তাঁর শিষ্যদের) বলেন, ‘আমি ইব্‌নু ‘আব্বাস (রাঃ)-কে যেরূপে তাঁর ঠোঁট দুটি নড়াতে দেখেছি, সেভাবেই আমার ঠোঁট দুটি নড়াচ্ছি’।  এই বলে তিনি তাঁর ঠোঁট দুটি নড়ালেন। এ সম্পর্কে আল্লাহ তা’আলা অবতীর্ণ করলেনঃ “ওয়াহী দ্রুত আয়ত্ত করার জন্য আপনি ওয়াহী নাযিল হবার সময় আপনার জিহ্বা নড়াবেন না, এর সংরক্ষণ ও পাঠ করানোর দায়িত্ব আমার”। (সূরাহ ক্বিয়ামাহ ৭৫/১৬)। ইব্‌নু ''আব্বাস (রাঃ) বলেন, “এর অর্থ হলোঃ তোমার অন্তরে তা হেফাযত করা এবং তোমার দ্বারা তা পাঠ করানো। “সুতরাং আমি যখন তা পাঠ করি, তখন আপনি সেই পাঠের অনুসরণ করুন”- (সূরাহ ক্বিয়ামাহ ৭৫/১৮)। ইব্‌নু আব্বাস (রাঃ) বলেন, অর্থাৎ মনোযোগ সহকারে শুন এবং চুপ থাক। “তারপর এর বিশদ বর্ণনার দায়িত্ব তো আমারই”-(সূরাহ ক্বিয়ামাহ ৭৫/১৯)। অর্থাৎ তুমি তা পাঠ করবে, এটাও আমার দায়িত্ব। তারপর যখন আল্লাহর রসূল (সাল্লাল্লাহু আলাইহি ওয়া সাল্লাম)- এর নিকট জিবরীল (‘আ.) আসতেন, তখন তিনি মনোযোগ দিয়ে কেবল শুনতেন। জিবরীল (‘আ.) চলে যাবার পর তিনি যেমন পাঠ করেছিলেন, আল্লাহর রসূল (সাল্লাল্লাহু আলাইহি ওয়া সাল্লাম)-ও তদ্রুপ পাঠ করতেন। \r\n(৪৯২৭, ৪৯২৮, ৪৯২৯, ৫০৪৪, ৭৫২৪; মুসলিম ৪/৩২ হাঃ ৪৪৮, আহমাদ ৩১৯১)', NULL, '5', '5', '৪\r\n৪-এর শেষাংশ', 0, 40, '2013-11-20 22:08:27', '2013-11-20 22:08:27', 'anon.', 'anon.', 'anon.', NULL, 0),
(10, 1, 1, 5, 'ইব্‌নু ‘আব্বাস (রাঃ)', 'তিনি বলেন আল্লাহর রসূল (সাল্লাল্লাহু আলাইহি ওয়া সাল্লাম) ছিলেন সর্বশ্রেষ্ঠ দানশীল। রমাযানে তিনি আরো অধিক দানশীল হতেন, যখন জিবরীল (‘আ.) তাঁর সঙ্গে সাক্ষাৎ করতেন। আর রমাযানের প্রতি রাতেই জিবরীল (‘আ.) তাঁর সাথে দেখা করতেন এবং তারা একে অপরকে কুরআন তিলাওয়াত করে শোনাতেন। নিশ্চয়ই আল্লাহর রসূল (সাল্লাল্লাহু আলাইহি ওয়া সাল্লাম) রহমতের বায়ু অপেক্ষাও অধিক দানশীল ছিলেন। \r\n(১৯০২, ৩২২০, ৩৫৫৪, ৪৯৯৭; মুসলিম ৪৩/১২ হাঃ ৩২০৮, আহমাদ ৩৬১৬, ৩৪২৫)', NULL, '6', '6', '৫\r\n৫', 0, 40, '2013-11-20 22:10:09', '2013-11-20 22:10:09', 'anon.', 'anon.', 'anon.', NULL, 0),
(11, 1, 1, 6, '‘আবদুল্লাহ ইব্‌নু ‘আব্বাস (রা.)', 'আবূ সুফিয়ান ইব্‌নু হরব তাকে বলেছেন, রাজা হিরাক্লিয়াস একদা তাঁর নিকট লোক প্রেরণ করলেন। তিনি তখন ব্যবসা উপলক্ষে কুরাইশদের কাফেলায় সিরিয়ায় ছিলেন। আল্লাহর রসূল (সাঃ) সে সময় আবূ সুফিয়ান ও কুরাইশদের সঙ্গে নির্দিষ্ট সময়ের জন্য সন্ধিতে আবদ্ধ ছিলেন। আবূ সুফিয়ান তার সাথী সহ হিরাক্লিয়াসের নিকট আসলেন এবং দোভাষীকে ডাকলেন। অতঃপর জিজ্ঞেস করলেন, ‘এই যে ব্যক্তি নিজেকে নবী বলে দাবী করে-তোমাদের মাঝে বংশের দিক হতে তাঁর সবচেয়ে  নিকটাত্মীয় কে’? আবূ সুফিয়ান বলেন, ‘আমি বললাম, বংশের দিক দিয়ে আমিই তাঁর নিকটাত্মীয়’। তিনি বললেন, ‘তাঁকে আমার অতি নিকটে আন এবং তাঁর সাথীদেরকেও তার পেছনে বসিয়ে দাও’।\r\nঅতঃপর তাঁর দোভাষীকে বললেন, ‘তাদের বলে দাও, আমি এর নিকট সে ব্যক্তি সম্পর্কে কিছু জিজ্ঞেস করব, যদি সে আমার নিকট মিথ্যা বলে, তখন সঙ্গে সঙ্গে তোমরা তাকে মিথ্যুক বলবে। আবূ সুফিয়ান বলেন, ‘আল্লাহর কসম! আমার যদি এ লজ্জা না থাকত যে, তারা আমাকে মিথ্যাবাদী বলে প্রচার করবে, তবে আমি অবশ্যই তাঁর সম্পর্কে মিথ্যা বলতাম’।\r\nঅতঃপর তিনি তাঁর সম্পর্কে  আমাকে সর্বপ্রথম যে প্রশ্ন করেন তা হলো, ‘বংশমর্যাদার দিক দিয়ে তোমাদের মধ্যে সে কিরূপ?’ আমি বললাম, ‘তিনি আমাদের মধ্যে খুব সম্ভ্রান্ত বংশের’। তিনি বললেন, ‘তোমাদের মধ্যে এর পূর্বে আর কখনো কি কেউ এরূপ কথা বলেছে?’ আমি বললাম, ‘না’। তিনি বললেন, ‘তাঁর পূর্বপুরুষের মধ্যে কেউ কি বাদশাহ ছিলেন?’ আমি বললাম, ‘না’। তিনি বললেন, ‘সম্ভ্রান্ত মর্যাদাবান শ্রেণীর লোকেরা তাঁর অনুসরণ করে, নাকি দুর্বল লোকেরা?’ আমি বললাম, ‘দুর্বল লোকেরা’। তিনি বললেন, ‘তাদের সংখ্যা কি বাড়ছে, না কমছে?’ আমি বললাম, ‘তারা বেড়েই চলছে’। তিনি বললেন, ‘তাঁর ধর্মে ঢুকে কেউ কি অসন্তুষ্ট হয়ে তা ত্যাগ করে?’ আমি বললাম, ‘না’। তিনি বললেন, ‘তাঁর দাবীর পূর্বে তোমরা কি কখনো তাঁকে মিথ্যার অভিযোগে অভিযুক্ত করেছ?’ আমি বললাম, ‘না’। তিনি বললেন, ‘তিনি কি সন্ধি ভঙ্গ করেন?’ আমি বললাম, ‘না’। তবে আমরা তাঁর সঙ্গে একটি নির্দিষ্ট সময়ের সন্ধিতে আবদ্ধ আছি। জানি না এর মধ্যে তিনি কি করবেন’। আবূ সুফিয়ান বলেন, ‘এ কথাটি ব্যতীত নিজের পক্ষ হতে আর কোন কথা যোগ করার সু্যোগই আমি পাইনি’। তিনি বললেন, ‘তোমরা তাঁর সঙ্গে কখনো যুদ্ধ করেছ কি?’ আমি বললাম, ‘হ্যাঁ’। তিনি বললেন, ‘তাঁর সঙ্গে তোমাদের যুদ্ধের পরিণাম কি হয়েছে?’ আমি বললাম, ‘তাঁর ও আমাদের মধ্যে যুদ্ধের ফলাফল কুপের বালতির ন্যায়’। কখনো তাঁর পক্ষে যায়, আবার কখনো আমাদের পক্ষে আসে’। তিনি বললেন, ‘তিনি তোমাদের কিসের আদেশ দেন?’ আমি বললাম, ‘তিনি বলেনঃ তোমরা এক আল্লাহর ইবাদত কর এবং তাঁর সঙ্গে কোন কিছুর অংশীদার সাব্যস্ত করো না এবং তোমাদের পূর্বপুরুষরা যা বলে তা ত্যাগ কর। আর তিনি আমাদের সালাত আদায়ের, সত্য বলার, চারিত্রিক নিষ্কলুষতার এবং আত্মীয়দের সঙ্গে সদাচরণ করার নির্দেশ দেন’।\r\nঅতঃপর তিনি দোভাষীকে বললেন, ‘তুমি তাকে বল, আমি তোমার নিকট তাঁর বংশ সম্পর্কে জিজ্ঞেস করেছি। তুমি তার জবাবে উল্লেখ করেছ যে, তিনি তোমাদের মধ্যে সম্ভ্রান্ত বংশের। প্রকৃতপক্ষে রসূলগণকে তাঁদের কওমের উচ্চ বংশেই পাঠানো হয়ে থাকে। তোমাকে জিজ্ঞেস করেছি, এ কথা তোমাদের মধ্যে ইতিপূর্বে আর কেউ বলেছে কিনা? তুমি বলেছ, ‘না’। তাই আমি বলছি, পূর্বে যদি কেউ এরূপ বলত, তবে আমি অবশ্যই বলতাম, ইনি এমন এক ব্যক্তি, যিনি তাঁর পুর্বসূরীর কথারই অনুসরণ করছেন। আমি তোমাকে জিজ্ঞেস করেছি, তাঁর পূর্বপুরুষদের মধ্যে কোন  বাদশাহ ছিলেন কিনা? তুমি তার জবাবে বলেছ, ‘না’। তাই আমি বলছি যে, তাঁর পূর্বপুরুষের মধ্যে যদি কোন বাদশাহ থাকতেন, তবে আমি বলতাম, ইনি এমন এক ব্যক্তি যিনি তাঁর বাপ-দাদার বাদশাহী ফিরে পেতে চান। আমি তোমাকে জিজ্ঞেস করেছি-এর পূর্বে কখনো তোমরা তাঁকে মিথ্যার অভিযোগে অভিযুক্ত করেছ কিনা? তুমি বলেছ, ‘না’। এতে আমি বুঝলাম, এমনটি হতে পারে না যে, কেউ মানুষের ব্যাপারে মিথ্যা পরিত্যাগ করবে আর আল্লাহর ব্যাপারে মিথ্যা বলবে। আমি তোমাকে জিজ্ঞেস করেছি, সম্ভ্রান্ত লোক তাঁর অনুসরণ করে, না সাধারণ লোক? তুমি বলেছ, সাধারণ লোকই তাঁর অনুসরণ করে। আর বাস্তবেও এই শ্রেনীর লোকেরাই হন রসূলগণের অনুসারী। আমি তোমাকে জিজ্ঞেস করেছি, তারা সংখ্যায় বাড়ছে না কমছে? তুমি বলেছ, বাড়ছে। প্রকৃতপক্ষে ঈমানে পূর্ণতা লাভ করা পর্যন্ত এ রকমই হয়ে থাকে। আমি তোমাকে জিজ্ঞেস করেছি, তাঁর দীনে প্রবেশ করে কেউ কি অসন্তুষ্ট হয়ে তা ত্যাগ করে? তুমি বলেছ, ‘না’। ঈমানের স্নিগ্ধতা অন্তরের সঙ্গে মিশে গেলে ঈমান এরূপই হয়। আমি তোমাকে জিজ্ঞেস করেছি, তিনি সন্ধি ভঙ্গ করেন কিনা? তুমি বলেছ, ‘না’। প্রকৃতপক্ষে রসূলগণ এরূপই, সন্ধি ভঙ্গ করেন না। আমি তোমাকে জিজ্ঞেস করেছি, তিনি তোমাদের কিসের আদেশ দেন? তুমি বলেছ, তিনি তোমাদের এক আল্লাহর বন্দেগী করা ও তাঁর সঙ্গে অন্য কিছুর অংশীদার স্থাপন না করার নির্দেশ দেন। তিনি তোমাদের নিষেধ করেন মূর্তিপূজা করতে আর তোমাদের আদেশ করেন সালাত আদায় করতে, সত্য বলতে ও সচ্চরিত্র থাকতে। তুমি যা বলেছ তা যদি সত্যি হয়, তবে শীঘ্রই তিনি আমার দু’পায়ের নীচের জায়গার অধিকারী হবেন। আমি নিশ্চিত জানতাম, তাঁর আবির্ভাব হবে; কিন্তু তিনি যে তোমাদের মধ্য হতে হবেন, এ কথা ভাবতে পারিনি। যদি জানতাম, আমি তাঁর নিকট পৌছতে পারব, তাঁর সঙ্গে সাক্ষাৎ করার জন্য আমি যে কোন কষ্ট সহ্য করে নিতাম। আর আমি যদি তাঁর নিকট থাকতাম তবে অবশ্যই তাঁর দু’খানা পা ধৌত করে দিতাম। অতঃপর তিনি আল্লাহর রসূল (সাঃ)-এর সেই পত্রখানি আনার নির্দেশ দিলেন, যা তিনি দিহ্‌ইয়াতুল কালবী (রাযি.)-কে দিয়ে বসরার শাসকের মাধ্যমে হিরাক্লিয়াসের নিকট প্রেরণ করেছিলেন। তিনি তা পড়লেন। তাতে (লেখা) ছিলঃ\r\nবিসমিল্লা-হির রহমা-নির রহীম (পরম করুণাময় দয়ালু আল্লাহর নামে) । আল্লাহর বান্দা ও তাঁর রসূল মুহাম্মাদ (সাঃ)-এর পক্ষ হতে রোম সম্রাট হিরাক্লিয়াসের প্রতি। -শান্তি (বর্ষিত হোক) তার প্রতি, যে হিদায়াতের অনুসরণ করে। তারপর আমি আপনাকে ইসলামের দাওয়াত দিচ্ছি। ইসলাম গ্রহণ করুন, শান্তিতে থাকবেন। আল্লাহ আপনাকে দ্বিগুণ প্রতিদান দান করবেন। আর যদি মুখ ফিরিয়ে নেন, তবে সকল প্রজার পাপই আপনার উপর বর্তাবে।\r\n“হে আহলে কিতাব! এসো সে কথায় যা আমাদের ও তোমাদের মধ্যে এক ও অভিন্ন। তা হল, আমরা যেন আল্লাহ ছাড়া অন্য কারো ইবাদত না করি, কোন কিছুকেই যেন তাঁর শরীক সাব্যস্ত না করি এবং আমাদের কেউ যেন কাউকে পালনকর্তারূপে গ্রহণ না করে আল্লাহকে ত্যাগ করে। যদি তারা মুখ ফিরিয়ে নেয়, তবে তোমরা বল, “তোমরা সাক্ষী থাক, আমারা তো মুসলিম”। (সূরাহ্‌ আল-‘ইমরান ৩/৬৪)\r\nআবূ সুফিয়ান বলেন, ‘হিরাক্লিয়াস যখন তাঁর বক্তব্য শেষ করলেন এবং পত্র পাঠও শেষ করলেন, তখন সেখানে হট্টগোল শুরু হয়ে গেল, চীৎকার ও হৈ-হল্লা চরমে পৌছল এবং আমাদেরকে বের করে দেয়া হলো। আমাদেরকে বের করে দিলে আমি আমার সাথীদের বললাম, আবূ কাবশার [১] ছেলের বিষয় তো শক্তিশালী হয়ে উঠেছে, বনূ আসফার (রোম)-এর বাদশাহও তাকে ভয় পাচ্ছে! তখন থেকে আমি বিশ্বাস রাখতাম, তিনি শীঘ্রই জয়ী হবেন। অবশেষে আল্লাহ তা’আলা আমাকে ইসলাম গ্রহণের তাওফীক দান করলেন।\r\nইব্‌ন নাতূর ছিলেন জেরুযালেমের শাসনকর্তা এবং হিরাক্লিয়াসের বন্ধু ও সিরিয়ার খৃস্টানদের পাদ্রী। তিনি বলেন, হিরাক্লিয়াস যখন জেরুজালেম আসেন, তখন একদা তাঁকে অত্যন্ত মলিন দেখাচ্ছিল। তাঁর একজন বিশিষ্ট সহচর বলল, ‘আমরা আপনার চেহারা আজ এত মলিন দেখছি, ইব্‌নু নাতূর বলেন, হিরাক্লিয়াস ছিলেন জ্যোতির্বিদ, জ্যোতির্বিদ্যায় তাঁর দক্ষতা ছিল। তারা জিজ্ঞেস করলে তিনি তাদের বললেন, ‘আজ রাতে আমি তারকারাজির দিকে তাকিয়ে দেখতে পেলাম, খতনাকারীদের বাদশাহ আবির্ভূত হয়েছেন। বর্তমান যুগে কোন্‌ জাতি খাতনা করে’? তারা বলল, ‘ইয়াহূদ জাতি ব্যতীত কেউ খাতনা করে না। কিন্তু তাদের ব্যাপারে আপনি মোটেও চিন্তিত হবেন না। আপনার রাজ্যের শহরগুলোতে লিখে পাঠান, তারা যেন সেখানকার সকল ইয়াহূদীকে কতল করে ফেলে’। তারা যখন এ ব্যাপারে ব্যতিব্যস্ত ছিল, তখন হিরাক্লিয়াসের নিকট জনৈক ব্যক্তিকে হাযির করা হলো, যাকে গাস্‌সানের শাসনকর্তা পাঠিয়েছিল। সে আল্লাহর রসূল (সাঃ) সম্পর্কে খবর দিচ্ছিল। হিরাক্লিয়াস তার কাছ থেকে খবর জেনে নিয়ে বললেন, ‘তোমরা একে নিয়ে গিয়ে দেখ, তার খাতনা হয়েছে কি-না’। তারা তাকে নিয়ে গিয়ে দেখে এসে সংবাদ দিল, তার খতনা হয়েছে। হিরাক্লিয়াস তাকে আরবদের সম্পর্কে জিজ্ঞেস করলে সে জওয়াব দিল, ‘তারা খাতনা করে’। অতঃপর হিরাক্লিয়াস তাদের বললেন, ইনি [আল্লাহর রসূল (সাঃ)] এ উম্মতের বাদশাহ। তিনি আবির্ভূত হয়েছেন’। আতঃপর হিরাক্লিয়াস রোমে তাঁর বন্ধুর নিকট লিখলেন। তিনি জ্ঞানে তাঁর সমকক্ষ ছিলেন। পরে হিরাক্লিয়াস হিমস চলে গেলেন। হিমসে থাকতেই তাঁর নিকট তাঁর বন্ধুর চিঠি এলো, যা নবী (সাঃ)-এর আবির্ভাব এবং তিনিই যে প্রকৃত নবী, এ ব্যাপারে হিরাক্লিয়াসের মতকে সমর্থন করছিল। তারপর হিরাক্লিয়াস তাঁর হিমসের প্রাসাদে রোমের নেতৃস্থানীয় ব্যক্তিদের ডাকলেন এবং প্রাসাদের সকল দরজা বন্ধ করার আদেশ দিলে দরজা বন্ধ করা হলো। অতঃপর তিনি সম্মুখে এসে বললেন, হে রোমের অধিবাসী! তোমরা কি মঙ্গল, হিদায়াত এবং তোমাদের রাষ্ট্রের স্থায়িত্ব চাও? তাহলে এই নবীর বায়’আত গ্রহণ কর’। এ কথা শুনে তারা বন্য গাধার ন্যায় দ্রুত নিঃশ্বাস ফেলতে ফেলতে দরজার দিকে ছুটল, কিন্তু তারা তা বন্ধ দেখতে পেল। হিরাক্লিয়াস যখন তাদের অনীহা লক্ষ্য করলেন এবং তাদের ঈমান থেকে নিরাশ হয়ে গেলেন, তখন বললেন, ‘ওদের আমার নিকট ফিরিয়ে আন’। তিনি বললেন, ‘আমি একটু পূর্বে যে কথা বলেছি, তা দিয়ে তোমরা তোমাদের দীনের উপর কতটুকু অটল, কেবল তার পরীক্ষা করছিলাম। এখন তা দেখে নিলাম’। একথা শুনে তারা তাঁকে সাজদাহ করল এবং তাঁর প্রতি সন্তুষ্ট হলো। এটাই ছিল হিরাক্লিয়াসের সর্বশেষ অবস্থা।\r\nআবূ ‘আবদুল্লাহ [বুখারী (রহ.)] বলেন, সালিহ ইব্‌নু কায়সান (রহ.) , ইউনুস (রহ.) ও মা’মার (রহ.) এ হাদীস যুহরী (রহ.) থেকে রিওয়ায়াত করেছেন। \r\n(৫১, ২৬৮১, ২৮০৪, ২৯৪১, ২৯৭৮, ৩১৭৪, ৪৫৫৩, ৫৯৮০, ৬২৬০, ৭১৯৬, ৭৫৪১ দ্রষ্টব্য)', '[১] আবূ কাবশা : এ নামে জনৈক ব্যক্তি প্রতিমা পূজার বিরোধী ছিল বলে আল্লাহর রসূল (সাঃ)-কে তার ছেলে অর্থাৎ আবূ কাবশা বলা হয়েছে। এ মর্মে আরও কয়েকটি বর্ণনা রয়েছে।', '7', '7', '৬\r\n৬', 0, 40, '2013-11-20 22:14:03', '2013-11-20 22:14:03', 'anon.', 'anon.', 'anon.', NULL, 0),
(12, 1, 5, 8, 'ইবন ‘উমার (রাঃ)', 'তিনি বলেন, আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) ইরশাদ করেন, ইসলামের স্তম্ভ হচ্ছে পাঁচটি। ১. আল্লাহ ব্যতীত প্রকৃত কোন উপাস্য নেই এবং নিশ্চয়ই মুহাম্মাদ (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) আল্লাহর রসূল-এ কথার সাক্ষ্য প্রদান করা। ২. সলাত কায়িম করা। ৩. যাকাত আদায় করা। ৪. হাজ্জ সম্পাদন করা এবং ৫. রমযানের সিয়ামব্রত পালন করা (রোজা রাখা)। (৪৫৪১; মুসলিম ১/৫ হাঃ ১৬, আহমদ ৬০২২, ৬৩০৯)', '[১] কোন কোন ফকীহদের নিকট ঈমান বাড়েও না কমেও না। বরং সমান থাকে। তাদের নিকট একজন নবীর ঈমান ও ইবলিসের ঈমান এক সমান। তাদের এই ‘আকীদাহ কুরআন ও সুন্নাহ বিরোধী। এটা মুরজি’আহ সম্প্রদায়ের ভ্রান্ত ‘আকীদাহর অন্তর্ভুক্ত।', '8', '8', '৭\r\n৭', 0, 40, '2013-11-23 00:25:35', '2013-11-23 00:25:35', 'anon.', 'anon.', 'anon.', NULL, 1),
(13, 1, 5, 9, 'আবূ হুরাইরা (রাঃ)', 'নবী (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) বলেছেন, ঈমানের ষাটেরও অধিক শাখা আছে। আর লজ্জা হচ্ছে ঈমানের একটি শাখা। (মুসলিম ১/১২ হাঃ ৩৫, আহামাদ ৯৩৭২)', NULL, '9', '9', '৮\r\n৮', 0, 40, '2013-11-23 00:29:47', '2013-11-23 00:29:47', 'anon.', 'anon.', 'anon.', NULL, 1),
(14, 1, 5, 10, '‘আবদুল্লাহ্‌ ইব্‌নু ‘‘আমর (রাঃ)', 'আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) ইরশাদ করেন, সে-ই মুসলিম, যার জিহবা ও হাত হতে সকল মুসলিম নিরাপদ এবং সে-ই প্রকৃত মুহাজির, আল্লাহ যা নিষেধ করেছেন তা যে ত্যাগ করে। (৬৪৮৪; মুসলিম ১/১৪ হাঃ ৪০, আহমাদ ৬৭৬৫)', NULL, '10', '10', '৯\r\n৯', 0, 40, '2013-11-23 00:34:48', '2013-11-23 00:34:48', 'anon.', 'anon.', 'anon.', NULL, 1),
(15, 1, 5, 11, 'আবূ মূসা (রাঃ)', 'তিনি বলেন, তারা (সাহাবাগণ) জিজ্ঞেস করলেন, হে আল্লাহর রসূল! ইসলামে কোন্‌ জিনিসটি উত্তম? তিনি বললেনঃ যার জিহবা ও হাত হতে মুসলিমগণ নিরাপদ থাকে। (মুসলিম ১/১৪ হাঃ ৪২, আহমাদ ৬৭৬৫)', NULL, '11', '11', '১০\r\n১০', 0, 40, '2013-11-23 00:37:51', '2013-11-23 00:37:51', 'anon.', 'anon.', 'anon.', NULL, 1);
INSERT INTO `hadiths` (`id`, `book_id`, `chapter_id`, `section_id`, `narrator`, `body`, `note`, `numberPrimary`, `numberSecondary`, `reference`, `scholarReviewed`, `validity`, `createdAt`, `updatedAt`, `createdBy`, `updatedBy`, `deletedBy`, `explanation`, `crossChecked`) VALUES
(16, 1, 5, 12, '‘আবদুল্লাহ্‌ ইব্‌নু ‘‘আমর (রাঃ)', 'জনৈক ব্যক্তি আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম)-কে জিজ্ঞেস করল, ইসলামে কোন্‌ জিনিসটি উত্তম? তিনি বললেন, তুমি খাদ্য খাওয়াবে ও চেনা অচেনা সকলকে সালাম দিবে। (২৮, ৬২৩৬; মুসলিম ১/১৪ হাঃ ৪২, আহমাদ ৬৭৬৫)', NULL, '12', '12', '১১\r\n১১', 0, 40, '2013-11-23 00:40:05', '2013-11-23 00:40:05', 'anon.', 'anon.', 'anon.', NULL, 1),
(17, 1, 5, 13, 'আনাস (রাঃ)', 'নবী (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) বলেনঃ তোমাদের কেউ প্রকৃত মু’মিন হতে পারবে না, যতক্ষণ না সে তার ভাইয়ের জন্য সেটাই পছন্দ করবে, যা তার নিজের জন্য পছন্দ করে। (মুসলিম ১/১৭ হাঃ ৪৫, আহমাদ ১২৮০১, ১৩৮৭৫)', NULL, '13', '13', 'হাদিস নং ১২\r\nহাদিস নং ১২', 0, 40, '2013-11-23 11:14:38', '2013-11-23 11:14:38', 'anon.', 'anon.', 'anon.', NULL, 0),
(18, 1, 5, 14, 'আবূ হুরাইরাহ (রাঃ)', 'আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) বলেনঃ  সেই আল্লাহর শপথ, যাঁর হাতে আমার প্রাণ, তোমাদের কেউ প্রকৃত মু’মিন হতে পারবে না, যতক্ষন না আমি তার নিকট তার পিতা ও সন্তানাদির চেয়ে অধিক ভালবাসার পাত্র হই।', NULL, '14', '14', 'হাদিস নং ১৩\r\nহাদিস নং ১৩', 0, 40, '2013-11-23 11:22:40', '2013-11-23 11:22:40', 'anon.', 'anon.', 'anon.', NULL, 0),
(19, 1, 5, 14, 'আনাস (রাঃ)', 'আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) বলেনঃ তোমাদের কেউ প্রকৃত মু’মিন হতে পারবে না, যতক্ষন না আমি তার নিকট তার পিতা, তার সন্তান ও সব মানুষের অপেক্ষা অধিক প্রিয়পাত্র হই। (মুসলিম ১/১৬ হাঃ ৪৪, আহমাদ ১২৮১৪)', NULL, '15', '15', 'হাদিস নং ১৫\r\nহাদিস নং ১৫', 0, 40, '2013-11-23 11:22:41', '2013-11-23 11:22:41', 'anon.', 'anon.', 'anon.', NULL, 0),
(20, 1, 5, 15, 'আনাস (রাঃ)', 'নবী (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) বলেছেনঃ তিনটি গুন যার মধ্যে আছে, সে ঈমানের স্বাদ আস্বাদন করতে পারেঃ ১. আল্লাহ ও তাঁর রসূল তার নিকট অন্য সকল কিছু হতে অধিক প্রিয় হওয়া; ২. কাউকে একমাত্র আল্লাহর জন্যই ভালবাসা; ৩. কুফ্‌রীতে প্রত্যাবর্তনকে আগুনে নিক্ষিপ্ত হবার মত অপছন্দ করা। (২১, ৬০৪১, ৬৯৪১; মুসলিম ১/১৫ হাঃ ৪৩, আহমাদ ১২০০২)', NULL, '16', '16', 'হাদিস নং ১৫\r\nহাদিস নং ১৫', 0, 40, '2013-11-23 11:25:18', '2013-11-23 11:25:18', 'anon.', 'anon.', 'anon.', NULL, 0),
(21, 1, 5, 16, 'আনাস ইব্‌নু মালিক (রাঃ)', 'নবী (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) ইরশাদ করেনঃ ঈমানের আলামত হল আনসারকে ভালবাসা এবং মুনাফিকীর চিহ্ন হল আনসারের প্রতি শত্রুতা পোষণ করা। (৩৭৮৪; মুসলিম ১/৩৩ হাঃ ৭৪, আহমাদ ১৩৬০৮)', NULL, '17', '17', 'হাদিস নং ১৬\r\nহাদিস নং ১৬', 0, 40, '2013-11-23 11:27:37', '2013-11-23 11:27:37', 'anon.', 'anon.', 'anon.', NULL, 0),
(22, 1, 5, 17, '‘উবাদাহ ইবনু সামিত (রাঃ)', '‘উবাদাহ ইবনু সামিত (রাঃ) যিনি বদ্‌র যুদ্ধে অংশগ্রহণকারী ও লায়লাতুল ‘আকাবার একজন নকীব । ‘উবাদাহ ইব্‌নুস সামিত (রাঃ) বর্ণনা করেন, আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম)-এর পাশে একজন সহাবীর উপস্থিতিতে তিনি বলেনঃ তোমরা আমার নিকট এই মর্মে বায়’আত গ্রহণ কর যে, আল্লাহর সঙ্গে কোন কিছুকে অংশীদার সাব্যস্ত করবে না, চুরি করবে না, ব্যভিচার করবে না, তোমাদের সন্তানদের হত্যা করবে না, কারো প্রতি মিথ্যা অপবাদ আরোপ করবে না এবং সৎকাজে নাফরমানী করবে না। তোমাদের মধ্যে যে তা পূর্ণ করবে, তার পুরস্কার আল্লাহর নিকট রয়েছে। আর কেউ এর কোন একটিতে লিপ্ত হলো এবং দুনিয়াতে তার শাস্তি পেয়ে গেলে, তবে তা হবে তার জন্য কাফ্‌ফারা। আর কেউ এর কোন একটিতে লিপ্ত হয়ে পড়লে এবং আল্লাহ তা অপ্রকাশিত রাখলে, তবে তা আল্লাহর ইচ্ছাধীন। তিনি যদি চান, তাকে মার্জনা করবেন আর যদি চান, তাকে শাস্তি প্রদান করবেন। আমরা এর উপর বায়’আত গ্রহণ করলাম। \r\n(৩৮৯২, ৩৮৯৩, ৩৯৯৯, ৪৮৯৪, ৬৭৮৪, ৬৮০১, ৬৮৭৩, ৭০৫৫, ৭১৯৯, ৭২১৩, ৭৪৬৮; মুসলিম ২৯/১০ হাঃ ১৭০৯, আহমাদ ২২৭৪১)', NULL, '18', '18', 'হাদিস নং ১৭\r\nহাদিস নং ১৭', 0, 40, '2013-11-23 11:33:04', '2013-11-23 11:33:04', 'anon.', 'anon.', 'anon.', NULL, 0),
(23, 1, 5, 18, 'আবূ সা’ঈদ খুদরী (রাঃ)', 'তিনি বলেন, আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) বলেছেনঃ সেদিন দূরে নয়, যেদিন মুসলিমের উত্তম সম্পদ হবে কয়েকটি বকরী, যা নিয়ে সে পাহাড়ের চূড়ায় অথবা বৃষ্টিপাতের স্থানে চলে যাবে। ফিতনা হতে সে তার ধর্ম সহকারে পলায়ন করবে।', NULL, '19', '19', 'হাদিস নং ১৮\r\nহাদিস নং ১৮\r\n৩৩০০, ৩৬০০, ৬৪৯৫, ৭০৮৮', 0, 40, '2013-11-23 11:55:56', '2013-11-23 11:55:56', 'anon.', 'anon.', 'anon.', NULL, 0),
(24, 1, 5, 19, '‘আয়িশাহ (রাঃ)', 'তিনি বলেন, আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) সহাবীদের যখন কোন কাজের নির্দেশ দিতেন, তাঁদের সামর্থ্য অনুযায়ী নির্দেশ দিতেন। একবার তাঁরা বললেন, ‘হে আল্লাহর রসূল্‌! আমরা তো আপনার মত নই। আল্লাহ তা’আলা আপনার পূর্ববর্তী এবং পরবর্তী সকল গুনাহ ক্ষমা করে দিয়েছেন।’ তা শুনে তিনি রাগ করলেন, এমনকি তাঁর চেহারায় রাগের চিহ্ন ফুটে উঠল। অতঃপর তিনি বললেনঃ তোমাদের চেয়ে আমিই আল্লাহকে অধিক ভয় করি ও বেশী জানি।', NULL, '20', '20', 'হাদিস নং ১৯\r\nহাদিস নং ১৯', 0, 40, '2013-11-23 11:57:45', '2013-11-23 11:57:45', 'anon.', 'anon.', 'anon.', NULL, 0),
(25, 1, 5, 20, 'আনাস (রাঃ)', 'নবী (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) বলেছেনঃ তিনটি গুণ যার মধ্যে বিদ্যমান, সে ঈমানের স্বাদ পায়- (১) যার নিকট আল্লাহ ও তাঁর রসূল অন্য সকল বস্তু হতে অধিক প্রিয় ; (২) যে একমাত্র আল্লাহরই জন্য কোন বান্দাকে ভালবাসে এবং (৩) আল্লাহ তা’আলা কুফর হতে মুক্তি প্রদানের পর যে কুফর-এ প্রত্যাবর্তনকে আগুনে নিক্ষিপ্ত হবার মতোই অপছন্দ করে।', NULL, '21', '21', 'হাদিস নং ২০\r\nহাদিস নং ২০\r\n১৬', 0, 40, '2013-11-23 11:59:43', '2013-11-23 11:59:43', 'anon.', 'anon.', 'anon.', NULL, 0),
(26, 1, 5, 21, 'আবূ সা’ঈদ খুদরী (রাঃ)', 'আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) বলেছেনঃ একবার আমি নিদ্রাবস্থায় (স্বপ্নে) দেখলাম যে, লোকদেরকে আমার সামনে আনা হচ্ছে। আর তাদের পরণে রয়েছে জামা। কারো জামা বুক পর্যন্ত আর কারো জামা এর নীচ পর্যন্ত। আর ‘উমার ইব্‌নুল খাত্তাব (রাঃ)-কে আমার সামনে আনা হল এমন অবস্থায় যে, তিনি তাঁর জামা (অধিক লম্বা হওয়ায়) টেনে ধরে নিয়ে যাচ্ছিলেন। সহাবীগণ আরয করলেন, হে আল্লাহর রসূল! আপনি এর কী তা’বীর করেছেন? তিনি বললেনঃ (এ জামা অর্থ) দ্বীন।', NULL, '23', '23', 'হাদিস নং ২২\r\nহাদিস নং ২২\r\n৩৬৯১, ৭০০৮, ৭০০৯; মুসলিম ৪৪/২ হাঃ ২৩৯০, আহমাদ ১১৮১৪', 0, 40, '2013-11-23 12:06:22', '2013-11-23 12:06:22', 'anon.', 'anon.', 'anon.', NULL, 0),
(27, 1, 5, 21, 'আবূ সা’ঈদ খুদরী (রাঃ)', 'নবী (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) বলেছেনঃ জান্নাতবাসীরা জান্নাতে এবং জাহান্নামীরা জাহান্নামে প্রবেশ করবে। অতঃপর আল্লাহ তা’আলা ফেরেশতাদের বলবেন, যার অন্তরে সরিষার দানা পরিমাণও ঈমান আছে, তাকে জাহান্নাম হতে বের করে আনো। তারপর তাদের জাহান্নাম হতে এমন অবস্থায় বের করা হবে যে, তারা (পুড়ে) কালো হয়ে গেছে। অতঃপর তাদের বৃষ্টিতে বা হায়াতের [বর্ণনাকারী মালিক (রহঃ) শব্দ দু’টির কোনটি এ সম্পর্কে সন্দেহ প্রকাশ করেছেন] নদীতে নিক্ষেপ করা হবে। ফলে তারা সতেজ হয়ে উঠবে, যেমন নদীর তীরে ঘাসের বীজ গজিয়ে উঠে। তুমি কি দেখতে পাও না সেগুলো কেমন হলুদ বর্ণের হয় ও ঘন হয়ে গজায়? উহাইব (রহঃ) বলেন, ''আমর (রহঃ) আমাদের কাছে حيا এর স্থলে  حياة এবং خردل من ايمان এর স্থলে خردل من خير বর্ণনা করেছেন।', NULL, '22', '22', 'হাদিস নং ২১\r\nহাদিস নং ২১\r\n৪৫৮১, ৪৯১৯, ৬৫৬০, ৬৫৭৪, ৭৪৩৮, ৭৪৩৯; মুসলিম ১/৮২ হাঃ ১৮৪', 0, 40, '2013-11-23 12:06:24', '2013-11-23 12:06:24', 'anon.', 'anon.', 'anon.', NULL, 0),
(28, 1, 5, 22, '''আবদুল্লাহ্‌ ইব্‌নু ‘উমার (রাঃ)', 'একদা আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) এক আনসারীর পাশ দিয়ে অতিক্রম করছিলেন। তিনি তাঁর ভাইকে তখন (অধিক) লজ্জা ত্যাগের নাসীহাত করছিলেন। আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) তাকে বললেনঃ ওকে ছেড়ে দাও। কারণ লজ্জা ঈমানের অঙ্গ।', NULL, '24', '24', 'হাদিস নং ২৩\r\nহাদিস নং ২৩\r\n৬১১৮; মুসলিম ১/১২ হাঃ ৩৬, আহমাদ ৪৫৫৪', 0, 40, '2013-11-23 12:10:03', '2013-11-23 12:10:03', 'anon.', 'anon.', 'anon.', NULL, 0),
(29, 1, 5, 23, 'ইব্‌নু ‘উমার (রাঃ)', 'আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) বলেনঃ আমি লোকদের সাথে যুদ্ধ চালিয়ে যাবার জন্য নির্দেশিত হয়েছি, যতক্ষন না তারা সাক্ষ্য দেয় যে, আল্লাহ ব্যতীত প্রকৃত কোন উপাস্য নেই ও মুহাম্মাদ (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) আল্লাহর রসূল, আর সালাত প্রতিষ্ঠা করে ও যাকাত আদায় করে। তারা যদি এগুলো করে, তবে আমার পক্ষ হতে তাদের জান ও মালের ব্যাপারে নিরাপত্তা লাভ করলো; অবশ্য ইসলামের বিধান অনুযায়ী যদি কোন কারণ থাকে, তাহলে স্বতন্ত্র কথা। আর তাদের হিসাবের ভার আল্লাহর উপর অর্পিত।', NULL, '25', '25', 'হাদিস নং ২৪\r\nহাদিস নং ২৪\r\nমুসলিম ১/৮ হাঃ ২২', 0, 40, '2013-11-23 12:12:12', '2013-11-23 12:12:12', 'anon.', 'anon.', 'anon.', NULL, 0),
(30, 1, 5, 24, 'আবূ হুরাইরা (রাঃ)', 'আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম)-কে জিজ্ঞেস করা হল, ‘কোন্‌ ‘আমলটি উত্তম?’ তিনি বললেনঃ ‘আল্লাহ ও তাঁর রসূলের উপর বিশ্বাস স্থাপন করা।‘ [১]  জিজ্ঞেস করা হলো, ‘অতঃপর কোন্‌টি?’ তিনি বললেনঃ ‘আল্লাহর রাস্তায় জিহাদ করা।‘ প্রশ্ন করা হল, ‘অতঃপর কোন্‌টি?’ তিনি বললেনঃ ‘মাকবূল হাজ্জ সম্পাদন করা।"', '[১] মুরজি’আহদের নিকট শুধু অন্তরে বিশ্বাসের নাম ঈমান। মুখে স্বীকার করা রুকন বা শর্ত নয় এবং ‘আমল ঈমানের হাকীকাতের বাইরে। ঈমান আনার পর গুনাহর কাজ ক্ষতিকর নয় এমনকি কবীরা গুনাহ করলেও নয়। (মিরআত ৩৬ পৃঃ)', '26', '26', 'হাদিস নং ২৫\r\nহাদিস নং ২৫\r\n১৫১৯; মুসলিম ১/৩৬ হাঃ ৮০', 0, 40, '2013-11-23 12:15:43', '2013-11-23 12:15:43', 'anon.', 'anon.', 'anon.', NULL, 0),
(31, 1, 5, 25, 'সা‘দ (রাঃ)', 'আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) একদল লোককে কিছু দান করলেন। সা‘দ (রাঃ) সেখানে বসেছিলেন। সা‘দ (রাঃ) বলেন, আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) তাদের এক ব্যক্তিকে কিছু দিলেন না। সে ব্যক্তি আমার নিকট তাদের চেয়ে অধিক পছন্দের ছিল। তাই আমি আরয করলাম, হে আল্লাহর রসূল! অমুক ব্যক্তিকে আপনি বাদ দিলেন কেন? আল্লাহর শপথ! আমি তো তাকে মু’মিন বলেই জানি। তিনি বললেনঃ না, মুসলিম। তখন আমি কিছুক্ষণ নীরব থাকলাম। অতঃপর আমি তার সম্পর্কে যা জানি, তা (ব্যক্ত করার) প্রবল ইচ্ছা হলো। তাই আমি আমার বক্তব্য আবার বললাম, আপনি অমুককে দান থেকে বাদ রাখলেন? আল্লাহর শপথ! আমি তো তাকে মু‘মিন বলেই জানি। তিনি বললেনঃ ‘না, মুসলিম?’ তখন আমি কিছুক্ষণ নীরব থাকলাম। তারপর আমি তার সম্পর্কে যা জানি তা (ব্যক্ত করার) প্রবল ইচ্ছা হলো। তাই আমি আবার বললাম, আপনি অমুককে দান হতে বাদ রাখলেন? আল্লাহর শপথ! আমি তো তাকে মু‘মিন বলেই জানি। তিনি বললেনঃ ‘না, মুসলিম?’ তখন আমি কিছুক্ষণ চুপ থাকলাম। তারপর আমি তার সম্পর্কে যা জানি তা (ব্যক্ত করার) প্রবল ইচ্ছা হলো। তাই আমি আমার বক্তব্য আবার বললাম। আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) পুনরায় সেই একই জবাব দিলেন। তারপর বললেনঃ ‘সা’দ! আমি কখনো ব্যক্তি বিশেষকে দান করি, অথচ অন্যলোক আমার নিকট তার চেয়ে অধিক প্রিয়। তা এ আশঙ্কায় যে (সে ঈমান থেকে ফিরে যেতে পারে পরিণামে), আল্লাহ তা‘আলা তাকে অধঃমুখে জাহান্নামে নিক্ষিপ্ত করবেন। \r\nএ হাদীস ইউনুস, সালিহ, মা‘মার এবং যুহরী (রহঃ)--এর ভ্রাতুস্পুত্র যুহরী (রহঃ) হতে বর্ণনা করেছেন।', NULL, '27', '27', 'হাদিস নং ২৬\r\nহাদিস নং ২৬\r\n১৪৭৮; মুসলিম ১/৬৮ হাঃ ১৫০', 0, 40, '2013-11-23 12:18:37', '2013-11-23 12:18:37', 'anon.', 'anon.', 'anon.', NULL, 0),
(32, 1, 5, 26, '‘আবদুল্লাহ্‌ ইব্‌নু ‘আম্‌র (রাঃ)', 'এক ব্যক্তি আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম)-কে জিজ্ঞেস করল, ‘ইসলামের কোন্‌ কাজ সবচেয়ে উত্তম?’ তিনি বললেনঃ তুমি লোকদের খাদ্য খাওয়াবে এবং চেনা অচেনা সকলকে সালাম দিবে।', NULL, '28', '28', 'হাদিস নং ২৭\r\nহাদিস নং ২৭\r\n১২', 0, 40, '2013-11-23 12:21:13', '2013-11-23 12:21:13', 'anon.', 'anon.', 'anon.', NULL, 0),
(33, 1, 5, 27, 'ইব্‌নু ‘আব্বাস (রাঃ)', 'তিনি বলেন, নবী (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) বলেছেনঃ আমাকে জাহান্নাম দেখানো হয়। (আমি দেখি), তার অধিবাসীদের বেশির ভাগই নারীজাতি; (কারণ) তারা কুফরী করে। জিজ্ঞেস করা হল, ‘তারা কি আল্লাহর সঙ্গে কুফরী করে?’ তিনি বললেনঃ ‘তারা স্বামীর অবাধ্য হয় এবং অকৃতজ্ঞ হয়।’ তুমি যদি দীর্ঘদিন তাদের কারো প্রতি ইহসান করতে থাক, অতঃপর সে তোমার সামান্য অবহেলা দেখতে পেলেই বলে ফেলে, ‘আমি কক্ষণো তোমার নিকট হতে ভালো ব্যবহার পাইনি।’', NULL, '29', '29', 'হাদিস নং ২৮\r\nহাদিস নং ২৮\r\n৪৩১,৭৪৮,১০৫২,৩২০২,৫১৯৭; মুসলিম ৮/১ হাঃ ৮৮৪, আহমাদ ৩০৬৪', 0, 40, '2013-11-23 12:23:44', '2013-11-23 12:23:44', 'anon.', 'anon.', 'anon.', NULL, 0),
(34, 1, 5, 28, 'মা‘রূর (রহঃ)', 'তিনি বলেনঃ আমি একবার রাবাযা নামক স্থানে আবূ যর (রাঃ)-এর সঙ্গে দেখা করলাম। তখন তাঁর পরনে ছিল এক জোড়া কাপড় (লুঙ্গি ও চাদর) আর তাঁর ভৃত্যের পরনেও ছিল ঠিক একই ধরনের এক জোড়া কাপড়। আমি তাঁকে এর কারণ জিজ্ঞেস করলে তিনি বললেনঃ একবার আমি জনৈক ব্যক্তিকে গালি দিয়েছিলাম এবং আমি তাকে তার মা সম্পর্কে লজ্জা দিয়েছিলাম। তখন আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) আমাকে বললেন, আবূ যার! তুমি তাকে তার মা সম্পর্কে লজ্জা দিয়েছ? তুমি তো এমন ব্যক্তি, তোমার মধ্যে এখনো অন্ধকার যুগের স্বভাব বিদ্যমান। জেনে রেখো, তোমাদের দাস-দাসী তোমাদেরই ভাই। আল্লাহ তা‘আলা তাদের তোমাদের অধীনস্থ করে দিয়েছেন। তাই যার ভাই তার অধীনে থাকবে, সে যেন নিজে যা খায় তাকে তা-ই খাওয়ায় এবং নিজে যা পরিধান করে, তাকেও তা-ই পরায়। তাদের উপর এমন কাজ চাপিয়ে দিও না, যা তাদের জন্য অধিক কষ্টদায়ক। যদি এমন কষ্টকর কাজ করতে দাও, তাহলে তোমরাও তাদের সে কাজে সহযোগিতা করবে।', NULL, '30', '30', 'হাদিস নং ৩০\r\nহাদিস নং ৩০\r\n২৫৪৫,৬০৫০; মুসলিম ২৭/১০ হাঃ ১৬৬১, আহমাদ ২১৪৮৮', 0, 40, '2013-11-23 12:26:32', '2013-11-23 12:26:32', 'anon.', 'anon.', 'anon.', NULL, 0),
(35, 1, 5, 29, 'আহনাফ ইব্‌নু কায়স (রহঃ)', 'তিনি বলেন, আমি (সিফফীনের যুদ্ধে ) এক ব্যক্তিকে [আলী (রাঃ)-কে] সাহায্য করতে যাচ্ছিলাম। আবূ বাক্‌রাহ্‌ (রাঃ)-এর সঙ্গে আমার দেখা হলে তিনি বললেনঃ ‘তুমি কোথায় যাচ্ছ?’ আমি বললাম, ‘আমি এ ব্যক্তিকে সাহায্য করতে যাচ্ছি।’ তিনি বললেনঃ ‘ফিরে যাও। কারণ আমি আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) কে বলতে শুনেছি যে, দু’জন মুসলমান তাদের তরবারি নিয়ে মুখোমুখি হলে হত্যাকারী এবং নিহত ব্যক্তি উভয়ে জাহান্নামে যাবে।’ আমি বললাম, ‘হে আল্লাহর রসূল! এ হত্যাকারী (তো অপরাধী), কিন্তু নিহত ব্যক্তির কী অপরাধ? তিনি বললেন, (নিশ্চয়ই) সেও তার সাথীকে হত্যা করার জন্য উদগ্রীব ছিল।’', NULL, '31', '31', 'হাদিস নং ২৯\r\nহাদিস নং ২৯\r\n৬৮৭৫,৭০৮৩; মুসলিম ৫২/৪ হাঃ ২৮৮৮, আহমাদ ২০৪৪৬', 0, 40, '2013-11-23 12:30:24', '2013-11-23 12:30:24', 'anon.', 'anon.', 'anon.', NULL, 0),
(36, 1, 5, 30, '‘আবদুল্লাহ্‌ (ইব্‌নু মাস‘ঊদ) (রাঃ)', '‘‘যারা ঈমান এনেছে এবং নিজেদের ঈমানকে শিরকের সাথে মিশ্রিত করেনি’’- (সূরাহ্‌ আন্‌‘আম ৬/৮২); এ আয়াত নাযিল হলে আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম)-এর সাহাবীগণ বললেন, ‘আমাদের মধ্যে এমন কে আছে যে যুল্‌ম করেনি?’ তখন আল্লাহ তা‘আলা এ আয়াত নাযিল করেনঃ ‘‘নিশ্চয়ই শির্‌ক হচ্ছে অধিকতর যুল্‌ম’’-(সূরা লুকমান ৩১/১৩)', NULL, '32', '32', 'হাদিস নং ৩১\r\nহাদিস নং ৩১\r\n৩৩৬০,৩৪২৮,৩৪২৯,৪৬২৯,৪৭৭৬,৬৯১৮,৬৯৩৭; মুসলিম ১/৫৬ হাঃ ১২৬, আহমাদ ৪০৩১', 0, 40, '2013-11-23 12:34:03', '2013-11-23 12:34:57', 'anon.', 'anon.', 'anon.', NULL, 0),
(37, 1, 5, 31, 'আবূ হুরাইরা (রাঃ)', 'আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) বলেছেন, মুনাফিকের চিহ্ন তিনটিঃ ১. যখন কথা বলে মিথ্যা বলে; ২. যখন অঙ্গীকার করে ভঙ্গ করে এবং ৩. আমানত রাখা হলে খিয়ানত করে।', NULL, '33', '33', 'হাদিস নং ৩২\r\nহাদিস নং ৩২\r\n২৬৮২,২৭৪৯,৬০৯৫; মুসলিম ১/২৫ হাঃ ৫৯, আহমাদ ৯১৬২', 0, 40, '2013-11-23 12:40:42', '2013-11-23 12:40:42', 'anon.', 'anon.', 'anon.', NULL, 0),
(38, 1, 5, 31, '‘আবদুল্লাহ ইব্‌নু ‘আম্‌র (রাঃ)', 'নবী (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) বলেনঃ চারটি স্বভাব যার মধ্যে বিদ্যমান সে হচ্ছে খাঁটি মুনাফিক। যার মধ্যে এর কোন একটি স্বভাব থাকবে, তা পরিত্যাগ না করা পর্যন্ত তার মধ্যে মুনাফিকের একটি স্বভাব থেকে যায়। ১. আমানত রাখা হলে খিয়ানত করে; ২. কথা বললে মিথ্যা বলে; ৩. অঙ্গীকার করলে ভঙ্গ করে; এবং ৪. বিবাদে লিপ্ত হলে অশ্লীলভাবে গালাগালি করে। শু‘বা আ‘মাশ (রহঃ) থেকে হাদীস বর্ণনায় সুফিয়ান (রহঃ)-এর অনুসরণ করেছেন।', NULL, '34', '34', 'হাদিস নং ৩৩\r\nহাদিস নং ৩৩\r\n২৪৫৯,৩১৭৮; মুসলিম ১/২৫ হাঃ ৫৮, আহমাদ ৬৭৮২', 0, 40, '2013-11-23 12:40:42', '2013-11-23 12:40:42', 'anon.', 'anon.', 'anon.', NULL, 0),
(39, 1, 5, 32, 'আবূ হুরাইরা (রাঃ)', 'তিনি বলেন, আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) এরশাদ করেনঃ যে ব্যক্তি ঈমানের সঙ্গে নেকির আশায় কদরের রাতে ইবাদতের মধ্যে রাত্রি জাগবে, তার পূর্বের গুনাহ্‌ ক্ষমা করে দেয়া হবে।', NULL, '35', '35', 'হাদিস নং ৩৪\r\nহাদিস নং ৩৪\r\n৩৭,৩৮,১৯০১,২০০৮,২০০৯,২০১৪; মুসলিম ২/২৫ হাঃ ৭৬০', 0, 40, '2013-11-23 12:44:11', '2013-11-23 12:44:11', 'anon.', 'anon.', 'anon.', NULL, 0),
(40, 1, 5, 33, 'আবূ যুর‘আহ ইব্‌নু ‘আম্‌র ইব্‌নু জারীর (রহঃ)', 'তিনি বলেনঃ আমি আবূ হুরাইরা (রাঃ)-কে আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) হতে বলতে শুনেছি, তিনি বলেনঃ যে ব্যক্তি আল্লাহর রাস্তায় বের হয়, যদি সে শুধু আল্লাহর উপর ঈমান এবং তাঁর রসূলগণের প্রতি ঈমানের কারণে বের হয়ে থাকে, তবে আল্লাহ তা‘আলা ঘোষণা দেন যে, আমি তাকে তার পুণ্য বা গানীমাত (ও বাহন) সহ ঘরে ফিরিয়ে আনব কিংবা তাকে জান্নাতে প্রবেশ করাব।\r\nআর আমার উম্মতের উপর কষ্টদায়ক হবে বলে যদি মনে না করতাম তবে কোন সেনাদলের সঙ্গে না গিয়ে বসে থাকতাম না। আমি অবশ্যই এটা ভালবাসি যে, আল্লাহর রাস্তায় নিহত হই, পুনরায় জীবিত হই, পুনরায় নিহত হই, পুনরায় জীবিত হই, পুনরায় নিহত হই।', NULL, '36', '36', 'হাদিস নং ৩৫\r\nহাদিস নং ৩৫\r\n২৭৮৭,২৭৯৭,২৯৭২,৩১২৩,৭২২৬,৭২২৭,৭৪৫৭,৭৪৬৩; মুসলিম ৩৩/২৮ হাঃ ১৮৭৬, আহমাদ ৯১৯৮,৯৪৮১,৯৪৮৪', 0, 40, '2013-11-23 12:49:20', '2013-11-23 12:49:20', 'anon.', 'anon.', 'anon.', NULL, 0),
(41, 1, 5, 34, 'আবূ হুরাইরা (রাঃ)', 'তিনি বলেন যে, আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) ইরশাদ করেনঃ যে ব্যক্তি রমযানের রাতে ঈমানসহ পূণ্যের আশায় রাত জেগে ইবাদত করে, তার পূর্বের গুনাহ্‌ ক্ষমা করে দেয়া হয়।', NULL, '37', '37', 'হাদিস নং ৩৬\r\nহাদিস নং ৩৬\r\n৩৫', 0, 40, '2013-11-23 12:51:16', '2013-11-23 12:51:16', 'anon.', 'anon.', 'anon.', NULL, 0),
(42, 1, 5, 35, 'আবূ হুরাইরা (রাঃ)', 'তিনি বলেন, আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) বলেছেনঃ যে ব্যক্তি ঈমানসহ পূণ্যের আশায় রমযানের সিয়াম ব্রত পালন করে, তার পূর্বের গুনাহ ক্ষমা করে দেয়া হয়।', NULL, '38', '38', 'হাদিস নং ৩৭\r\nহাদিস নং ৩৭\r\n৩৫', 0, 40, '2013-11-23 12:53:08', '2013-11-23 12:53:08', 'anon.', 'anon.', 'anon.', NULL, 0),
(43, 1, 5, 36, 'আবূ হুরাইরা (রাঃ)', 'নবী (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) বলেছেনঃ নিশ্চয়ই দ্বীন সহজ। দ্বীন নিয়ে যে বাড়াবাড়ি করে দ্বীন তার উপর জয়ী হয়। কাজেই তোমরা মধ্যপন্থা অবলম্বন কর এবং (মধ্যপন্থার) নিকটে থাক, আশান্বিত থাক এবং সকাল-সন্ধ্যায় ও রাতের কিছু অংশে (‘ইবাদাত সহযোগে) সাহায্য চাও।', NULL, '39', '39', 'হাদিস নং ৩৮\r\nহাদিস নং ৩৮\r\n৫৬৭৩, ৬৪৬৩, ৭২৩৫', 0, 40, '2013-11-23 12:55:28', '2013-11-23 12:55:28', 'anon.', 'anon.', 'anon.', NULL, 0),
(44, 1, 5, 37, 'বারাআ (ইব্‌নু ‘আযিব) (রাঃ)', 'নবী (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) মদীনায় হিজরত করে সর্বপ্রথম আনসারদের মধ্যে তাঁর নানাদের গোত্র [আবূ ইসহাক (রহঃ) বলেন] বা মামাদের গোত্রে এসে ওঠেন। তিনি ষোল-সতের মাস বাইতুল মাকদিসের দিকে ফিরে সালাত আদায় করেন। কিন্তু তাঁর পছন্দ ছিল যে, তাঁর কিবলা বাইতুল্লাহ্‌র দিকে হোক। আর তিনি (বাইতুল্লাহ্‌র দিকে) প্রথম যে সালাত আদায় করেন, তা ছিল আসরের সালাত এবং তাঁর সঙ্গে একদল লোক সে সালাত আদায় করেন। তাঁর সঙ্গে যাঁরা সালাত আদায় করেছিলেন তাঁদের একজন লোক বের হয়ে এক মাসজিদে মুসল্লীদের কাছ দিয়ে যাচ্ছিলেন, তাঁরা তখন রুকূ‘ অবস্থায় ছিলেন। তখন তিনি বললেনঃ “আমি আল্লাহকে সাক্ষী রেখে বলছি যে, এইমাত্র আমি আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম)-এর সঙ্গে মক্কার দিকে ফিরে সালাত আদায় করে এসেছি। তখন তাঁরা যে অবস্থায় ছিলেন সে অবস্থায়ই বাইতুল্লাহ্‌র দিকে ঘুরে গেলেন। রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) যখন বায়তুল মাকদিস-এর দিকে সালাত আদায় করতেন তখন ইয়াহুদীদের ও আহলি-কিতাবদের নিকট এটা খুব ভাল লাগত; কিন্তু তিনি যখন বায়তুল্লাহ্‌র দিকে তাঁর মুখ ফিরালেন তখন তারা এটা খুব অপছন্দ করল। যুহায়র (রহঃ) বলে, আবূ ইসহাক (রহঃ) বারাআ (রাঃ) থেকে আমার নিকট যে হাদীস বর্ণনা করেছেন, তাতে এ কথাও রয়েছে যে, কিবলা পরিবর্তনের পূর্বে বেশ কিছু লোক মৃত্যুবরণ করেছিলেন এবং শাহাদাত বরণ করেছিলেন, তাঁদের ব্যাপারে আমরা কি বলব, সেটা আমাদের জানা ছিল না। তখন আল্লাহ তা‘আলা অবতীর্ণ করেনঃ  (আরবি) “আল্লাহ তা‘আলা তোমাদের সালাতকে বিনষ্ট করবেন না”।', NULL, '40', '40', 'হাদিস নং ৩৯\r\nহাদিস নং ৩৯\r\n৩৯৯, ৪৪৭৬, ৪৪৯২, ৭২৫২; মুসলিম ৫/২ হাঃ ৫২৫, আহমাদ ১৮৫৬৪, ১৮৭৩২', 0, 40, '2013-11-23 12:57:19', '2013-11-23 12:57:19', 'anon.', 'anon.', 'anon.', NULL, 0),
(45, 1, 5, 38, 'আবূ সা‘ঈদ খুদরী (রাঃ)', 'তিনি আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম)-কে বলতে শুনেছেন, বান্দা যখন ইসলাম গ্রহণ করে এবং তার ইসলাম উত্তম হয়, আল্লাহ তা‘আলা তার পূর্বের পাপসমূহ ক্ষমা করে দেন। অতঃপর শুরু হয় প্রতিফল; একটি পূণ্যের বিনিময়ে দশ হতে সাতশ গুণ পর্যন্ত; আর একটি পাপ কাজের বিনিময়ে ঠিক ততটুকু মন্দ প্রতিফল। অবশ্য আল্লাহ যদি ক্ষমা করে দেন তবে তা অন্য ব্যাপার।', NULL, '41', '41', 'অনুচ্ছেদ পৃঃ ৪৯\r\nপরিচ্ছেদ ৩১', 0, 40, '2013-11-23 13:28:22', '2013-11-23 13:28:22', 'anon.', 'anon.', 'anon.', NULL, 0),
(46, 1, 5, 38, 'আবূ হুরাইরা (রাঃ)', 'তিনি বলেন, আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) ইরশাদ করেনঃ তোমাদের মধ্যে কেউ যখন উত্তমরূপে ইসলামের উপর প্রতিষ্ঠিত থাকে তখন সে যে আমালে সালেহ (নেক আমল) করে তার প্রত্যেকটির বিনিময়ে সাতশ গুণ পর্যন্ত (পুণ্য) লেখা হয়। আর সে যে পাপ কাজ করে তার প্রত্যেকটির বিনিময়ে তার জন্য ঠিক ততটুকুই পাপ লেখা হয়।', NULL, '42', '42', 'হাদিস নং ৪০\r\nহাদিস নং ৪০\r\nমুসলিম ১/৫৯ হাঃ ১২৯, আহমাদ ৮২২৪', 0, 40, '2013-11-23 13:28:23', '2013-11-23 13:28:23', 'anon.', 'anon.', 'anon.', NULL, 0),
(47, 1, 5, 39, '‘আয়িশাহ (রাঃ)', 'নবী (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) একবার তাঁর নিকট আসেন, তাঁর নিকট তখন এক মহিলা ছিলেন। আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) জিজ্ঞেস করলেনঃ ‘ইনি কে?’ আয়িশাহ (রাঃ) উত্তর দিলেন, অমুক মহিলা, এ বলে তিনি তাঁর সলাতের উল্লেখ করলেন। আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) বললেনঃ ‘থাম, তোমরা যতটুকু সামর্থ্য রাখ, ততটুকুই তোমাদের করা উচিত। আল্লাহর শপথ! আল্লাহ তা‘আলা ততক্ষণ পর্যন্ত (সওয়াব দিতে) বিরত হন না, যতক্ষন না তোমরা নিজেরা পরিশ্রান্ত হয়ে পড়।  আল্লাহর নিকট অধিক পছন্দনীয় আমল সেটাই, যা আমলকারী নিয়মিত করে থাকে।', NULL, '43', '43', 'হাদিস নং ৪১\r\nহাদিস নং ৪১\r\n১১৫১; মুসলিম ২/৩১ হাঃ ৭৮৫, আহমাদ ২৪৯৯', 0, 40, '2013-11-23 13:31:15', '2013-11-23 13:31:15', 'anon.', 'anon.', 'anon.', NULL, 0),
(48, 1, 5, 40, '‘উমার ইবনুল খাত্তাব (রাঃ)', 'জনৈক ইয়াহূদী তাঁকে বললঃ হে আমীরুল মু’মিনীন! আপনাদের কিতাবে একটি আয়াত আছে, যা আপনারা পাঠ করে থাকেন, তা যদি আমাদের ইয়াহূদী জাতির উপর অবতীর্ণ হত, তবে অবশ্যই আমরা সে দিনকে খুশীর দিন হিসেবে পালন করতাম। তিনি বললেন, কোন আয়াত? সে বললঃ “আজ তোমাদের জন্য তোমাদের দ্বীন পরিপূর্ণ করলাম ও তোমাদের প্রতি আমার নিয়ামত সম্পূর্ণ করলাম এবং  ইসলামকে তোমাদের দ্বীন মনোনীত করলাম” –(সূরাহ্‌ মায়িদাহ্‌ ৫/৩)। ‘উমার (রাঃ) বললেন, এটি যে দিনে এবং যে স্থানে নবী (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম)-এর উপর অবতীর্ণ হয়েছিল তা আমরা জানি; তিনি সেদিন ‘আরাফায় দাঁড়িয়েছিলেন আর সেটা ছিল জুমু‘আহ্‌র দিন।', NULL, '45', '45', 'হাদিস নং ৪৩\r\nহাদিস নং ৪৩\r\n৪৪০৭, ৪৬০৬, ৭২৬৮; মুসলিম ৪৩/১ হাঃ ৩০১৭', 0, 40, '2013-11-23 13:35:29', '2013-11-23 13:35:29', 'anon.', 'anon.', 'anon.', NULL, 0),
(49, 1, 5, 40, 'আনাস (রাঃ)', 'নবী (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) বলেছেনঃ যে ‘লা- ইলা-হা ইল্লাল্লাহ’ বলবে আর তার অন্তরে একটি যব পরিমাণও পূণ্য বিদ্যমান থাকবে, তাকে জাহান্নাম হতে বের করা হবে এবং যে ‘লা- ইলা-হা ইল্লাল্লাহ’ বলবে আর তার অন্তরে একটি গম পরিমাণও পূণ্য বিদ্যমান থাকবে তাকে জাহান্নাম হতে বের করা হবে এবং যে ‘লা-ইলাহা ইল্লাল্লাহ’ বলবে আর তার অন্তরে একটি অণু পরিমানও নেকী থাকবে তাকে জাহান্নাম থেকে বের করা হবে।\r\nআবূ ‘আবদুল্লাহ বলেন, আবান (রহঃ) বর্ণনা করেছেন, আনাস (রাঃ) হতে এবং তিনি রসূলুল্লাহ (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) হতে নেকী –এর স্থলে ‘ঈমান’ শব্দটি রিওয়ায়াত করেছেন।', NULL, '44', '44', 'হাদিস নং ৪২\r\nহাদিস নং ৪২\r\n৪৪৭৬, ৬৫৬৫, ৭৪১০, ৭৪৪০, ৭৫০৯, ৭৫১০, ৭৫১৬; মুসলিম ১/৮৪ হাঃ ১৯৩, আহমাদ ১২১৫৪', 0, 40, '2013-11-23 13:35:29', '2013-11-23 13:35:29', 'anon.', 'anon.', 'anon.', NULL, 0);
INSERT INTO `hadiths` (`id`, `book_id`, `chapter_id`, `section_id`, `narrator`, `body`, `note`, `numberPrimary`, `numberSecondary`, `reference`, `scholarReviewed`, `validity`, `createdAt`, `updatedAt`, `createdBy`, `updatedBy`, `deletedBy`, `explanation`, `crossChecked`) VALUES
(50, 1, 5, 41, 'ত্বলহা ইব্‌নু ‘উবাইদুল্লাহ্‌ (রাঃ)', 'তিনি বলেন, জনৈক নাজ্‌দবাসী আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম)-এর নিকট এলো। তার মাথার চুল ছিল এলোমেলো। আমরা তার কথার মৃদু আওয়ায শুনতে পাচ্ছিলাম, কিন্তু সে কি বলছিল, আমরা তা বুঝতে পারছিলাম না। এভাবে সে নিকটে এসে ইসলাম সম্পর্কে প্রশ্ন করতে লাগল। আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) বললেনঃ ‘দিন-রাতে পাঁচ ওয়াক্ত সালাত’ ; সে বলল, ‘আমার উপর এ ছাড়া আরো সালাত আছে?’ তিনি বললেনঃ ‘না, তবে নফল আদায় করতে পার।’ আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) বললেনঃ ‘আর রমযানের সাওম।’ সে বলল, ‘আমার উপর এছাড়া আরো সাওম আছে?’ তিনি বললেনঃ ‘না, তবে নফল আদায় করতে পার।’ বর্ণনাকারী বলেন, আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) তার নিকট যাকাতের কথা বললেন। সে বলল, ‘আমার উপর এছাড়া আরো আছে?’ তিনি বললেনঃ ‘না, তবে নফল হিসেবে দিতে পার।’ বর্ননাকারী বলেন, ‘সে ব্যক্তি এই বলে চলে গেলেন; ‘আল্লাহর শপথ’ আমি এর চেয়ে অধিকও করব না এবং কমও করব না।’ তখন আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) বললেনঃ ‘সে কৃতকার্য হবে যদি সত্য বলে থাকে।’', NULL, '46', '46', 'হাদিস নং ৪৪\r\nহাদিস নং ৪৪\r\n১৮৯১, ২৬৭৮, ৬৯৫৬; মুসলিম ১/২ হাঃ ১১, আহমাদ ১৩৯০', 0, 40, '2013-11-24 11:32:41', '2013-11-24 11:32:41', 'anon.', 'anon.', 'anon.', NULL, 0),
(51, 1, 5, 42, 'আবূ হুরাইরা (রাঃ)', 'আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) ইরশাদ করেনঃ যে ব্যক্তি ঈমানের সাথে ও পুণ্যের আশায় কোন মুসলমানের জানাযার অনুগমন করে এবং তার সালাত-ই-জানাযা আদায় করে ও দাফন সম্পন্ন হওয়া পর্যন্ত সঙ্গে থাকে, সে দুই কীরাত সওয়াব নিয়ে ফিরবে। প্রতিটি কীরাত হল উহুদ পর্বতের মতো। আর যে ব্যক্তি শুধু তার জানাযা আদায় করে, তারপর দাফন সম্পন্ন হবার পূর্বেই চলে আসে, সে এক কীরাত সওয়াব নিয়ে ফিরবে। ‘উসমান আল-মুয়ায্‌যিন (রহঃ)....আবূ হুরাইরা (রাঃ) সূত্রে নবী (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) হতে অনুরূপ হাদীস বর্ণনা করেছেন।', NULL, '47', '47', 'হাদিস নং ৪৫\r\nহাদিস নং ৪৫\r\n১৩২৩, ১৩২৫', 0, 40, '2013-11-24 11:34:59', '2013-11-24 11:34:59', 'anon.', 'anon.', 'anon.', NULL, 0),
(52, 1, 5, 43, 'যুবায়দ (রহঃ)', 'তিনি বলেনঃ আমি আবূ ওয়াইল (রহঃ)-কে মুরজিআ’ [১] সম্পর্কে জিজ্ঞেস করলাম, তিনি বললেন, ‘আবদুল্লাহ (ইব্‌ন মাস‘ঊদ) আমার নিকট বলেছেন, নবী (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) বলেছেনঃ মুসলিমকে গালি দেয়া ফাসিকী এবং তার সাথে লড়াই করা কুফরী।', '[১] একটি বাতিল ফিরকা, যাদের মত হল, ভাল হোক বা মন্দ কোন আমলের মূল্য নেই এবং ঈমান আনার পর কোন গুনাহ ক্ষতিকর নয়।', '48', '48', 'হাদিস নং ৪৬\r\nহাদিস নং ৪৬\r\n৬০৪৪, ৭০৭৬; মুসলিম ১/২৮, হাঃ ৬৪, আহমাদ ৩৬৪৭', 0, 40, '2013-11-24 11:40:32', '2013-11-24 11:40:32', 'anon.', 'anon.', 'anon.', NULL, 0),
(53, 1, 5, 43, '‘উবাদাহ ইব্‌নু সামিত (রাঃ)', 'আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) লায়লাতুল কদ্‌র সম্পর্কে জানানোর জন্য বের হলেন। তখন দু’জন মুসলমান বিবাদ করছিল। তিনি বললেনঃ আমি তোমাদের লাইলাতুল কদ্‌র সম্পর্কে জানানোর জন্য বেরিয়েছিলাম; কিন্তু তখন অমুক অমুক বিবাদে লিপ্ত থাকায় তা (লাইলাতুল কদরের নির্দিষ্ট তারিখ সম্পর্কিত জ্ঞান) উঠিয়ে নেয়া হয়েছে। আর হয়তো বা এটাই তোমাদের জন্য মঙ্গলজনক হবে। তোমরা তা অনুসন্ধান কর (রমযানের) ২৭, ২৯ ও ২৫ তম রাতে।', NULL, '49', '49', 'হাদিস নং ৪৭\r\nহাদিস নং ৪৭\r\n২০২৩, ৬০৪৯', 0, 40, '2013-11-24 11:40:32', '2013-11-24 11:40:32', 'anon.', 'anon.', 'anon.', NULL, 0),
(54, 1, 5, 44, 'আবূ হুরাইরা (রাঃ)', 'তিনি বলেন, একদা আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) জনসমক্ষে উপবিষ্ট ছিলেন, এমন সময় তাঁর নিকট জনৈক ব্যক্তি এসে জিজ্ঞেস করলেন ‘ঈমান কি?’ তিনি বললেনঃ ‘ঈমান হল, আপনি বিশ্বাস রাখবেন আল্লাহর প্রতি, তাঁর ফেরেশতাগণের প্রতি, (ক্বিয়ামাতের দিন) তাঁর সঙ্গে সাক্ষাতের প্রতি এবং তাঁর রসূলগণের প্রতি। আপনি আরো বিশ্বাস রাখবেন পুনরুত্থানের প্রতি।’ তিনি জিজ্ঞেস করলেন, ‘ইসলাম কি?’ তিনি বললেনঃ ‘ইসলাম হল, আপনি আল্লাহর ইবাদত করবেন এবং তাঁর সাথে অংশীদার স্থাপন করবেন না, সালাত প্রতিষ্ঠা করবেন, ফার্‌য যাকাত আদায় করবেন এবং রমযান-এর সিয়ামব্রত পালন করবেন।’ ঐ ব্যক্তি জিজ্ঞেস করলেন, ‘ইহসান কি?’ তিনি বললেনঃ ‘আপনি এমনভাবে আল্লাহর ‘ইবাদত করবেন যেন আপনি তাঁকে দেখছেন, আর যদি আপনি তাঁকে দেখতে না পান তবে (মনে করবেন) তিনি আপনাকে দেখছেন।’ ঐ ব্যক্তি জিজ্ঞেস করলেন, ‘কিয়ামত কবে?’ তিনি বললেনঃ ‘এ ব্যাপারে যাকে জিজ্ঞেস করা হচ্ছে, তিনি জিজ্ঞেসকারী অপেক্ষা অধিক জ্ঞাত নন। তবে আমি আপনাকে ক্বিয়ামাতের আলামতসমূহ বলে দিচ্ছিঃ বাঁদী যখন তার প্রভুকে প্রসব করবে এবং উটের নগণ্য রাখালেরা যখন বড় বড় অট্টালিকা নির্মাণে প্রতিযোগিতা করবে। (ক্বিয়ামাতের জ্ঞান) সেই পাঁচটি জিনিসের অন্তর্ভুক্ত, যা আল্লাহ ব্যতীত কেউ জানে না।’ অতঃপর আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) এই আয়াতটি শেষ পর্যন্ত তিলাওয়াত করলেনঃ ‘কিয়ামাতের জ্ঞান কেবল আল্লাহরই নিকট.......।’ (সূরা লুক্বমান ৩১/৩৪)\r\nএরপর ঐ ব্যক্তি চলে গেলে তিনি বললেনঃ ‘তোমরা তাকে ফিরিয়ে আন।’ তারা কিছুই দেখতে পেল না। তখন তিনি বললেন, ‘ইনি জিবরীল (‘আঃ) ; লোকদেরকে তাদের দ্বীন শেখাতে এসেছিলেন।’ আবূ ‘আবদুল্লাহ বুখারী (রহঃ) বলেন, আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) এসব বিষয়কে ঈমানের অন্তর্ভুক্ত করেছেন।', NULL, '50', '50', 'হাদিস নং ৪৮\r\nহাদিস নং ৪৮\r\n৪৭৭৭; মুসলিম ১/১ হাঃ ৯', 0, 40, '2013-11-24 11:44:09', '2013-11-24 11:44:09', 'anon.', 'anon.', 'anon.', NULL, 0),
(55, 1, 5, 45, '‘আবদুল্লাহ ইব্‌নু ‘আব্বাস (রাঃ)', 'তিনি বলেনঃ আবূ সুফিয়ান ইব্‌নু হারব আমার নিকট বর্ণনা করেন, হিরাক্লিয়াস তাঁকে বলেছিল, আমি তোমাকে জিজ্ঞেস করেছিলাম তারা (ঈমানদারগণ) সংখ্যায় বাড়ছে না কমছে? তুমি উত্তর দিয়েছিলে, তারা সংখ্যায় বাড়ছে। প্রকৃতপক্ষে ঈমানের ব্যাপার এরূপই থাকে যতক্ষণ না তা পূর্ণতা লাভ করে। আর আমি তোমাকে জিজ্ঞেস করেছিলাম, কেউ তাঁর দ্বীন গ্রহণ করার পর তা অপছন্দ করে মুরতাদ হয়ে যায় কি-না? তুমি জবাব দিয়েছ, ‘না।’ প্রকৃত ঈমান এরূপই, ঈমানের স্বাদ অন্তরের সাথে মিশে গেলে কেউ তা অপছন্দ করে না।', NULL, '51', '51', 'হাদিস নং ৪৯\r\nহাদিস নং ৪৯\r\n৭', 0, 40, '2013-11-24 11:51:55', '2013-11-24 11:51:55', 'anon.', 'anon.', 'anon.', NULL, 0),
(56, 1, 5, 46, 'নু‘মান ইব্‌নু বশীর (রাঃ)', 'তিনি বলেনঃ আমি আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম)-কে বলতে শুনেছি যে, ‘হালাল স্পষ্ট এবং হারামও স্পষ্ট। আর এ দু’য়ের মাঝে রয়েছে বহু সন্দেহজনক বিষয়- যা অনেকেই জানে না। যে ব্যক্তি সেই সন্দেহজনক বিষয় হতে বেঁচে থাকবে, সে তার দ্বীন ও মর্যাদা রক্ষা করতে পারবে। আর যে সন্দেহজনক বিষয়সমূহে লিপ্ত হয়ে পড়ে, তার উদাহরণ সে রাখালের ন্যায়, যে তার পশু বাদশাহ্‌ সংরক্ষিত চারণভূমির আশেপাশে চরায়, অচিরেই সেগুলোর সেখানে ঢুকে পড়ার আশংকা রয়েছে। জেনে রাখ যে, প্রত্যেক বাদশাহ্‌রই একটি সংরক্ষিত এলাকা রয়েছে। আরো জেনে রাখ যে, আল্লাহর যমীনে তাঁর সংরক্ষিত এলাকা হলো তাঁর নিষিদ্ধ কাজসমূহ। জেনে রাখ, শরীরের মধ্যে একটি গোশতের টুকরা আছে, তা যখন ঠিক হয়ে যায়, গোটা শরীরই তখন ঠিক হয়ে যায়। আর তা যখন খারাপ হয়ে যায়, গোটা শরীরই তখন খারাপ হয়ে যায়। জেনে রাখ, সে গোশতের টুকরোটি হল অন্তর।', NULL, '52', '52', 'হাদিস নং ৫০\r\nহাদিস নং ৫০\r\n২০৫১; মুসলিম ২২/২০ হাঃ ১৫৯৯, আহমাদ ১৮৩৯৬, ১৮৪০২', 0, 40, '2013-11-24 11:57:41', '2013-11-24 11:57:41', 'anon.', 'anon.', 'anon.', NULL, 0),
(57, 1, 5, 47, 'আবূ জামরাহ (রহঃ)', 'তিনি বলেন, আমি ইব্‌নু ‘আব্বাস (রাঃ)-এর সাথে বসতাম। তিনি আমাকে তাঁর আসনে বসাতেন। একবার তিনি বললেনঃ তুমি আমার কাছে থেকে যাও, আমি তোমাকে আমার ধন-সম্পদ হতে কিয়দংশ প্রদান করব। আমি তাঁর সাথে দু’মাস থাকলাম। অতঃপর একদা তিনি বললেন, আবদুল কায়েস-এর একটি প্রতিনিধি দল আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম)-এর নিকট আগমন করলে তিনি বললেনঃ তোমরা কোন্‌ গোত্রের? কিংবা বললেন, কোন প্রতিনিধিদলের? তারা বলল, ‘রাবী‘আ গোত্রের।’  তিনি বললেনঃ স্বাগতম সে গোত্র বা সে প্রতিনিধি দলের প্রতি, যারা অপদস্থ ও লজ্জিত না হয়েই আগমন করেছে। তারা বলল, হে আল্লাহর রসূল! শাহরুল হারাম ব্যতীত অন্য কোন সময় আমরা আপনার নিকট আগমন করতে পারি না। আমাদের এবং আপনার মধ্যে মুযার গোত্রীয় কাফিরদের বসবাস। তাই আমাদের কিছু স্পষ্ট নির্দেশ দিন, যাতে করে আমরা যাদের পিছনে ছেড়ে এসেছি তাদের অবগত করতে পারি এবং যাতে করে আমরা জান্নাতে দাখিল হতে পারি। তারা পানীয় সম্বন্ধেও জিজ্ঞেস করল। তখন তিনি তাদেরকে চারটি বিষয়ের আদেশ এবং চারটি বিষয় হতে নিষেধ করলেন। তাদেরকে এক আল্লাহতে বিশ্বাস স্থাপনের নির্দেশ দিয়ে বললেনঃ ‘এক আল্লাহর প্রতি কিভাবে বিশ্বাস স্থাপন করা হয় তা কি তোমরা অবগত আছ?’ তাঁরা বলল, ‘আল্লাহ ও তাঁর রসূলই অধিক জ্ঞাত।’ তিনি বললেনঃ ‘তা হচ্ছে এ সাক্ষ্য দেয়া যে, আল্লাহ ব্যতীত প্রকৃত কোন উপাস্য নেই এবং মুহাম্মাদ (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) আল্লাহর রসূল এবং সালাত প্রতিষ্ঠা করা, যাকাত আদায় করা, রমাযানের সিয়ামব্রত পালন করা; আর তোমরা গানীমাতের সম্পদ হতে এক-পঞ্চমাংশ আদায় করবে। তিনি তাদেরকে চারটি বিষয় হতে বিরত থাকতে বললেন। আর তা হচ্ছেঃ সবুজ কলস, শুকনো কদুর খোল, খেজুর বৃক্ষের গুড়ি হতে তৈরী বাসন এবং আলকাতরা দ্বারা রাঙানো পাত্র।  রাবী বলেন, বর্ণনাকারী (মুযাফ্‌ফাত-এর স্থলে) কখনও আন্‌নাক্বীর উল্লেখ করেছেন (দু’টি শব্দের অর্থ একইরূপ) ; তিনি আরো বলেন, তোমরা এ বিষয়গুলো ভালো করে জেনে নাও এবং অন্যদেরও এগুলো অবগত কর।', NULL, '53', '53', 'হাদিস নং ৫১\r\nহাদিস নং ৫১\r\n৮৭, ৫২৩, ১৩৯৮, ৩০৯৫, ৩৫১০, ৪৩৬৮, ৪২৬৯, ৬১৭৬, ৭২৬৬, ৭৫৫৬; মুসলিম ১/৬ হাঃ ১৭', 0, 40, '2013-11-24 12:18:11', '2013-11-24 12:18:11', 'anon.', 'anon.', 'anon.', NULL, 0),
(58, 1, 5, 49, '‘উমার (রাঃ)', 'আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) বলেছেনঃ কর্মসমূহ সংকল্পের সাথে সম্পৃক্ত এবং প্রতিটি মানুষের প্রাপ্য তার সংকল্প অনুযায়ী। কাজেই যার হিজরত হবে আল্লাহ ও তদীয় রসূলের উদ্দেশ্যে, তার হিজরত আল্লাহ ও তদীয় রসূলের উদ্দেশ্যে হয়েছে বলেই ধরা হবে। আর যার হিজরত হয় দুনিয়া অর্জনের জন্য বা কোন নারীকে বিবাহ করার উদ্দেশ্যে, তার হিজরত সে উদ্দেশ্যেই হবে যে উদ্দেশ্যে সে হিজরত করেছে।', NULL, '54', '54', 'হাদিস নং ৫২\r\nহাদিস নং ৫২\r\n১; মুসলিম ৩৩/৪৫ হাঃ ১৯০৭, আহমাদ ১৬৮', 0, 40, '2013-11-24 12:27:40', '2013-11-24 12:27:40', 'anon.', 'anon.', 'anon.', NULL, 0),
(59, 1, 5, 49, 'সা‘আদ ইব্‌নু আবূ ওয়াক্কাস  (রাঃ)', 'আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) বলেছেনঃ ‘তুমি আল্লাহর নৈকট্য অর্জনের উদ্দেশ্যে যা-ই ব্যয় কর না কেন, তোমাকে তার প্রতিদান নিশ্চিতরূপে প্রদান করা হবে। এমনকি তুমি তোমার স্ত্রীর মুখে যা তুলে দাও, তারও।’', NULL, '56', '56', 'হাদিস নং ৫৪\r\nহাদিস নং ৫৪\r\n১২৯৫, ২৭৪২, ২৭৪৪, ৩৯৩৬, ৪৪০৯, ৫৩৫৪, ৫৬৫৯, ৫৬৬৮, ৬৩৭৩, ৬৭৩৩; মুসলিম ২৫/১ হাঃ ১৬২৮, আহমাদ ১৫৪৬', 0, 40, '2013-11-24 12:27:41', '2013-11-24 12:27:41', 'anon.', 'anon.', 'anon.', NULL, 0),
(60, 1, 5, 49, 'আবূ মাস‘ঊদ (রাঃ)', 'আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) বলেছেনঃ মানুষ স্বীয় পরিবার-পরিজনের জন্য পুণ্যের আশায় যখন ব্যয় করে তখন সেটা তার জন্য সদাকাহ হয়ে যায়।', NULL, '55', '55', 'হাদিস নং ৫৩\r\nহাদিস নং ৫৩\r\n৪০০৬, ৫৩৫১', 0, 40, '2013-11-24 12:27:41', '2013-11-24 12:27:41', 'anon.', 'anon.', 'anon.', NULL, 0),
(61, 1, 5, 49, 'জারীর ইব্‌নু ‘আবদুল্লাহ্‌ আল-বাজালী (রাঃ)', 'তিনি বলেছেনঃ আমি আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম)-এর নিকট বায়‘আত গ্রহণ করেছি সালাত কায়িম করার, যাকাত প্রদান করার এবং সমস্ত মুসলিমের মঙ্গল কামনা করার।', NULL, '57', '57', 'হাদিস নং ৫৫\r\nহাদিস নং ৫৫\r\n৫২৪, ১৪০১, ২১৫৭, ২৭১৪, ২৭১৫, ৭২০৪; মুসলিম ১/২৩ হাঃ ৫৬, আহমাদ ৩২৮১', 0, 40, '2013-11-24 12:27:41', '2013-11-24 12:27:41', 'anon.', 'anon.', 'anon.', NULL, 0),
(62, 1, 5, 49, 'যিয়াদ ইব্‌নু ‘ইলাকা (রহঃ)', 'তিনি বলেনঃ মুগীরাহ ইব্‌নু শু‘বাহ (রাঃ) [১] যেদিন ইন্তিকাল করেন সেদিন আমি জারীর ইবনূ ‘আব্দুল্লাহ্‌ (রাঃ)-এর নিকটে শুনেছি, তিনি (মিম্বারে) দাঁড়িয়ে আল্লাহর প্রশংসা ও সানা বর্ণনা করে বললেন, তোমরা এক আল্লাহকে ভয় কর যাঁর কোন অংশীদার নেই এবং নতুন কোন নেতার আগমন না হওয়া পর্যন্ত শৃংখলা বজায় রাখ, অতি সত্বর তোমাদের নেতা আগমন করবেন। অতঃপর জারীর (রাঃ) বললেন, তোমাদের নেতার জন্য ক্ষমা চাও; কেননা, তিনি ক্ষমা করা পছন্দ করেন। অতঃপর বললেন, একদা আমি আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম)-এর নিকটে এসে আরয করলাম, আমি আপনার নিকট ইসলামের বায়‘আত নিতে চাই। তিনি (অন্যান্য বিষয়ের সাথে) আমার উপর শর্ত দিয়ে বললেনঃ আর সকল মুসলমানের মঙ্গল কামনা করবে। অতঃপর আমি তাঁর নিকট এ শর্তের উপর বায়‘আত নিলাম। এ মাসজিদের প্রতিপালকের শপথ! আমি তোমাদের মঙ্গলকামনাকারী। অতঃপর তিনি আল্লাহর নিকট ক্ষমা প্রার্থনা করলেন এবং (মিম্বার হতে) নেমে গেলেন।', '[১] বিখ্যাত সাহাবী। তিনি কূফার আমীর ছিলেন।', '58', '58', 'হাদিস নং ৫৬\r\nহাদিস নং ৫৬\r\n৫৭', 0, 40, '2013-11-24 12:27:42', '2013-11-24 12:27:42', 'anon.', 'anon.', 'anon.', NULL, 0);
INSERT INTO `hadith_translation` (`id`, `translatable_id`, `narrator`, `body`, `note`, `reference`, `locale`) VALUES
(9, 5, NULL, 'حَدَّثَنَا الْحُمَيْدِيُّ عَبْدُ اللَّهِ بْنُ الزُّبَيْرِ، قَالَ حَدَّثَنَا سُفْيَانُ، قَالَ حَدَّثَنَا يَحْيَى بْنُ سَعِيدٍ الأَنْصَارِيُّ، قَالَ أَخْبَرَنِي مُحَمَّدُ بْنُ إِبْرَاهِيمَ التَّيْمِيُّ، أَنَّهُ سَمِعَ عَلْقَمَةَ بْنَ وَقَّاصٍ اللَّيْثِيَّ، يَقُولُ سَمِعْتُ عُمَرَ بْنَ الْخَطَّابِ ـ رضى الله عنه ـ عَلَى الْمِنْبَرِ قَالَ سَمِعْتُ رَسُولَ اللَّهِ صلى الله عليه وسلم يَقُولُ ‏ "‏ إِنَّمَا الأَعْمَالُ بِالنِّيَّاتِ، وَإِنَّمَا لِكُلِّ امْرِئٍ مَا نَوَى، فَمَنْ كَانَتْ هِجْرَتُهُ إِلَى دُنْيَا يُصِيبُهَا أَوْ إِلَى امْرَأَةٍ يَنْكِحُهَا فَهِجْرَتُهُ إِلَى مَا هَاجَرَ إِلَيْهِ', NULL, NULL, 'ar'),
(10, 5, '''Umar bin Al-Khattab', 'I heard Allah''s Messenger (sallallahu ‘alaihi wa sallam) saying, "The reward of deeds depends upon the intentions and every person will get the reward according to what he has intended. So whoever emigrated for worldly benefits or for a woman to marry, his emigration was for what he emigrated for."', NULL, 'Sahih al-Bukhari 1\r\nBook 1, Hadith 1', 'en'),
(11, 6, NULL, 'حَدَّثَنَا عَبْدُ اللَّهِ بْنُ يُوسُفَ، قَالَ أَخْبَرَنَا مَالِكٌ، عَنْ هِشَامِ بْنِ عُرْوَةَ، عَنْ أَبِيهِ، عَنْ عَائِشَةَ أُمِّ الْمُؤْمِنِينَ ـ رضى الله عنها ـ أَنَّ الْحَارِثَ بْنَ هِشَامٍ ـ رضى الله عنه ـ سَأَلَ رَسُولَ اللَّهِ صلى الله عليه وسلم فَقَالَ يَا رَسُولَ اللَّهِ كَيْفَ يَأْتِيكَ الْوَحْىُ فَقَالَ رَسُولُ اللَّهِ صلى الله عليه وسلم ‏ "‏ أَحْيَانًا يَأْتِينِي مِثْلَ صَلْصَلَةِ الْجَرَسِ ـ وَهُوَ أَشَدُّهُ عَلَىَّ ـ فَيُفْصَمُ عَنِّي وَقَدْ وَعَيْتُ عَنْهُ مَا قَالَ، وَأَحْيَانًا يَتَمَثَّلُ لِيَ الْمَلَكُ رَجُلاً فَيُكَلِّمُنِي فَأَعِي مَا يَقُولُ ‏"‏‏.‏ قَالَتْ عَائِشَةُ رضى الله عنها وَلَقَدْ رَأَيْتُهُ يَنْزِلُ عَلَيْهِ الْوَحْىُ فِي الْيَوْمِ الشَّدِيدِ الْبَرْدِ، فَيَفْصِمُ عَنْهُ وَإِنَّ جَبِينَهُ لَيَتَفَصَّدُ عَرَقًا‏.', NULL, NULL, 'ar'),
(12, 6, '''Aisha', 'Narrated ''Aisha:\r\n(the mother of the faithful believers) Al-Harith bin Hisham asked Allah''s Messenger (sallallahu ‘alaihi wa sallam) "O Allah''s Messenger (sallallahu ‘alaihi wa sallam) ! How is the Divine Inspiration revealed to you?" Allah''s Messenger (sallallahu ‘alaihi wa sallam) replied, "Sometimes it is (revealed) like the ringing of a bell, this form of Inspiration is the hardest of all and then this state passes off after I have grasped what is inspired. Sometimes the Angel comes in the form of a man and talks to me and I grasp whatever he says." ''Aisha added: Verily I saw the Prophet (sallallahu ‘alaihi wa sallam) being inspired divinely on a very cold day and noticed the sweat dropping from his forehead (as the Inspiration was over).', NULL, NULL, 'en'),
(13, 7, NULL, 'حَدَّثَنَا يَحْيَى بْنُ بُكَيْرٍ، قَالَ حَدَّثَنَا اللَّيْثُ، عَنْ عُقَيْلٍ، عَنِ ابْنِ شِهَابٍ، عَنْ عُرْوَةَ بْنِ الزُّبَيْرِ، عَنْ عَائِشَةَ أُمِّ الْمُؤْمِنِينَ، أَنَّهَا قَالَتْ أَوَّلُ مَا بُدِئَ بِهِ رَسُولُ اللَّهِ صلى الله عليه وسلم مِنَ الْوَحْىِ الرُّؤْيَا الصَّالِحَةُ فِي النَّوْمِ، فَكَانَ لاَ يَرَى رُؤْيَا إِلاَّ جَاءَتْ مِثْلَ فَلَقِ الصُّبْحِ، ثُمَّ حُبِّبَ إِلَيْهِ الْخَلاَءُ، وَكَانَ يَخْلُو بِغَارِ حِرَاءٍ فَيَتَحَنَّثُ فِيهِ ـ وَهُوَ التَّعَبُّدُ ـ اللَّيَالِيَ ذَوَاتِ الْعَدَدِ قَبْلَ أَنْ يَنْزِعَ إِلَى أَهْلِهِ، وَيَتَزَوَّدُ لِذَلِكَ، ثُمَّ يَرْجِعُ إِلَى خَدِيجَةَ، فَيَتَزَوَّدُ لِمِثْلِهَا، حَتَّى جَاءَهُ الْحَقُّ وَهُوَ فِي غَارِ حِرَاءٍ، فَجَاءَهُ الْمَلَكُ فَقَالَ اقْرَأْ‏.‏ قَالَ ‏"‏ مَا أَنَا بِقَارِئٍ ‏"‏‏.‏ قَالَ ‏"‏ فَأَخَذَنِي فَغَطَّنِي حَتَّى بَلَغَ مِنِّي الْجَهْدَ، ثُمَّ أَرْسَلَنِي فَقَالَ اقْرَأْ‏.‏ قُلْتُ مَا أَنَا بِقَارِئٍ‏.‏ فَأَخَذَنِي فَغَطَّنِي الثَّانِيَةَ حَتَّى بَلَغَ مِنِّي الْجَهْدَ، ثُمَّ أَرْسَلَنِي فَقَالَ اقْرَأْ‏.‏ فَقُلْتُ مَا أَنَا بِقَارِئٍ‏.‏ فَأَخَذَنِي فَغَطَّنِي الثَّالِثَةَ، ثُمَّ أَرْسَلَنِي فَقَالَ ‏{‏اقْرَأْ بِاسْمِ رَبِّكَ الَّذِي خَلَقَ * خَلَقَ الإِنْسَانَ مِنْ عَلَقٍ * اقْرَأْ وَرَبُّكَ الأَكْرَمُ‏}‏ ‏"‏‏.‏ فَرَجَعَ بِهَا رَسُولُ اللَّهِ صلى الله عليه وسلم يَرْجُفُ فُؤَادُهُ، فَدَخَلَ عَلَى خَدِيجَةَ بِنْتِ خُوَيْلِدٍ رضى الله عنها فَقَالَ ‏"‏ زَمِّلُونِي زَمِّلُونِي ‏"‏‏.‏ فَزَمَّلُوهُ حَتَّى ذَهَبَ عَنْهُ الرَّوْعُ، فَقَالَ لِخَدِيجَةَ وَأَخْبَرَهَا الْخَبَرَ ‏"‏ لَقَدْ خَشِيتُ عَلَى نَفْسِي ‏"‏‏.‏ فَقَالَتْ خَدِيجَةُ كَلاَّ وَاللَّهِ مَا يُخْزِيكَ اللَّهُ أَبَدًا، إِنَّكَ لَتَصِلُ الرَّحِمَ، وَتَحْمِلُ الْكَلَّ، وَتَكْسِبُ الْمَعْدُومَ، وَتَقْرِي الضَّيْفَ، وَتُعِينُ عَلَى نَوَائِبِ الْحَقِّ‏.‏ فَانْطَلَقَتْ بِهِ خَدِيجَةُ حَتَّى أَتَتْ بِهِ وَرَقَةَ بْنَ نَوْفَلِ بْنِ أَسَدِ بْنِ عَبْدِ الْعُزَّى ابْنَ عَمِّ خَدِيجَةَ ـ وَكَانَ امْرَأً تَنَصَّرَ فِي الْجَاهِلِيَّةِ، وَكَانَ يَكْتُبُ الْكِتَابَ الْعِبْرَانِيَّ، فَيَكْتُبُ مِنَ الإِنْجِيلِ بِالْعِبْرَانِيَّةِ مَا شَاءَ اللَّهُ أَنْ يَكْتُبَ، وَكَانَ شَيْخًا كَبِيرًا قَدْ عَمِيَ ـ فَقَالَتْ لَهُ خَدِيجَةُ يَا ابْنَ عَمِّ اسْمَعْ مِنَ ابْنِ أَخِيكَ‏.‏ فَقَالَ لَهُ وَرَقَةُ يَا ابْنَ أَخِي مَاذَا تَرَى فَأَخْبَرَهُ رَسُولُ اللَّهِ صلى الله عليه وسلم خَبَرَ مَا رَأَى‏.‏ فَقَالَ لَهُ وَرَقَةُ هَذَا النَّامُوسُ الَّذِي نَزَّلَ اللَّهُ عَلَى مُوسَى صلى الله عليه وسلم يَا لَيْتَنِي فِيهَا جَذَعًا، لَيْتَنِي أَكُونُ حَيًّا إِذْ يُخْرِجُكَ قَوْمُكَ‏.‏ فَقَالَ رَسُولُ اللَّهِ صلى الله عليه وسلم ‏"‏ أَوَمُخْرِجِيَّ هُمْ ‏"‏‏.‏ قَالَ نَعَمْ، لَمْ يَأْتِ رَجُلٌ قَطُّ بِمِثْلِ مَا جِئْتَ بِهِ إِلاَّ عُودِيَ، وَإِنْ يُدْرِكْنِي يَوْمُكَ أَنْصُرْكَ نَصْرًا مُؤَزَّرًا‏.‏ ثُمَّ لَمْ يَنْشَبْ وَرَقَةُ أَنْ تُوُفِّيَ وَفَتَرَ الْوَحْىُ‏.‏', NULL, NULL, 'ar'),
(14, 7, '''Aisha', '(the mother of the faithful believers) The commencement of the Divine Inspiration to Allah''s Messenger (sallallahu ‘alaihi wa sallam) was in the form of good dreams which came true like bright daylight, and then the love of seclusion was bestowed upon him. He used to go in seclusion in the cave of Hira where he used to worship (Allah alone) continuously for many days before his desire to see his family. He used to take with him the journey food for the stay and then come back to (his wife) Khadija to take his food likewise again till suddenly the Truth descended upon him while he was in the cave of Hira. The angel came to him and asked him to read. The Prophet (sallallahu ‘alaihi wa sallam) replied, "I do not know how to read." The Prophet (sallallahu ‘alaihi wa sallam) added, "The angel caught me (forcefully) and pressed me so hard that I could not bear it any more. He then released me and again asked me to read and I replied, ''I do not know how to read.'' Thereupon he caught me again and pressed me a second time till I could not bear it any more. He then released me and again asked me to read but again I replied, ''I do not know how to read (or what shall I read)?'' Thereupon he caught me for the third time and pressed me, and then released me and said, ''Read in the name of your Lord, who has created (all that exists), created man from a clot. Read! And your Lord is the Most Generous." (96.1, 96.2, 96.3) Then Allah''s Messenger (sallallahu ‘alaihi wa sallam) returned with the Inspiration and with his heart beating severely. Then he went to Khadija bint Khuwailid and said, "Cover me! Cover me!" They covered him till his fear was over and after that he told her everything that had happened and said, "I fear that something may happen to me." Khadija replied, "Never! By Allah, Allah will never disgrace you. You keep good relations with your kith and kin, help the poor and the destitute, serve your guests generously and assist the deserving calamity-afflicted ones." Khadija then accompanied him to her cousin Waraqa bin Naufal bin Asad bin ''Abdul ''Uzza, who, during the pre-Islamic Period became a Christian and used to write the writing with Hebrew letters. He would write from the Gospel in Hebrew as much as Allah wished him to write. He was an old man and had lost his eyesight. Khadija said to Waraqa, "Listen to the story of your nephew, O my cousin!" Waraqa asked, "O my nephew! What have you seen?" Allah''s Messenger (sallallahu ‘alaihi wa sallam) described whatever he had seen. Waraqa said, "This is the same one who keeps the secrets (angel Gabriel) whom Allah had sent to Moses. I wish I were young and could live up to the time when your people would turn you out." Allah''s Messenger (sallallahu ‘alaihi wa sallam) asked, "Will they drive me out?" Waraqa replied in the affirmative and said, "Anyone (man) who came with something similar to what you have brought was treated with hostility; and if I should remain alive till the day when you will be turned out then I would support you strongly." But after a few days Waraqa died and the Divine Inspiration was also paused for a while.', NULL, NULL, 'en'),
(15, 8, NULL, 'قَالَ ابْنُ شِهَابٍ وَأَخْبَرَنِي أَبُو سَلَمَةَ بْنُ عَبْدِ الرَّحْمَنِ، أَنَّ جَابِرَ بْنَ عَبْدِ اللَّهِ الأَنْصَارِيَّ، قَالَ ـ وَهُوَ يُحَدِّثُ عَنْ فَتْرَةِ الْوَحْىِ، فَقَالَ ـ فِي حَدِيثِهِ ‏"‏ بَيْنَا أَنَا أَمْشِي، إِذْ سَمِعْتُ صَوْتًا، مِنَ السَّمَاءِ، فَرَفَعْتُ بَصَرِي فَإِذَا الْمَلَكُ الَّذِي جَاءَنِي بِحِرَاءٍ جَالِسٌ عَلَى كُرْسِيٍّ بَيْنَ السَّمَاءِ وَالأَرْضِ، فَرُعِبْتُ مِنْهُ، فَرَجَعْتُ فَقُلْتُ زَمِّلُونِي‏.‏ فَأَنْزَلَ اللَّهُ تَعَالَى ‏{‏يَا أَيُّهَا الْمُدَّثِّرُ * قُمْ فَأَنْذِرْ‏}‏ إِلَى قَوْلِهِ ‏{‏وَالرُّجْزَ فَاهْجُرْ‏}‏ فَحَمِيَ الْوَحْىُ وَتَتَابَعَ ‏"‏‏.‏ تَابَعَهُ عَبْدُ اللَّهِ بْنُ يُوسُفَ وَأَبُو صَالِحٍ‏.‏ وَتَابَعَهُ هِلاَلُ بْنُ رَدَّادٍ عَنِ الزُّهْرِيِّ‏.‏ وَقَالَ يُونُسُ وَمَعْمَرٌ ‏"‏ بَوَادِرُهُ ‏"‏‏.‏', NULL, NULL, 'ar'),
(16, 8, 'Jabir bin ''Abdullah Al-Ansari', '(while talking about the period of pause in revelation) reporting the speech of the Prophet:\r\n"While I was walking, all of a sudden I heard a voice from the sky. I looked up and saw the same angel who had visited me at the cave of Hira'' sitting on a chair between the sky and the earth. I got afraid of him and came back home and said, ''Wrap me (in blankets).'' And then Allah revealed the following Holy Verses (of Quran): ''O you (i.e. Muhammad)! wrapped up in garments!'' Arise and warn (the people against Allah''s Punishment),... up to ''and desert the idols.'' (74.1-5) After this the revelation started coming strongly, frequently and regularly."', NULL, 'Sahih al-Bukhari 4\r\nBook 1, Hadith 4', 'en'),
(17, 9, NULL, 'حَدَّثَنَا مُوسَى بْنُ إِسْمَاعِيلَ، قَالَ حَدَّثَنَا أَبُو عَوَانَةَ، قَالَ حَدَّثَنَا مُوسَى بْنُ أَبِي عَائِشَةَ، قَالَ حَدَّثَنَا سَعِيدُ بْنُ جُبَيْرٍ، عَنِ ابْنِ عَبَّاسٍ، فِي قَوْلِهِ تَعَالَى ‏{‏لاَ تُحَرِّكْ بِهِ لِسَانَكَ لِتَعْجَلَ بِهِ‏}‏ قَالَ كَانَ رَسُولُ اللَّهِ صلى الله عليه وسلم يُعَالِجُ مِنَ التَّنْزِيلِ شِدَّةً، وَكَانَ مِمَّا يُحَرِّكُ شَفَتَيْهِ ـ فَقَالَ ابْنُ عَبَّاسٍ فَأَنَا أُحَرِّكُهُمَا لَكُمْ كَمَا كَانَ رَسُولُ اللَّهِ صلى الله عليه وسلم يُحَرِّكُهُمَا‏.‏ وَقَالَ سَعِيدٌ أَنَا أُحَرِّكُهُمَا كَمَا رَأَيْتُ ابْنَ عَبَّاسٍ يُحَرِّكُهُمَا‏.‏ فَحَرَّكَ شَفَتَيْهِ ـ فَأَنْزَلَ اللَّهُ تَعَالَى ‏{‏لاَ تُحَرِّكْ بِهِ لِسَانَكَ لِتَعْجَلَ بِهِ* إِنَّ عَلَيْنَا جَمْعَهُ وَقُرْآنَهُ‏}‏ قَالَ جَمْعُهُ لَهُ فِي صَدْرِكَ، وَتَقْرَأَهُ ‏{‏فَإِذَا قَرَأْنَاهُ فَاتَّبِعْ قُرْآنَهُ‏}‏ قَالَ فَاسْتَمِعْ لَهُ وَأَنْصِتْ ‏{‏ثُمَّ إِنَّ عَلَيْنَا بَيَانَهُ‏}‏ ثُمَّ إِنَّ عَلَيْنَا أَنْ تَقْرَأَهُ‏.‏ فَكَانَ رَسُولُ اللَّهِ صلى الله عليه وسلم بَعْدَ ذَلِكَ إِذَا أَتَاهُ جِبْرِيلُ اسْتَمَعَ، فَإِذَا انْطَلَقَ جِبْرِيلُ قَرَأَهُ النَّبِيُّ صلى الله عليه وسلم كَمَا قَرَأَهُ‏.‏', NULL, NULL, 'ar'),
(18, 9, 'Said bin Jubair', 'Ibn ''Abbas in the explanation of the statement of Allah "Move not your tongue concerning (the Quran) to make haste therewith." (75.16) said "Allah''s Messenger (sallallahu ‘alaihi wa sallam) used to bear the revelation with great trouble and used to move his lips (quickly) with the Inspiration." Ibn ''Abbas moved his lips saying, "I am moving my lips in front of you as Allah''s Messenger (sallallahu ‘alaihi wa sallam) used to move his." Said moved his lips saying: "I am moving my lips, as I saw Ibn ''Abbas moving his." Ibn ''Abbas added, "So Allah revealed ''Move not your tongue concerning (the Qur''an) to make haste therewith. It is for Us to collect it and to give you (O Muhammad) the ability to recite it (the Quran)'' (75.16-17) which means that Allah will make him (the Prophet (sallallahu ‘alaihi wa sallam) ) remember the portion of the Qur''an which was revealed at that time by heart and recite it. The statement of Allah: ''And when we have recited it to you (O Muhammad through Gabriel) then you follow its (Quran) recital'' (75.18) means ''listen to it and be silent.'' Then it is for Us (Allah) to make it clear to you'' (75.19) means ''Then it is (for Allah) to make you recite it (and its meaning will be clear by itself through your tongue). Afterwards, Allah''s Messenger (sallallahu ‘alaihi wa sallam) used to listen to Gabriel whenever he came and after his departure he used to recite it as Gabriel had recited it."', NULL, 'Sahih al-Bukhari 5\r\nBook 1, Hadith 5', 'en'),
(19, 10, NULL, 'حَدَّثَنَا عَبْدَانُ، قَالَ أَخْبَرَنَا عَبْدُ اللَّهِ، قَالَ أَخْبَرَنَا يُونُسُ، عَنِ الزُّهْرِيِّ، ح وَحَدَّثَنَا بِشْرُ بْنُ مُحَمَّدٍ، قَالَ أَخْبَرَنَا عَبْدُ اللَّهِ، قَالَ أَخْبَرَنَا يُونُسُ، وَمَعْمَرٌ، عَنِ الزُّهْرِيِّ، نَحْوَهُ قَالَ أَخْبَرَنِي عُبَيْدُ اللَّهِ بْنُ عَبْدِ اللَّهِ، عَنِ ابْنِ عَبَّاسٍ، قَالَ كَانَ رَسُولُ اللَّهِ صلى الله عليه وسلم أَجْوَدَ النَّاسِ، وَكَانَ أَجْوَدُ مَا يَكُونُ فِي رَمَضَانَ حِينَ يَلْقَاهُ جِبْرِيلُ، وَكَانَ يَلْقَاهُ فِي كُلِّ لَيْلَةٍ مِنْ رَمَضَانَ فَيُدَارِسُهُ الْقُرْآنَ، فَلَرَسُولُ اللَّهِ صلى الله عليه وسلم أَجْوَدُ بِالْخَيْرِ مِنَ الرِّيحِ الْمُرْسَلَةِ', NULL, NULL, 'ar'),
(20, 10, 'Ibn ''Abbas', 'Allah''s Messenger (sallallahu ‘alaihi wa sallam) was the most generous of all the people, and he used to reach the peak in generosity in the month of Ramadan when Gabriel met him. Gabriel used to meet him every night of Ramadan to teach him the Qur''an. Allah''s Messenger (sallallahu ‘alaihi wa sallam) was the most generous person, even more generous than the strong uncontrollable wind (in readiness and haste to do charitable deeds).', NULL, 'Sahih al-Bukhari 6\r\nBook 1, Hadith 6', 'en'),
(21, 11, NULL, 'حَدَّثَنَا أَبُو الْيَمَانِ الْحَكَمُ بْنُ نَافِعٍ، قَالَ أَخْبَرَنَا شُعَيْبٌ، عَنِ الزُّهْرِيِّ، قَالَ أَخْبَرَنِي عُبَيْدُ اللَّهِ بْنُ عَبْدِ اللَّهِ بْنِ عُتْبَةَ بْنِ مَسْعُودٍ، أَنَّ عَبْدَ اللَّهِ بْنَ عَبَّاسٍ، أَخْبَرَهُ أَنَّ أَبَا سُفْيَانَ بْنَ حَرْبٍ أَخْبَرَهُ أَنَّ هِرَقْلَ أَرْسَلَ إِلَيْهِ فِي رَكْبٍ مِنْ قُرَيْشٍ ـ وَكَانُوا تُجَّارًا بِالشَّأْمِ ـ فِي الْمُدَّةِ الَّتِي كَانَ رَسُولُ اللَّهِ صلى الله عليه وسلم مَادَّ فِيهَا أَبَا سُفْيَانَ وَكُفَّارَ قُرَيْشٍ، فَأَتَوْهُ وَهُمْ بِإِيلِيَاءَ فَدَعَاهُمْ فِي مَجْلِسِهِ، وَحَوْلَهُ عُظَمَاءُ الرُّومِ ثُمَّ دَعَاهُمْ وَدَعَا بِتَرْجُمَانِهِ فَقَالَ أَيُّكُمْ أَقْرَبُ نَسَبًا بِهَذَا الرَّجُلِ الَّذِي يَزْعُمُ أَنَّهُ نَبِيٌّ فَقَالَ أَبُو سُفْيَانَ فَقُلْتُ أَنَا أَقْرَبُهُمْ نَسَبًا‏.‏ فَقَالَ أَدْنُوهُ مِنِّي، وَقَرِّبُوا أَصْحَابَهُ، فَاجْعَلُوهُمْ عِنْدَ ظَهْرِهِ‏.‏ ثُمَّ قَالَ لِتَرْجُمَانِهِ قُلْ لَهُمْ إِنِّي سَائِلٌ هَذَا عَنْ هَذَا الرَّجُلِ، فَإِنْ كَذَبَنِي فَكَذِّبُوهُ‏.‏ فَوَاللَّهِ لَوْلاَ الْحَيَاءُ مِنْ أَنْ يَأْثِرُوا عَلَىَّ كَذِبًا لَكَذَبْتُ عَنْهُ، ثُمَّ كَانَ أَوَّلَ مَا سَأَلَنِي عَنْهُ أَنْ قَالَ كَيْفَ نَسَبُهُ فِيكُمْ قُلْتُ هُوَ فِينَا ذُو نَسَبٍ‏.‏ قَالَ فَهَلْ قَالَ هَذَا الْقَوْلَ مِنْكُمْ أَحَدٌ قَطُّ قَبْلَهُ قُلْتُ لاَ‏.‏ قَالَ فَهَلْ كَانَ مِنْ آبَائِهِ مِنْ مَلِكٍ قُلْتُ لاَ‏.‏ قَالَ فَأَشْرَافُ النَّاسِ يَتَّبِعُونَهُ أَمْ ضُعَفَاؤُهُمْ فَقُلْتُ بَلْ ضُعَفَاؤُهُمْ‏.‏ قَالَ أَيَزِيدُونَ أَمْ يَنْقُصُونَ قُلْتُ بَلْ يَزِيدُونَ‏.‏ قَالَ فَهَلْ يَرْتَدُّ أَحَدٌ مِنْهُمْ سَخْطَةً لِدِينِهِ بَعْدَ أَنْ يَدْخُلَ فِيهِ قُلْتُ لاَ‏.‏ قَالَ فَهَلْ كُنْتُمْ تَتَّهِمُونَهُ بِالْكَذِبِ قَبْلَ أَنْ يَقُولَ مَا قَالَ قُلْتُ لاَ‏.‏ قَالَ فَهَلْ يَغْدِرُ قُلْتُ لاَ، وَنَحْنُ مِنْهُ فِي مُدَّةٍ لاَ نَدْرِي مَا هُوَ فَاعِلٌ فِيهَا‏.‏ قَالَ وَلَمْ تُمْكِنِّي كَلِمَةٌ أُدْخِلُ فِيهَا شَيْئًا غَيْرُ هَذِهِ الْكَلِمَةِ‏.‏ قَالَ فَهَلْ قَاتَلْتُمُوهُ قُلْتُ نَعَمْ‏.‏ قَالَ فَكَيْفَ كَانَ قِتَالُكُمْ إِيَّاهُ قُلْتُ الْحَرْبُ بَيْنَنَا وَبَيْنَهُ سِجَالٌ، يَنَالُ مِنَّا وَنَنَالُ مِنْهُ‏.‏ قَالَ مَاذَا يَأْمُرُكُمْ قُلْتُ يَقُولُ اعْبُدُوا اللَّهَ وَحْدَهُ، وَلاَ تُشْرِكُوا بِهِ شَيْئًا، وَاتْرُكُوا مَا يَقُولُ آبَاؤُكُمْ، وَيَأْمُرُنَا بِالصَّلاَةِ وَالصِّدْقِ وَالْعَفَافِ وَالصِّلَةِ‏.‏ فَقَالَ لِلتَّرْجُمَانِ قُلْ لَهُ سَأَلْتُكَ عَنْ نَسَبِهِ، فَذَكَرْتَ أَنَّهُ فِيكُمْ ذُو نَسَبٍ، فَكَذَلِكَ الرُّسُلُ تُبْعَثُ فِي نَسَبِ قَوْمِهَا، وَسَأَلْتُكَ هَلْ قَالَ أَحَدٌ مِنْكُمْ هَذَا الْقَوْلَ فَذَكَرْتَ أَنْ لاَ، فَقُلْتُ لَوْ كَانَ أَحَدٌ قَالَ هَذَا الْقَوْلَ قَبْلَهُ لَقُلْتُ رَجُلٌ يَأْتَسِي بِقَوْلٍ قِيلَ قَبْلَهُ، وَسَأَلْتُكَ هَلْ كَانَ مِنْ آبَائِهِ مِنْ مَلِكٍ فَذَكَرْتَ أَنْ لاَ، قُلْتُ فَلَوْ كَانَ مِنْ آبَائِهِ مِنْ مَلِكٍ قُلْتُ رَجُلٌ يَطْلُبُ مُلْكَ أَبِيهِ، وَسَأَلْتُكَ هَلْ كُنْتُمْ تَتَّهِمُونَهُ بِالْكَذِبِ قَبْلَ أَنْ يَقُولَ مَا قَالَ فَذَكَرْتَ أَنْ لاَ، فَقَدْ أَعْرِفُ أَنَّهُ لَمْ يَكُنْ لِيَذَرَ الْكَذِبَ عَلَى النَّاسِ وَيَكْذِبَ عَلَى اللَّهِ، وَسَأَلْتُكَ أَشْرَافُ النَّاسِ اتَّبَعُوهُ أَمْ ضُعَفَاؤُهُمْ فَذَكَرْتَ أَنَّ ضُعَفَاءَهُمُ اتَّبَعُوهُ، وَهُمْ أَتْبَاعُ الرُّسُلِ، وَسَأَلْتُكَ أَيَزِيدُونَ أَمْ يَنْقُصُونَ فَذَكَرْتَ أَنَّهُمْ يَزِيدُونَ، وَكَذَلِكَ أَمْرُ الإِيمَانِ حَتَّى يَتِمَّ، وَسَأَلْتُكَ أَيَرْتَدُّ أَحَدٌ سَخْطَةً لِدِينِهِ بَعْدَ أَنْ يَدْخُلَ فِيهِ فَذَكَرْتَ أَنْ لاَ، وَكَذَلِكَ الإِيمَانُ حِينَ تُخَالِطُ بَشَاشَتُهُ الْقُلُوبَ، وَسَأَلْتُكَ هَلْ يَغْدِرُ فَذَكَرْتَ أَنْ لاَ، وَكَذَلِكَ الرُّسُلُ لاَ تَغْدِرُ، وَسَأَلْتُكَ بِمَا يَأْمُرُكُمْ، فَذَكَرْتَ أَنَّهُ يَأْمُرُكُمْ أَنْ تَعْبُدُوا اللَّهَ، وَلاَ تُشْرِكُوا بِهِ شَيْئًا، وَيَنْهَاكُمْ عَنْ عِبَادَةِ الأَوْثَانِ، وَيَأْمُرُكُمْ بِالصَّلاَةِ وَالصِّدْقِ وَالْعَفَافِ‏.‏ فَإِنْ كَانَ مَا تَقُولُ حَقًّا فَسَيَمْلِكُ مَوْضِعَ قَدَمَىَّ هَاتَيْنِ، وَقَدْ كُنْتُ أَعْلَمُ أَنَّهُ خَارِجٌ، لَمْ أَكُنْ أَظُنُّ أَنَّهُ مِنْكُمْ، فَلَوْ أَنِّي أَعْلَمُ أَنِّي أَخْلُصُ إِلَيْهِ لَتَجَشَّمْتُ لِقَاءَهُ، وَلَوْ كُنْتُ عِنْدَهُ لَغَسَلْتُ عَنْ قَدَمِهِ‏.‏ ثُمَّ دَعَا بِكِتَابِ رَسُولِ اللَّهِ صلى الله عليه وسلم الَّذِي بَعَثَ بِهِ دِحْيَةُ إِلَى عَظِيمِ بُصْرَى، فَدَفَعَهُ إِلَى هِرَقْلَ فَقَرَأَهُ فَإِذَا فِيهِ بِسْمِ اللَّهِ الرَّحْمَنِ الرَّحِيمِ‏.‏ مِنْ مُحَمَّدٍ عَبْدِ اللَّهِ وَرَسُولِهِ إِلَى هِرَقْلَ عَظِيمِ الرُّومِ‏.‏ سَلاَمٌ عَلَى مَنِ اتَّبَعَ الْهُدَى، أَمَّا بَعْدُ فَإِنِّي أَدْعُوكَ بِدِعَايَةِ الإِسْلاَمِ، أَسْلِمْ تَسْلَمْ، يُؤْتِكَ اللَّهُ أَجْرَكَ مَرَّتَيْنِ، فَإِنْ تَوَلَّيْتَ فَإِنَّ عَلَيْكَ إِثْمَ الأَرِيسِيِّينَ وَ‏{‏يَا أَهْلَ الْكِتَابِ تَعَالَوْا إِلَى كَلِمَةٍ سَوَاءٍ بَيْنَنَا وَبَيْنَكُمْ أَنْ لاَ نَعْبُدَ إِلاَّ اللَّهَ وَلاَ نُشْرِكَ بِهِ شَيْئًا وَلاَ يَتَّخِذَ بَعْضُنَا بَعْضًا أَرْبَابًا مِنْ دُونِ اللَّهِ فَإِنْ تَوَلَّوْا فَقُولُوا اشْهَدُوا بِأَنَّا مُسْلِمُونَ‏}‏ قَالَ أَبُو سُفْيَانَ فَلَمَّا قَالَ مَا قَالَ، وَفَرَغَ مِنْ قِرَاءَةِ الْكِتَابِ كَثُرَ عِنْدَهُ الصَّخَبُ، وَارْتَفَعَتِ الأَصْوَاتُ وَأُخْرِجْنَا، فَقُلْتُ لأَصْحَابِي حِينَ أُخْرِجْنَا لَقَدْ أَمِرَ أَمْرُ ابْنِ أَبِي كَبْشَةَ، إِنَّهُ يَخَافُهُ مَلِكُ بَنِي الأَصْفَرِ‏.‏ فَمَا زِلْتُ مُوقِنًا أَنَّهُ سَيَظْهَرُ حَتَّى أَدْخَلَ اللَّهُ عَلَىَّ الإِسْلاَمَ‏.‏ وَكَانَ ابْنُ النَّاظُورِ صَاحِبُ إِيلِيَاءَ وَهِرَقْلَ سُقُفًّا عَلَى نَصَارَى الشَّأْمِ، يُحَدِّثُ أَنَّ هِرَقْلَ حِينَ قَدِمَ إِيلِيَاءَ أَصْبَحَ يَوْمًا خَبِيثَ النَّفْسِ، فَقَالَ بَعْضُ بَطَارِقَتِهِ قَدِ اسْتَنْكَرْنَا هَيْئَتَكَ‏.‏ قَالَ ابْنُ النَّاظُورِ وَكَانَ هِرَقْلُ حَزَّاءً يَنْظُرُ فِي النُّجُومِ، فَقَالَ لَهُمْ حِينَ سَأَلُوهُ إِنِّي رَأَيْتُ اللَّيْلَةَ حِينَ نَظَرْتُ فِي النُّجُومِ مَلِكَ الْخِتَانِ قَدْ ظَهَرَ، فَمَنْ يَخْتَتِنُ مِنْ هَذِهِ الأُمَّةِ قَالُوا لَيْسَ يَخْتَتِنُ إِلاَّ الْيَهُودُ فَلاَ يُهِمَّنَّكَ شَأْنُهُمْ وَاكْتُبْ إِلَى مَدَايِنِ مُلْكِكَ، فَيَقْتُلُوا مَنْ فِيهِمْ مِنَ الْيَهُودِ‏.‏ فَبَيْنَمَا هُمْ عَلَى أَمْرِهِمْ أُتِيَ هِرَقْلُ بِرَجُلٍ أَرْسَلَ بِهِ مَلِكُ غَسَّانَ، يُخْبِرُ عَنْ خَبَرِ رَسُولِ اللَّهِ صلى الله عليه وسلم فَلَمَّا اسْتَخْبَرَهُ هِرَقْلُ قَالَ اذْهَبُوا فَانْظُرُوا أَمُخْتَتِنٌ هُوَ أَمْ لاَ‏.‏ فَنَظَرُوا إِلَيْهِ، فَحَدَّثُوهُ أَنَّهُ مُخْتَتِنٌ، وَسَأَلَهُ عَنِ الْعَرَبِ فَقَالَ هُمْ يَخْتَتِنُونَ‏.‏ فَقَالَ هِرَقْلُ هَذَا مَلِكُ هَذِهِ الأُمَّةِ قَدْ ظَهَرَ‏.‏ ثُمَّ كَتَبَ هِرَقْلُ إِلَى صَاحِبٍ لَهُ بِرُومِيَةَ، وَكَانَ نَظِيرَهُ فِي الْعِلْمِ، وَسَارَ هِرَقْلُ إِلَى حِمْصَ، فَلَمْ يَرِمْ حِمْصَ حَتَّى أَتَاهُ كِتَابٌ مِنْ صَاحِبِهِ يُوَافِقُ رَأْىَ هِرَقْلَ عَلَى خُرُوجِ النَّبِيِّ صلى الله عليه وسلم وَأَنَّهُ نَبِيٌّ، فَأَذِنَ هِرَقْلُ لِعُظَمَاءِ الرُّومِ فِي دَسْكَرَةٍ لَهُ بِحِمْصَ ثُمَّ أَمَرَ بِأَبْوَابِهَا فَغُلِّقَتْ، ثُمَّ اطَّلَعَ فَقَالَ يَا مَعْشَرَ الرُّومِ، هَلْ لَكُمْ فِي الْفَلاَحِ وَالرُّشْدِ وَأَنْ يَثْبُتَ مُلْكُكُمْ فَتُبَايِعُوا هَذَا النَّبِيَّ، فَحَاصُوا حَيْصَةَ حُمُرِ الْوَحْشِ إِلَى الأَبْوَابِ، فَوَجَدُوهَا قَدْ غُلِّقَتْ، فَلَمَّا رَأَى هِرَقْلُ نَفْرَتَهُمْ، وَأَيِسَ مِنَ الإِيمَانِ قَالَ رُدُّوهُمْ عَلَىَّ‏.‏ وَقَالَ إِنِّي قُلْتُ مَقَالَتِي آنِفًا أَخْتَبِرُ بِهَا شِدَّتَكُمْ عَلَى دِينِكُمْ، فَقَدْ رَأَيْتُ‏.‏ فَسَجَدُوا لَهُ وَرَضُوا عَنْهُ، فَكَانَ ذَلِكَ آخِرَ شَأْنِ هِرَقْلَ‏.‏ رَوَاهُ صَالِحُ بْنُ كَيْسَانَ وَيُونُسُ وَمَعْمَرٌ عَنِ الزُّهْرِيِّ‏.‏', NULL, NULL, 'ar'),
(22, 11, '''Abdullah bin ''Abbas', 'Abu Sufyan bin Harb informed me that Heraclius had sent a messenger to him while he had been accompanying a caravan from Quraish. They were merchants doing business in Sham (Syria, Palestine, Lebanon and Jordan), at the time when Allah''s Messenger (sallallahu ‘alaihi wa sallam) had truce with Abu Sufyan and Quraish infidels. So Abu Sufyan and his companions went to Heraclius at Ilya (Jerusalem). Heraclius called them in the court and he had all the senior Roman dignitaries around him. He called for his translator who, translating Heraclius''s question said to them, "Who amongst you is closely related to that man who claims to be a Prophet?" Abu Sufyan replied, "I am the nearest relative to him (amongst the group)."\r\nHeraclius said, "Bring him (Abu Sufyan) close to me and make his companions stand behind him." Abu Sufyan added, Heraclius told his translator to tell my companions that he wanted to put some questions to me regarding that man (The Prophet (sallallahu ‘alaihi wa sallam) ) and that if I told a lie they (my companions) should contradict me." Abu Sufyan added, "By Allah! Had I not been afraid of my companions labeling me a liar, I would not have spoken the truth about the Prophet (sallallahu ‘alaihi wa sallam) . The first question he asked me about him was:\r\n''What is his family status amongst you?''\r\nI replied, ''He belongs to a good (noble) family amongst us.''\r\nHeraclius further asked, ''Has anybody amongst you ever claimed the same (i.e. to be a Prophet) before him?''\r\nI replied, ''No.''\r\nHe said, ''Was anybody amongst his ancestors a king?''\r\nI replied, ''No.''\r\nHeraclius asked, ''Do the nobles or the poor follow him?''\r\nI replied, ''It is the poor who follow him.''\r\nHe said, ''Are his followers increasing decreasing (day by day)?''\r\nI replied, ''They are increasing.''\r\nHe then asked, ''Does anybody amongst those who embrace his religion become displeased and renounce the religion afterwards?''\r\nI replied, ''No.''\r\nHeraclius said, ''Have you ever accused him of telling lies before his claim (to be a Prophet)?''\r\nI replied, ''No. ''\r\nHeraclius said, ''Does he break his promises?''\r\nI replied, ''No. We are at truce with him but we do not know what he will do in it.'' I could not find opportunity to say anything against him except that.\r\nHeraclius asked, ''Have you ever had a war with him?''\r\nI replied, ''Yes.''\r\nThen he said, ''What was the outcome of the battles?''\r\nI replied, ''Sometimes he was victorious and sometimes we.''\r\nHeraclius said, ''What does he order you to do?''\r\nI said, ''He tells us to worship Allah and Allah alone and not to worship anything along with Him, and to renounce all that our ancestors had said. He orders us to pray, to speak the truth, to be chaste and to keep good relations with our Kith and kin.''\r\nHeraclius asked the translator to convey to me the following, I asked you about his family and your reply was that he belonged to a very noble family. In fact all the Apostles come from noble families amongst their respective peoples. I questioned you whether anybody else amongst you claimed such a thing, your reply was in the negative. If the answer had been in the affirmative, I would have thought that this man was following the previous man''s statement. Then I asked you whether anyone of his ancestors was a king. Your reply was in the negative, and if it had been in the affirmative, I would have thought that this man wanted to take back his ancestral kingdom.\r\nI further asked whether he was ever accused of telling lies before he said what he said, and your reply was in the negative. So I wondered how a person who does not tell a lie about others could ever tell a lie about Allah. I, then asked you whether the rich people followed him or the poor. You replied that it was the poor who followed him. And in fact all the Apostle have been followed by this very class of people. Then I asked you whether his followers were increasing or decreasing. You replied that they were increasing, and in fact this is the way of true faith, till it is complete in all respects. I further asked you whether there was anybody, who, after embracing his religion, became displeased and discarded his religion. Your reply was in the negative, and in fact this is (the sign of) true faith, when its delight enters the hearts and mixes with them completely. I asked you whether he had ever betrayed. You replied in the negative and likewise the Apostles never betray. Then I asked you what he ordered you to do. You replied that he ordered you to worship Allah and Allah alone and not to worship any thing along with Him and forbade you to worship idols and ordered you to pray, to speak the truth and to be chaste. If what you have said is true, he will very soon occupy this place underneath my feet and I knew it (from the scriptures) that he was going to appear but I did not know that he would be from you, and if I could reach him definitely, I would go immediately to meet him and if I were with him, I would certainly wash his feet.'' Heraclius then asked for the letter addressed by Allah''s Apostle\r\nwhich was delivered by Dihya to the Governor of Busra, who forwarded it to Heraclius to read. The contents of the letter were as follows: "In the name of Allah the Beneficent, the Merciful (This letter is) from Muhammad the slave of Allah and His Apostle to Heraclius the ruler of Byzantine. Peace be upon him, who follows the right path. Furthermore I invite you to Islam, and if you become a Muslim you will be safe, and Allah will double your reward, and if you reject this invitation of Islam you will be committing a sin by misguiding your Arisiyin (peasants). (And I recite to you Allah''s Statement:)\r\n''O people of the scripture! Come to a word common to you and us that we worship none but Allah and that we associate nothing in worship with Him, and that none of us shall take others as Lords beside Allah. Then, if they turn away, say: Bear witness that we are Muslims (those who have surrendered to Allah).'' (3:64).\r\nAbu Sufyan then added, "When Heraclius had finished his speech and had read the letter, there was a great hue and cry in the Royal Court. So we were turned out of the court. I told my companions that the question of Ibn-Abi-Kabsha) (the Prophet (sallallahu ‘alaihi wa sallam) Muhammad) has become so prominent that even the King of Bani Al-Asfar (Byzantine) is afraid of him. Then I started to become sure that he (the Prophet (sallallahu ‘alaihi wa sallam) ) would be the conqueror in the near future till I embraced Islam (i.e. Allah guided me to it)."\r\nThe sub narrator adds, "Ibn An-Natur was the Governor of llya'' (Jerusalem) and Heraclius was the head of the Christians of Sham. Ibn An-Natur narrates that once while Heraclius was visiting ilya'' (Jerusalem), he got up in the morning with a sad mood. Some of his priests asked him why he was in that mood? Heraclius was a foreteller and an astrologer. He replied, ''At night when I looked at the stars, I saw that the leader of those who practice circumcision had appeared (become the conqueror). Who are they who practice circumcision?'' The people replied, ''Except the Jews nobody practices circumcision, so you should not be afraid of them (Jews).\r\n''Just Issue orders to kill every Jew present in the country.''\r\nWhile they were discussing it, a messenger sent by the king of Ghassan to convey the news of Allah''s Messenger (sallallahu ‘alaihi wa sallam) to Heraclius was brought in. Having heard the news, he (Heraclius) ordered the people to go and see whether the messenger of Ghassan was circumcised. The people, after seeing him, told Heraclius that he was circumcised. Heraclius then asked him about the Arabs. The messenger replied, ''Arabs also practice circumcision.''\r\n(After hearing that) Heraclius remarked that sovereignty of the ''Arabs had appeared. Heraclius then wrote a letter to his friend in Rome who was as good as Heraclius in knowledge. Heraclius then left for Homs. (a town in Syrian and stayed there till he received the reply of his letter from his friend who agreed with him in his opinion about the emergence of the Prophet (sallallahu ‘alaihi wa sallam) and the fact that he was a Prophet. On that Heraclius invited all the heads of the Byzantines to assemble in his palace at Homs. When they assembled, he ordered that all the doors of his palace be closed. Then he came out and said, ''O Byzantines! If success is your desire and if you seek right guidance and want your empire to remain then give a pledge of allegiance to this Prophet (i.e. embrace Islam).''\r\n(On hearing the views of Heraclius) the people ran towards the gates of the palace like onagers but found the doors closed. Heraclius realized their hatred towards Islam and when he lost the hope of their embracing Islam, he ordered that they should be brought back in audience.\r\n(When they returned) he said, ''What already said was just to test the strength of your conviction and I have seen it.'' The people prostrated before him and became pleased with him, and this was the end of Heraclius''s story (in connection with his faith).', NULL, 'Sahih al-Bukhari 7\r\nBook 1, Hadith 7', 'en'),
(23, 12, 'Ibn ''Umar', 'حَدَّثَنَا عُبَيْدُ اللَّهِ بْنُ مُوسَى، قَالَ أَخْبَرَنَا حَنْظَلَةُ بْنُ أَبِي سُفْيَانَ، عَنْ عِكْرِمَةَ بْنِ خَالِدٍ، عَنِ ابْنِ عُمَرَ ـ رضى الله عنهما ـ قَالَ قَالَ رَسُولُ اللَّهِ صلى الله عليه وسلم ‏ "‏ بُنِيَ الإِسْلاَمُ عَلَى خَمْسٍ شَهَادَةِ أَنْ لاَ إِلَهَ إِلاَّ اللَّهُ وَأَنَّ مُحَمَّدًا رَسُولُ اللَّهِ، وَإِقَامِ الصَّلاَةِ، وَإِيتَاءِ الزَّكَاةِ، وَالْحَجِّ، وَصَوْمِ رَمَضَانَ ‏', NULL, NULL, 'ar'),
(24, 12, 'Ibn ''Umar', 'Allah''s Messenger (sallallahu ‘alaihi wa sallam) said: Islam is based on (the following) five (principles):\r\n\r\n1. To testify that none has the right to be worshipped but Allah and Muhammad is Allah''s Messenger (sallallahu ‘alaihi wa sallam) .\r\n2. To offer the (compulsory congregational) prayers dutifully and perfectly.\r\n3. To pay Zakat (i.e. obligatory charity) .\r\n4. To perform Hajj. (i.e. Pilgrimage to Mecca)\r\n5. To observe fast during the month of Ramadan.', NULL, 'Sahih al-Bukhari 8\r\nBook 2, Hadith 1', 'en'),
(25, 13, 'Abu Huraira', 'حَدَّثَنَا عَبْدُ اللَّهِ بْنُ مُحَمَّدٍ، قَالَ حَدَّثَنَا أَبُو عَامِرٍ الْعَقَدِيُّ، قَالَ حَدَّثَنَا سُلَيْمَانُ بْنُ بِلاَلٍ، عَنْ عَبْدِ اللَّهِ بْنِ دِينَارٍ، عَنْ أَبِي صَالِحٍ، عَنْ أَبِي هُرَيْرَةَ ـ رضى الله عنه ـ عَنِ النَّبِيِّ صلى الله عليه وسلم قَالَ ‏ "‏ الإِيمَانُ بِضْعٌ وَسِتُّونَ شُعْبَةً، وَالْحَيَاءُ شُعْبَةٌ مِنَ الإِيمَانِ', NULL, NULL, 'ar'),
(26, 13, 'Abu Huraira', 'The Prophet (sallallahu ‘alaihi wa sallam) said, "Faith (Belief) consists of more than sixty branches (i.e. parts). And Haya (This term "Haya" covers a large number of concepts which are to be taken together; amongst them are self respect, modesty, bashfulness, and scruple, etc.) is a part of faith."', NULL, 'Sahih al-Bukhari 9\r\nBook 2, Hadith 2', 'en'),
(27, 14, '''Abdullah bin ''Amr', 'حَدَّثَنَا آدَمُ بْنُ أَبِي إِيَاسٍ، قَالَ حَدَّثَنَا شُعْبَةُ، عَنْ عَبْدِ اللَّهِ بْنِ أَبِي السَّفَرِ، وَإِسْمَاعِيلَ، عَنِ الشَّعْبِيِّ، عَنْ عَبْدِ اللَّهِ بْنِ عَمْرٍو ـ رضى الله عنهما ـ عَنِ النَّبِيِّ صلى الله عليه وسلم قَالَ ‏ "‏ الْمُسْلِمُ مَنْ سَلِمَ الْمُسْلِمُونَ مِنْ لِسَانِهِ وَيَدِهِ، وَالْمُهَاجِرُ مَنْ هَجَرَ مَا نَهَى اللَّهُ عَنْهُ ‏"‏‏.‏ قَالَ أَبُو عَبْدِ اللَّهِ وَقَالَ أَبُو مُعَاوِيَةَ حَدَّثَنَا دَاوُدُ عَنْ عَامِرٍ قَالَ سَمِعْتُ عَبْدَ اللَّهِ عَنِ النَّبِيِّ صلى الله عليه وسلم‏.‏ وَقَالَ عَبْدُ الأَعْلَى عَنْ دَاوُدَ عَنْ عَامِرٍ عَنْ عَبْدِ اللَّهِ عَنِ النَّبِيِّ صلى الله عليه وسلم‏.‏', NULL, NULL, 'ar'),
(28, 14, '''Abdullah bin ''Amr', 'The Prophet (sallallahu ‘alaihi wa sallam) said, "A Muslim is the one who avoids harming Muslims with his tongue and hands. And a Muhajir (emigrant) is the one who gives up (abandons) all what Allah has forbidden."', NULL, 'Sahih al-Bukhari 10\r\nBook 2, Hadith 3', 'en'),
(29, 15, 'Abu Musa', 'حَدَّثَنَا سَعِيدُ بْنُ يَحْيَى بْنِ سَعِيدٍ الْقُرَشِيِّ، قَالَ حَدَّثَنَا أَبِي قَالَ، حَدَّثَنَا أَبُو بُرْدَةَ بْنُ عَبْدِ اللَّهِ بْنِ أَبِي بُرْدَةَ، عَنْ أَبِي بُرْدَةَ، عَنْ أَبِي مُوسَى ـ رضى الله عنه ـ قَالَ قَالُوا يَا رَسُولَ اللَّهِ أَىُّ الإِسْلاَمِ أَفْضَلُ قَالَ ‏ "‏ مَنْ سَلِمَ الْمُسْلِمُونَ مِنْ لِسَانِهِ وَيَدِهِ', NULL, NULL, 'ar'),
(30, 15, 'Abu Musa', 'Some people asked Allah''s Messenger (sallallahu ‘alaihi wa sallam) , "Whose Islam is the best? i.e. (Who is a very good Muslim)?" He replied, "One who avoids harming the Muslims with his tongue and hands."', NULL, 'Sahih al-Bukhari 11\r\nBook 2, Hadith 4', 'en'),
(31, 16, '''Abdullah bin ''Amr:', 'حَدَّثَنَا عَمْرُو بْنُ خَالِدٍ، قَالَ حَدَّثَنَا اللَّيْثُ، عَنْ يَزِيدَ، عَنْ أَبِي الْخَيْرِ، عَنْ عَبْدِ اللَّهِ بْنِ عَمْرٍو ـ رضى الله عنهما ـ أَنَّ رَجُلاً، سَأَلَ النَّبِيَّ صلى الله عليه وسلم أَىُّ الإِسْلاَمِ خَيْرٌ قَالَ ‏ "‏ تُطْعِمُ الطَّعَامَ، وَتَقْرَأُ السَّلاَمَ عَلَى مَنْ عَرَفْتَ وَمَنْ لَمْ تَعْرِفْ', NULL, NULL, 'ar'),
(32, 16, '''Abdullah bin ''Amr:', 'A man asked the Prophet (sallallahu ‘alaihi wa sallam) , "What sort of deeds or (what qualities of) Islam are good?" The Prophet (sallallahu ‘alaihi wa sallam) replied, ''To feed (the poor) and greet those whom you know and those whom you do not Know (See Hadith No. 27).', NULL, 'Sahih al-Bukhari 12\r\nBook 2, Hadith 5', 'en'),
(33, 17, 'Anas', 'حَدَّثَنَا مُسَدَّدٌ، قَالَ حَدَّثَنَا يَحْيَى، عَنْ شُعْبَةَ، عَنْ قَتَادَةَ، عَنْ أَنَسٍ ـ رضى الله عنه ـ عَنِ النَّبِيِّ صلى الله عليه وسلم‏.‏\r\nوَعَنْ حُسَيْنٍ الْمُعَلِّمِ، قَالَ حَدَّثَنَا قَتَادَةُ، عَنْ أَنَسٍ، عَنِ النَّبِيِّ صلى الله عليه وسلم قَالَ ‏ "‏ لا يُؤْمِنُ أَحَدُكُمْ حَتَّى يُحِبَّ لأَخِيهِ مَا يُحِبُّ لِنَفْسِهِ', NULL, NULL, 'ar'),
(34, 17, 'Anas', 'The Prophet (sallallahu ‘alaihi wa sallam) said, "None of you will have faith till he wishes for his (Muslim) brother what he likes for himself."', NULL, NULL, 'en'),
(35, 18, NULL, 'حَدَّثَنَا أَبُو الْيَمَانِ، قَالَ أَخْبَرَنَا شُعَيْبٌ، قَالَ حَدَّثَنَا أَبُو الزِّنَادِ، عَنِ الأَعْرَجِ، عَنْ أَبِي هُرَيْرَةَ ـ رضى الله عنه ـ أَنَّ رَسُولَ اللَّهِ صلى الله عليه وسلم قَالَ ‏ "‏ فَوَالَّذِي نَفْسِي بِيَدِهِ لاَ يُؤْمِنُ أَحَدُكُمْ حَتَّى أَكُونَ أَحَبَّ إِلَيْهِ مِنْ وَالِدِهِ وَوَلَدِهِ', NULL, NULL, 'ar'),
(36, 18, 'Abu Huraira', '"Allah''s Messenger (sallallahu ‘alaihi wa sallam) said, "By Him in Whose Hands my life is, none of you will have faith till he loves me more than his father and his children."', NULL, 'Sahih al-Bukhari 14\r\nBook 2, Hadith 7', 'en');
INSERT INTO `hadith_translation` (`id`, `translatable_id`, `narrator`, `body`, `note`, `reference`, `locale`) VALUES
(37, 19, NULL, 'حَدَّثَنَا يَعْقُوبُ بْنُ إِبْرَاهِيمَ، قَالَ حَدَّثَنَا ابْنُ عُلَيَّةَ، عَنْ عَبْدِ الْعَزِيزِ بْنِ صُهَيْبٍ، عَنْ أَنَسٍ، عَنِ النَّبِيِّ صلى الله عليه وسلم ح وَحَدَّثَنَا آدَمُ، قَالَ حَدَّثَنَا شُعْبَةُ، عَنْ قَتَادَةَ، عَنْ أَنَسٍ، قَالَ قَالَ النَّبِيُّ صلى الله عليه وسلم ‏ "‏ لاَ يُؤْمِنُ أَحَدُكُمْ حَتَّى أَكُونَ أَحَبَّ إِلَيْهِ مِنْ وَالِدِهِ وَوَلَدِهِ وَالنَّاسِ أَجْمَعِينَ', NULL, NULL, 'ar'),
(38, 19, 'Anas', 'The Prophet (sallallahu ‘alaihi wa sallam) said "None of you will have faith till he loves me more than his father, his children and all mankind.', NULL, 'Sahih al-Bukhari 15\r\nBook 2, Hadith 8', 'en'),
(39, 20, NULL, 'حَدَّثَنَا مُحَمَّدُ بْنُ الْمُثَنَّى، قَالَ حَدَّثَنَا عَبْدُ الْوَهَّابِ الثَّقَفِيُّ، قَالَ حَدَّثَنَا أَيُّوبُ، عَنْ أَبِي قِلاَبَةَ، عَنْ أَنَسٍ، عَنِ النَّبِيِّ صلى الله عليه وسلم قَالَ ‏ "‏ ثَلاَثٌ مَنْ كُنَّ فِيهِ وَجَدَ حَلاَوَةَ الإِيمَانِ أَنْ يَكُونَ اللَّهُ وَرَسُولُهُ أَحَبَّ إِلَيْهِ مِمَّا سِوَاهُمَا، وَأَنْ يُحِبَّ الْمَرْءَ لاَ يُحِبُّهُ إِلاَّ لِلَّهِ، وَأَنْ يَكْرَهَ أَنْ يَعُودَ فِي الْكُفْرِ كَمَا يَكْرَهُ أَنْ يُقْذَفَ فِي النَّارِ', NULL, NULL, 'ar'),
(40, 20, 'Anas', 'The Prophet (sallallahu ‘alaihi wa sallam) said, "Whoever possesses the following three qualities will have the sweetness (delight) of faith:\r\n\r\n1. The one to whom Allah and His Apostle becomes dearer than anything else.\r\n2. Who loves a person and he loves him only for Allah''s sake.\r\n3. Who hates to revert to Atheism (disbelief) as he hates to be thrown into the fire."', NULL, 'Sahih al-Bukhari 16\r\nBook 2, Hadith 9', 'en'),
(41, 21, NULL, 'حَدَّثَنَا أَبُو الْوَلِيدِ، قَالَ حَدَّثَنَا شُعْبَةُ، قَالَ أَخْبَرَنِي عَبْدُ اللَّهِ بْنُ عَبْدِ اللَّهِ بْنِ جَبْرٍ، قَالَ سَمِعْتُ أَنَسًا، عَنِ النَّبِيِّ صلى الله عليه وسلم قَالَ ‏ "‏ آيَةُ الإِيمَانِ حُبُّ الأَنْصَارِ، وَآيَةُ النِّفَاقِ بُغْضُ الأَنْصَارِ', NULL, NULL, 'ar'),
(42, 21, 'Anas', 'The Prophet (sallallahu ‘alaihi wa sallam) said, "Love for the Ansar is a sign of faith and hatred for the Ansar is a sign of hypocrisy."', NULL, 'Sahih al-Bukhari 17\r\nBook 2, Hadith 10', 'en'),
(43, 22, NULL, 'حَدَّثَنَا أَبُو الْيَمَانِ، قَالَ أَخْبَرَنَا شُعَيْبٌ، عَنِ الزُّهْرِيِّ، قَالَ أَخْبَرَنِي أَبُو إِدْرِيسَ، عَائِذُ اللَّهِ بْنُ عَبْدِ اللَّهِ أَنَّ عُبَادَةَ بْنَ الصَّامِتِ ـ رضى الله عنه ـ وَكَانَ شَهِدَ بَدْرًا، وَهُوَ أَحَدُ النُّقَبَاءِ لَيْلَةَ الْعَقَبَةِ ـ أَنَّ رَسُولَ اللَّهِ صلى الله عليه وسلم قَالَ وَحَوْلَهُ عِصَابَةٌ مِنْ أَصْحَابِهِ ‏ "‏ بَايِعُونِي عَلَى أَنْ لاَ تُشْرِكُوا بِاللَّهِ شَيْئًا، وَلاَ تَسْرِقُوا، وَلاَ تَزْنُوا، وَلاَ تَقْتُلُوا أَوْلاَدَكُمْ، وَلاَ تَأْتُوا بِبُهْتَانٍ تَفْتَرُونَهُ بَيْنَ أَيْدِيكُمْ وَأَرْجُلِكُمْ، وَلاَ تَعْصُوا فِي مَعْرُوفٍ، فَمَنْ وَفَى مِنْكُمْ فَأَجْرُهُ عَلَى اللَّهِ، وَمَنْ أَصَابَ مِنْ ذَلِكَ شَيْئًا فَعُوقِبَ فِي الدُّنْيَا فَهُوَ كَفَّارَةٌ لَهُ، وَمَنْ أَصَابَ مِنْ ذَلِكَ شَيْئًا ثُمَّ سَتَرَهُ اللَّهُ، فَهُوَ إِلَى اللَّهِ إِنْ شَاءَ عَفَا عَنْهُ، وَإِنْ شَاءَ عَاقَبَهُ ‏"‏‏.‏ فَبَايَعْنَاهُ عَلَى ذَلِكَ‏.‏', NULL, NULL, 'ar'),
(44, 22, '''Ubada bin As-Samit', 'who took part in the battle of Badr and was a Naqib (a person heading a group of six persons), on the night of Al-''Aqaba pledge: Allah''s Apostle said while a group of his companions were around him, "Swear allegiance to me for:\r\n1. Not to join anything in worship along with Allah.\r\n2. Not to steal.\r\n3. Not to commit illegal sexual intercourse.\r\n4. Not to kill your children.\r\n5. Not to accuse an innocent person (to spread such an accusation among people).\r\n6. Not to be disobedient (when ordered) to do good deed."\r\nThe Prophet (sallallahu ‘alaihi wa sallam) added: "Whoever among you fulfills his pledge will be rewarded by Allah. And whoever indulges in any one of them (except the ascription of partners to Allah) and gets the punishment in this world, that punishment will be an expiation for that sin. And if one indulges in any of them, and Allah conceals his sin, it is up to Him to forgive or punish him (in the Hereafter)." ''Ubada bin As-Samit added: "So we swore allegiance for these." (points to Allah''s Apostle)', NULL, 'Sahih al-Bukhari 18\r\nBook 2, Hadith 11', 'en'),
(45, 23, NULL, 'حَدَّثَنَا عَبْدُ اللَّهِ بْنُ مَسْلَمَةَ، عَنْ مَالِكٍ، عَنْ عَبْدِ الرَّحْمَنِ بْنِ عَبْدِ اللَّهِ بْنِ عَبْدِ الرَّحْمَنِ بْنِ أَبِي صَعْصَعَةَ، عَنْ أَبِيهِ، عَنْ أَبِي سَعِيدٍ الْخُدْرِيِّ، أَنَّهُ قَالَ قَالَ رَسُولُ اللَّهِ صلى الله عليه وسلم ‏ "‏ يُوشِكُ أَنْ يَكُونَ خَيْرَ مَالِ الْمُسْلِمِ غَنَمٌ يَتْبَعُ بِهَا شَعَفَ الْجِبَالِ وَمَوَاقِعَ الْقَطْرِ، يَفِرُّ بِدِينِهِ مِنَ الْفِتَنِ ‏"‏‏.‏', NULL, NULL, 'ar'),
(46, 23, 'Abu Said Al-Khudri', 'Allah''s Messenger (sallallahu ‘alaihi wa sallam) said, "A time will come that the best property of a Muslim will be sheep which he will take on the top of mountains and the places of rainfall (valleys) so as to flee with his religion from afflictions."', NULL, 'Sahih al-Bukhari 19\r\nBook 2, Hadith 12', 'en'),
(47, 24, NULL, 'حَدَّثَنَا مُحَمَّدُ بْنُ سَلاَمٍ، قَالَ أَخْبَرَنَا عَبْدَةُ، عَنْ هِشَامٍ، عَنْ أَبِيهِ، عَنْ عَائِشَةَ، قَالَتْ كَانَ رَسُولُ اللَّهِ صلى الله عليه وسلم إِذَا أَمَرَهُمْ أَمَرَهُمْ مِنَ الأَعْمَالِ بِمَا يُطِيقُونَ قَالُوا إِنَّا لَسْنَا كَهَيْئَتِكَ يَا رَسُولَ اللَّهِ، إِنَّ اللَّهَ قَدْ غَفَرَ لَكَ مَا تَقَدَّمَ مِنْ ذَنْبِكَ وَمَا تَأَخَّرَ‏.‏ فَيَغْضَبُ حَتَّى يُعْرَفَ الْغَضَبُ فِي وَجْهِهِ ثُمَّ يَقُولُ ‏ "‏ إِنَّ أَتْقَاكُمْ وَأَعْلَمَكُمْ بِاللَّهِ أَنَا', NULL, NULL, 'ar'),
(48, 24, '''Aisha', 'Whenever Allah''s Messenger (sallallahu ‘alaihi wa sallam) ordered the Muslims to do something, he used to order them deeds which were easy for them to do, (according to their strength endurance). They said, "O Allah''s Messenger (sallallahu ‘alaihi wa sallam) ! We are not like you. Allah has forgiven your past and future sins." So Allah''s Apostle became angry and it was apparent on his face. He said, "I am the most Allah fearing, and know Allah better than all of you do."', NULL, 'Sahih al-Bukhari 20\r\nBook 2, Hadith 13', 'en'),
(49, 25, NULL, 'حَدَّثَنَا سُلَيْمَانُ بْنُ حَرْبٍ، قَالَ حَدَّثَنَا شُعْبَةُ، عَنْ قَتَادَةَ، عَنْ أَنَسٍ ـ رضى الله عنه ـ عَنِ النَّبِيِّ صلى الله عليه وسلم قَالَ ‏ "‏ ثَلاَثٌ مَنْ كُنَّ فِيهِ وَجَدَ حَلاَوَةَ الإِيمَانِ مَنْ كَانَ اللَّهُ وَرَسُولُهُ أَحَبَّ إِلَيْهِ مِمَّا سِوَاهُمَا، وَمَنْ أَحَبَّ عَبْدًا لاَ يُحِبُّهُ إِلاَّ لِلَّهِ، وَمَنْ يَكْرَهُ أَنْ يَعُودَ فِي الْكُفْرِ بَعْدَ إِذْ أَنْقَذَهُ اللَّهُ، كَمَا يَكْرَهُ أَنْ يُلْقَى فِي النَّارِ', NULL, NULL, 'ar'),
(50, 25, 'Anas', 'The Prophet (sallallahu ‘alaihi wa sallam) said, "Whoever possesses the following three qualities will taste the sweetness of faith:\r\n1. The one to whom Allah and His Apostle become dearer than anything else.\r\n2. Who loves a person and he loves him only for Allah''s sake.\r\n3. Who hates to revert to disbelief (Atheism) after Allah has brought (saved) him out from it, as he hates to be thrown in fire."', NULL, 'Sahih al-Bukhari 21\r\nBook 2, Hadith 14', 'en'),
(51, 26, NULL, 'حَدَّثَنَا مُحَمَّدُ بْنُ عُبَيْدِ اللَّهِ، قَالَ حَدَّثَنَا إِبْرَاهِيمُ بْنُ سَعْدٍ، عَنْ صَالِحٍ، عَنِ ابْنِ شِهَابٍ، عَنْ أَبِي أُمَامَةَ بْنِ سَهْلٍ، أَنَّهُ سَمِعَ أَبَا سَعِيدٍ الْخُدْرِيَّ، يَقُولُ قَالَ رَسُولُ اللَّهِ صلى الله عليه وسلم ‏"‏ بَيْنَا أَنَا نَائِمٌ رَأَيْتُ النَّاسَ يُعْرَضُونَ عَلَىَّ، وَعَلَيْهِمْ قُمُصٌ مِنْهَا مَا يَبْلُغُ الثُّدِيَّ، وَمِنْهَا مَا دُونَ ذَلِكَ، وَعُرِضَ عَلَىَّ عُمَرُ بْنُ الْخَطَّابِ وَعَلَيْهِ قَمِيصٌ يَجُرُّهُ ‏"‏‏.‏ قَالُوا فَمَا أَوَّلْتَ ذَلِكَ يَا رَسُولَ اللَّهِ قَالَ ‏"‏ الدِّينَ', NULL, NULL, 'ar'),
(52, 26, 'Abu Said Al-Khudri', 'Allah''s Messenger (sallallahu ‘alaihi wa sallam) said, "While I was sleeping I saw (in a dream) some people wearing shirts of which some were reaching up to the breasts only while others were even shorter than that. Umar bin Al-Khattab was shown wearing a shirt that he was dragging." The people asked, "How did you interpret it? (What is its interpretation) O Allah''s Messenger (sallallahu ‘alaihi wa sallam) ?" He (the Prophet (sallallahu ‘alaihi wa sallam) ) replied, "It is the Religion."', NULL, 'Sahih al-Bukhari 23\r\nBook 2, Hadith 16\r\nVol. 1, Book 2, Hadith 23', 'en'),
(53, 27, NULL, 'حَدَّثَنَا إِسْمَاعِيلُ، قَالَ حَدَّثَنِي مَالِكٌ، عَنْ عَمْرِو بْنِ يَحْيَى الْمَازِنِيِّ، عَنْ أَبِيهِ، عَنْ أَبِي سَعِيدٍ الْخُدْرِيِّ، رضى الله عنه ـ عَنِ النَّبِيِّ صلى الله عليه وسلم قَالَ ‏"‏ يَدْخُلُ أَهْلُ الْجَنَّةِ الْجَنَّةَ، وَأَهْلُ النَّارِ النَّارَ، ثُمَّ يَقُولُ اللَّهُ تَعَالَى أَخْرِجُوا مَنْ كَانَ فِي قَلْبِهِ مِثْقَالُ حَبَّةٍ مِنْ خَرْدَلٍ مِنْ إِيمَانٍ‏.‏ فَيُخْرَجُونَ مِنْهَا قَدِ اسْوَدُّوا فَيُلْقَوْنَ فِي نَهَرِ الْحَيَا ـ أَوِ الْحَيَاةِ، شَكَّ مَالِكٌ ـ فَيَنْبُتُونَ كَمَا تَنْبُتُ الْحِبَّةُ فِي جَانِبِ السَّيْلِ، أَلَمْ تَرَ أَنَّهَا تَخْرُجُ صَفْرَاءَ مُلْتَوِيَةً ‏"‏‏.‏ قَالَ وُهَيْبٌ حَدَّثَنَا عَمْرٌو ‏"‏ الْحَيَاةِ ‏"‏‏.‏ وَقَالَ ‏"‏ خَرْدَلٍ مِنْ خَيْرٍ', NULL, NULL, 'ar'),
(54, 27, 'Abu Said Al-Khudri', 'The Prophet (sallallahu ‘alaihi wa sallam) said, "When the people of Paradise will enter Paradise and the people of Hell will go to Hell, Allah will order those who have had faith equal to the weight of a grain of mustard seed to be taken out from Hell. So they will be taken out but (by then) they will be blackened (charred). Then they will be put in the river of Haya'' (rain) or Hayat (life) (the Narrator is in doubt as to which is the right term), and they will revive like a grain that grows near the bank of a flood channel. Don''t you see that it comes out yellow and twisted"', NULL, 'Sahih al-Bukhari 22\r\nBook 2, Hadith 15\r\nVol. 1, Book 2, Hadith 22', 'en'),
(55, 28, NULL, 'حَدَّثَنَا عَبْدُ اللَّهِ بْنُ يُوسُفَ، قَالَ أَخْبَرَنَا مَالِكُ بْنُ أَنَسٍ، عَنِ ابْنِ شِهَابٍ، عَنْ سَالِمِ بْنِ عَبْدِ اللَّهِ، عَنْ أَبِيهِ، أَنَّ رَسُولَ اللَّهِ صلى الله عليه وسلم مَرَّ عَلَى رَجُلٍ مِنَ الأَنْصَارِ وَهُوَ يَعِظُ أَخَاهُ فِي الْحَيَاءِ، فَقَالَ رَسُولُ اللَّهِ صلى الله عليه وسلم ‏ "‏ دَعْهُ فَإِنَّ الْحَيَاءَ مِنَ الإِيمَانِ', NULL, NULL, 'ar'),
(56, 28, '''Abdullah (bin ''Umar)', 'Once Allah''s Messenger (sallallahu ‘alaihi wa sallam) passed by an Ansari (man) who was admonishing to his brother regarding Haya''. On that Allah''s Messenger (sallallahu ‘alaihi wa sallam) said, "Leave him as Haya'' is a part of faith." (See Hadith No. 8)', NULL, 'Sahih al-Bukhari 24\r\nBook 2, Hadith 17\r\nVol. 1, Book 2, Hadith 24', 'en'),
(57, 29, NULL, 'حَدَّثَنَا عَبْدُ اللَّهِ بْنُ مُحَمَّدٍ الْمُسْنَدِيُّ، قَالَ حَدَّثَنَا أَبُو رَوْحٍ الْحَرَمِيُّ بْنُ عُمَارَةَ، قَالَ حَدَّثَنَا شُعْبَةُ، عَنْ وَاقِدِ بْنِ مُحَمَّدٍ، قَالَ سَمِعْتُ أَبِي يُحَدِّثُ، عَنِ ابْنِ عُمَرَ، أَنَّ رَسُولَ اللَّهِ صلى الله عليه وسلم قَالَ ‏ "‏ أُمِرْتُ أَنْ أُقَاتِلَ النَّاسَ حَتَّى يَشْهَدُوا أَنْ لاَ إِلَهَ إِلاَّ اللَّهُ وَأَنَّ مُحَمَّدًا رَسُولُ اللَّهِ، وَيُقِيمُوا الصَّلاَةَ، وَيُؤْتُوا الزَّكَاةَ، فَإِذَا فَعَلُوا ذَلِكَ عَصَمُوا مِنِّي دِمَاءَهُمْ وَأَمْوَالَهُمْ إِلاَّ بِحَقِّ الإِسْلاَمِ، وَحِسَابُهُمْ عَلَى اللَّهِ', NULL, NULL, 'ar'),
(58, 29, 'Ibn ''Umar', 'Allah''s Messenger (sallallahu ‘alaihi wa sallam) said: "I have been ordered (by Allah) to fight against the people until they testify that none has the right to be worshipped but Allah and that Muhammad is Allah''s Messenger (sallallahu ‘alaihi wa sallam) , and offer the prayers perfectly and give the obligatory charity, so if they perform that, then they save their lives and property from me except for Islamic laws and then their reckoning (accounts) will be done by Allah."', NULL, 'Sahih al-Bukhari 25\r\nBook 2, Hadith 18\r\nVol. 1, Book 2, Hadith 25', 'en'),
(59, 30, NULL, 'حَدَّثَنَا أَحْمَدُ بْنُ يُونُسَ، وَمُوسَى بْنُ إِسْمَاعِيلَ، قَالاَ حَدَّثَنَا إِبْرَاهِيمُ بْنُ سَعْدٍ، قَالَ حَدَّثَنَا ابْنُ شِهَابٍ، عَنْ سَعِيدِ بْنِ الْمُسَيَّبِ، عَنْ أَبِي هُرَيْرَةَ، أَنَّ رَسُولَ اللَّهِ صلى الله عليه وسلم سُئِلَ أَىُّ الْعَمَلِ أَفْضَلُ فَقَالَ ‏"‏ إِيمَانٌ بِاللَّهِ وَرَسُولِهِ ‏"‏‏.‏ قِيلَ ثُمَّ مَاذَا قَالَ ‏"‏ الْجِهَادُ فِي سَبِيلِ اللَّهِ ‏"‏‏.‏ قِيلَ ثُمَّ مَاذَا قَالَ ‏"‏ حَجٌّ مَبْرُورٌ', NULL, NULL, 'ar'),
(60, 30, 'Abu Huraira', 'Allah''s Messenger (sallallahu ‘alaihi wa sallam) was asked, "What is the best deed?" He replied, "To believe in Allah and His Apostle (Muhammad). The questioner then asked, "What is the next (in goodness)? He replied, "To participate in Jihad (religious fighting) in Allah''s Cause." The questioner again asked, "What is the next (in goodness)?" He replied, "To perform Hajj (Pilgrim age to Mecca) ''Mubrur, (which is accepted by Allah and is performed with the intention of seeking Allah''s pleasure only and not to show off and without committing a sin and in accordance with the traditions of the Prophet (sallallahu ‘alaihi wa sallam) )."', NULL, 'Sahih al-Bukhari 26\r\nBook 2, Hadith 19\r\nVol. 1, Book 2, Hadith 26', 'en'),
(61, 31, NULL, 'حَدَّثَنَا أَبُو الْيَمَانِ، قَالَ أَخْبَرَنَا شُعَيْبٌ، عَنِ الزُّهْرِيِّ، قَالَ أَخْبَرَنِي عَامِرُ بْنُ سَعْدِ بْنِ أَبِي وَقَّاصٍ، عَنْ سَعْدٍ، رضى الله عنه أَنَّ رَسُولَ اللَّهِ صلى الله عليه وسلم أَعْطَى رَهْطًا وَسَعْدٌ جَالِسٌ، فَتَرَكَ رَسُولُ اللَّهِ صلى الله عليه وسلم رَجُلاً هُوَ أَعْجَبُهُمْ إِلَىَّ فَقُلْتُ يَا رَسُولَ اللَّهِ مَا لَكَ عَنْ فُلاَنٍ فَوَاللَّهِ إِنِّي لأَرَاهُ مُؤْمِنًا‏.‏ فَقَالَ ‏"‏ أَوْ مُسْلِمًا ‏"‏‏.‏ فَسَكَتُّ قَلِيلاً، ثُمَّ غَلَبَنِي مَا أَعْلَمُ مِنْهُ فَعُدْتُ لِمَقَالَتِي فَقُلْتُ مَا لَكَ عَنْ فُلاَنٍ فَوَاللَّهِ إِنِّي لأَرَاهُ مُؤْمِنًا فَقَالَ ‏"‏ أَوْ مُسْلِمًا ‏"‏‏.‏ ثُمَّ غَلَبَنِي مَا أَعْلَمُ مِنْهُ فَعُدْتُ لِمَقَالَتِي وَعَادَ رَسُولُ اللَّهِ صلى الله عليه وسلم ثُمَّ قَالَ ‏"‏ يَا سَعْدُ، إِنِّي لأُعْطِي الرَّجُلَ وَغَيْرُهُ أَحَبُّ إِلَىَّ مِنْهُ، خَشْيَةَ أَنْ يَكُبَّهُ اللَّهُ فِي النَّارِ ‏"‏‏.‏ وَرَوَاهُ يُونُسُ وَصَالِحٌ وَمَعْمَرٌ وَابْنُ أَخِي الزُّهْرِيِّ عَنِ الزُّهْرِيِّ‏.‏', NULL, NULL, 'ar'),
(62, 31, 'Sa''d', 'Allah''s Messenger (sallallahu ‘alaihi wa sallam) distributed (Zakat) amongst (a group of) people while I was sitting there but Allah''s Messenger (sallallahu ‘alaihi wa sallam) left a man whom I thought the best of the lot. I asked, "O Allah''s Messenger (sallallahu ‘alaihi wa sallam) ! Why have you left that person? By Allah I regard him as a faithful believer." The Prophet (sallallahu ‘alaihi wa sallam) commented: "Or merely a Muslim." I remained quiet for a while, but could not help repeating my question because of what I knew about him. And then asked Allah''s Messenger (sallallahu ‘alaihi wa sallam) , "Why have you left so and so? By Allah! He is a faithful believer." The Prophet (sallallahu ‘alaihi wa sallam) again said, "Or merely a Muslim." And I could not help repeating my question because of what I knew about him. Then the Prophet (sallallahu ‘alaihi wa sallam) said, "O Sa''d! I give to a person while another is dearer to me, for fear that he might be thrown on his face in the Fire by Allah."', NULL, 'Sahih al-Bukhari 27\r\nBook 2, Hadith 20\r\nVol. 1, Book 2, Hadith 27', 'en'),
(63, 32, NULL, 'حَدَّثَنَا قُتَيْبَةُ، قَالَ حَدَّثَنَا اللَّيْثُ، عَنْ يَزِيدَ بْنِ أَبِي حَبِيبٍ، عَنْ أَبِي الْخَيْرِ، عَنْ عَبْدِ اللَّهِ بْنِ عَمْرٍو، أَنَّ رَجُلاً، سَأَلَ رَسُولَ اللَّهِ صلى الله عليه وسلم أَىُّ الإِسْلاَمِ خَيْرٌ قَالَ ‏ "‏ تُطْعِمُ الطَّعَامَ، وَتَقْرَأُ السَّلاَمَ عَلَى مَنْ عَرَفْتَ وَمَنْ لَمْ تَعْرِفْ', NULL, NULL, 'ar'),
(64, 32, '''Abdullah bin ''Amr', 'A person asked Allah''s Messenger (sallallahu ‘alaihi wa sallam) . "What (sort of) deeds in or (what qualities of) Islam are good?" He replied, "To feed (the poor) and greet those whom you know and those whom you don''t know."', NULL, 'Sahih al-Bukhari 28\r\nBook 2, Hadith 21\r\nVol. 1, Book 2, Hadith 28', 'en'),
(65, 33, NULL, 'حَدَّثَنَا عَبْدُ اللَّهِ بْنُ مَسْلَمَةَ، عَنْ مَالِكٍ، عَنْ زَيْدِ بْنِ أَسْلَمَ، عَنْ عَطَاءِ بْنِ يَسَارٍ، عَنِ ابْنِ عَبَّاسٍ، قَالَ قَالَ النَّبِيُّ صلى الله عليه وسلم ‏"‏ أُرِيتُ النَّارَ فَإِذَا أَكْثَرُ أَهْلِهَا النِّسَاءُ يَكْفُرْنَ ‏"‏‏.‏ قِيلَ أَيَكْفُرْنَ بِاللَّهِ قَالَ ‏"‏ يَكْفُرْنَ الْعَشِيرَ، وَيَكْفُرْنَ الإِحْسَانَ، لَوْ أَحْسَنْتَ إِلَى إِحْدَاهُنَّ الدَّهْرَ ثُمَّ رَأَتْ مِنْكَ شَيْئًا قَالَتْ مَا رَأَيْتُ مِنْكَ خَيْرًا قَطُّ', NULL, NULL, 'ar'),
(66, 33, 'Ibn ''Abbas', 'The Prophet (sallallahu ‘alaihi wa sallam) said: "I was shown the Hell-fire and that the majority of its dwellers were women who were ungrateful." It was asked, "Do they disbelieve in Allah?" (or are they ungrateful to Allah?) He replied, "They are ungrateful to their husbands and are ungrateful for the favors and the good (charitable deeds) done to them. If you have always been good (benevolent) to one of them and then she sees something in you (not of her liking), she will say, ''I have never received any good from you."', NULL, 'Sahih al-Bukhari 29\r\nBook 2, Hadith 22\r\nVol. 1, Book 2, Hadith 29', 'en'),
(67, 34, NULL, 'حَدَّثَنَا سُلَيْمَانُ بْنُ حَرْبٍ، قَالَ حَدَّثَنَا شُعْبَةُ، عَنْ وَاصِلٍ الأَحْدَبِ، عَنِ الْمَعْرُورِ، قَالَ لَقِيتُ أَبَا ذَرٍّ بِالرَّبَذَةِ، وَعَلَيْهِ حُلَّةٌ، وَعَلَى غُلاَمِهِ حُلَّةٌ، فَسَأَلْتُهُ عَنْ ذَلِكَ، فَقَالَ إِنِّي سَابَبْتُ رَجُلاً، فَعَيَّرْتُهُ بِأُمِّهِ، فَقَالَ لِيَ النَّبِيُّ صلى الله عليه وسلم ‏ "‏ يَا أَبَا ذَرٍّ أَعَيَّرْتَهُ بِأُمِّهِ إِنَّكَ امْرُؤٌ فِيكَ جَاهِلِيَّةٌ، إِخْوَانُكُمْ خَوَلُكُمْ، جَعَلَهُمُ اللَّهُ تَحْتَ أَيْدِيكُمْ، فَمَنْ كَانَ أَخُوهُ تَحْتَ يَدِهِ فَلْيُطْعِمْهُ مِمَّا يَأْكُلُ، وَلْيُلْبِسْهُ مِمَّا يَلْبَسُ، وَلاَ تُكَلِّفُوهُمْ مَا يَغْلِبُهُمْ، فَإِنْ كَلَّفْتُمُوهُمْ فَأَعِينُوهُمْ', NULL, NULL, 'ar'),
(68, 34, 'Al-Ma''rur', 'At Ar-Rabadha I met Abu Dhar who was wearing a cloak, and his slave, too, was wearing a similar one. I asked about the reason for it. He replied, "I abused a person by calling his mother with bad names." The Prophet said to me, ''O Abu Dhar! Did you abuse him by calling his mother with bad names You still have some characteristics of ignorance. Your slaves are your brothers and Allah has put them under your command. So whoever has a brother under his command should feed him of what he eats and dress him of what he wears. Do not ask them (slaves) to do things beyond their capacity (power) and if you do so, then help them.'' "', NULL, 'Sahih al-Bukhari 30\r\nBook 2, Hadith 23\r\nVol. 1, Book 2, Hadith 30', 'en'),
(69, 35, NULL, 'حَدَّثَنَا عَبْدُ الرَّحْمَنِ بْنُ الْمُبَارَكِ، حَدَّثَنَا حَمَّادُ بْنُ زَيْدٍ، حَدَّثَنَا أَيُّوبُ، وَيُونُسُ، عَنِ الْحَسَنِ، عَنِ الأَحْنَفِ بْنِ قَيْسٍ، قَالَ ذَهَبْتُ لأَنْصُرَ هَذَا الرَّجُلَ، فَلَقِيَنِي أَبُو بَكْرَةَ فَقَالَ أَيْنَ تُرِيدُ قُلْتُ أَنْصُرُ هَذَا الرَّجُلَ‏.‏ قَالَ ارْجِعْ فَإِنِّي سَمِعْتُ رَسُولَ اللَّهِ صلى الله عليه وسلم يَقُولُ ‏"‏ إِذَا الْتَقَى الْمُسْلِمَانِ بِسَيْفَيْهِمَا فَالْقَاتِلُ وَالْمَقْتُولُ فِي النَّارِ ‏"‏‏.‏ فَقُلْتُ يَا رَسُولَ اللَّهِ هَذَا الْقَاتِلُ فَمَا بَالُ الْمَقْتُولِ قَالَ ‏"‏ إِنَّهُ كَانَ حَرِيصًا عَلَى قَتْلِ صَاحِبِهِ', NULL, NULL, 'ar'),
(70, 35, 'Al-Ahnaf bin Qais', 'While I was going to help this man (''Ali Ibn Abi Talib), Abu Bakra met me and asked, "Where are you going?" I replied, "I am going to help that person." He said, "Go back for I have heard Allah''s Messenger (sallallahu ‘alaihi wa sallam) saying, ''When two Muslims fight (meet) each other with their swords, both the murderer as well as the murdered will go to the Hell-fire.'' I said, ''O Allah''s Messenger (sallallahu ‘alaihi wa sallam) ! It is all right for the murderer but what about the murdered one?'' Allah''s Messenger (sallallahu ‘alaihi wa sallam) replied, "He surely had the intention to kill his companion."', NULL, 'Sahih al-Bukhari 31\r\nBook 2, Hadith 24\r\nVol. 1, Book 2, Hadith 31', 'en'),
(71, 36, NULL, 'حَدَّثَنَا أَبُو الْوَلِيدِ، قَالَ حَدَّثَنَا شُعْبَةُ، ح‏.‏ قَالَ وَحَدَّثَنِي بِشْرٌ، قَالَ حَدَّثَنَا مُحَمَّدٌ، عَنْ شُعْبَةَ، عَنْ سُلَيْمَانَ، عَنْ إِبْرَاهِيمَ، عَنْ عَلْقَمَةَ، عَنْ عَبْدِ اللَّهِ، قَالَ لَمَّا نَزَلَتِ ‏{‏الَّذِينَ آمَنُوا وَلَمْ يَلْبِسُوا إِيمَانَهُمْ بِظُلْمٍ‏}‏ قَالَ أَصْحَابُ رَسُولِ اللَّهِ صلى الله عليه وسلم أَيُّنَا لَمْ يَظْلِمْ فَأَنْزَلَ اللَّهُ ‏{‏إِنَّ الشِّرْكَ لَظُلْمٌ عَظِيمٌ‏}‏‏.‏', NULL, NULL, 'ar'),
(72, 36, '''Abdullah', 'When the following Verse was revealed: "It is those who believe and confuse not their belief with wrong (worshipping others besides Allah.)" (6:83), the companions of Allah''s Messenger (sallallahu ‘alaihi wa sallam) asked, "Who is amongst us who had not done injustice (wrong)?" Allah revealed: "No doubt, joining others in worship with Allah is a great injustice (wrong) indeed." (31.13)', NULL, 'Sahih al-Bukhari 32\r\nBook 2, Hadith 25\r\nVol. 1, Book 2, Hadith 32', 'en'),
(73, 37, NULL, 'حَدَّثَنَا سُلَيْمَانُ أَبُو الرَّبِيعِ، قَالَ حَدَّثَنَا إِسْمَاعِيلُ بْنُ جَعْفَرٍ، قَالَ حَدَّثَنَا نَافِعُ بْنُ مَالِكِ بْنِ أَبِي عَامِرٍ أَبُو سُهَيْلٍ، عَنْ أَبِيهِ، عَنْ أَبِي هُرَيْرَةَ، عَنِ النَّبِيِّ صلى الله عليه وسلم قَالَ ‏ "‏ آيَةُ الْمُنَافِقِ ثَلاَثٌ إِذَا حَدَّثَ كَذَبَ، وَإِذَا وَعَدَ أَخْلَفَ، وَإِذَا اؤْتُمِنَ خَانَ', NULL, NULL, 'ar'),
(74, 37, 'Abu Huraira', 'The Prophet (sallallahu ‘alaihi wa sallam) said, "The signs of a hypocrite are three:\r\n1. Whenever he speaks, he tells a lie.\r\n2. Whenever he promises, he always breaks it (his promise ).\r\n3. If you trust him, he proves to be dishonest. (If you keep something as a trust with him, he will not return it.)"', NULL, 'Sahih al-Bukhari 33\r\nBook 2, Hadith 26\r\nVol. 1, Book 2, Hadith 33', 'en'),
(75, 38, NULL, 'حَدَّثَنَا قَبِيصَةُ بْنُ عُقْبَةَ، قَالَ حَدَّثَنَا سُفْيَانُ، عَنِ الأَعْمَشِ، عَنْ عَبْدِ اللَّهِ بْنِ مُرَّةَ، عَنْ مَسْرُوقٍ، عَنْ عَبْدِ اللَّهِ بْنِ عَمْرٍو، أَنَّ النَّبِيَّ صلى الله عليه وسلم قَالَ ‏ "‏ أَرْبَعٌ مَنْ كُنَّ فِيهِ كَانَ مُنَافِقًا خَالِصًا، وَمَنْ كَانَتْ فِيهِ خَصْلَةٌ مِنْهُنَّ كَانَتْ فِيهِ خَصْلَةٌ مِنَ النِّفَاقِ حَتَّى يَدَعَهَا إِذَا اؤْتُمِنَ خَانَ وَإِذَا حَدَّثَ كَذَبَ وَإِذَا عَاهَدَ غَدَرَ، وَإِذَا خَاصَمَ فَجَرَ ‏"‏‏.‏ تَابَعَهُ شُعْبَةُ عَنِ الأَعْمَشِ‏.', NULL, NULL, 'ar'),
(76, 38, '''Abdullah bin ''Amr', 'The Prophet (sallallahu ‘alaihi wa sallam) said, "Whoever has the following four (characteristics) will be a pure hypocrite and whoever has one of the following four characteristics will have one characteristic of hypocrisy unless and until he gives it up.\r\n1. Whenever he is entrusted, he betrays.\r\n2. Whenever he speaks, he tells a lie.\r\n3. Whenever he makes a covenant, he proves treacherous.\r\n4. Whenever he quarrels, he behaves in a very imprudent, evil and insulting manner."', NULL, 'Sahih al-Bukhari 34\r\nBook 2, Hadith 27\r\nVol. 1, Book 2, Hadith 34', 'en'),
(77, 39, NULL, 'حَدَّثَنَا أَبُو الْيَمَانِ، قَالَ أَخْبَرَنَا شُعَيْبٌ، قَالَ حَدَّثَنَا أَبُو الزِّنَادِ، عَنِ الأَعْرَجِ، عَنْ أَبِي هُرَيْرَةَ، قَالَ قَالَ رَسُولُ اللَّهِ صلى الله عليه وسلم ‏ "‏ مَنْ يَقُمْ لَيْلَةَ الْقَدْرِ إِيمَانًا وَاحْتِسَابًا غُفِرَ لَهُ مَا تَقَدَّمَ مِنْ ذَنْبِهِ ‏"‏‏.‏', NULL, NULL, 'ar'),
(78, 39, 'Abu Huraira', 'Allah''s Messenger (sallallahu ‘alaihi wa sallam) said, "Whoever establishes the prayers on the night of Qadr out of sincere faith and hoping to attain Allah''s rewards (not to show off) then all his past sins will be forgiven."', NULL, 'Sahih al-Bukhari 35\r\nBook 2, Hadith 28\r\nVol. 1, Book 2, Hadith 35', 'en'),
(79, 40, NULL, 'حَدَّثَنَا حَرَمِيُّ بْنُ حَفْصٍ، قَالَ حَدَّثَنَا عَبْدُ الْوَاحِدِ، قَالَ حَدَّثَنَا عُمَارَةُ، قَالَ حَدَّثَنَا أَبُو زُرْعَةَ بْنُ عَمْرِو بْنِ جَرِيرٍ، قَالَ سَمِعْتُ أَبَا هُرَيْرَةَ، عَنِ النَّبِيِّ صلى الله عليه وسلم قَالَ ‏ "‏ انْتَدَبَ اللَّهُ لِمَنْ خَرَجَ فِي سَبِيلِهِ لاَ يُخْرِجُهُ إِلاَّ إِيمَانٌ بِي وَتَصْدِيقٌ بِرُسُلِي أَنْ أُرْجِعَهُ بِمَا نَالَ مِنْ أَجْرٍ أَوْ غَنِيمَةٍ، أَوْ أُدْخِلَهُ الْجَنَّةَ، وَلَوْلاَ أَنْ أَشُقَّ عَلَى أُمَّتِي مَا قَعَدْتُ خَلْفَ سَرِيَّةٍ، وَلَوَدِدْتُ أَنِّي أُقْتَلُ فِي سَبِيلِ اللَّهِ ثُمَّ أُحْيَا، ثُمَّ أُقْتَلُ ثُمَّ أُحْيَا، ثُمَّ أُقْتَلُ', NULL, NULL, 'ar'),
(80, 40, 'Abu Huraira', 'The Prophet (sallallahu ‘alaihi wa sallam) said, "The person who participates in (Holy battles) in Allah''s cause and nothing compels him to do so except belief in Allah and His Apostles, will be recompensed by Allah either with a reward, or booty (if he survives) or will be admitted to Paradise (if he is killed in the battle as a martyr). Had I not found it difficult for my followers, then I would not remain behind any sariya going for Jihad and I would have loved to be martyred in Allah''s cause and then made alive, and then martyred and then made alive, and then again martyred in His cause."', NULL, 'Sahih al-Bukhari 36\r\nBook 2, Hadith 29\r\nVol. 1, Book 2, Hadith 36', 'en'),
(81, 41, NULL, 'حَدَّثَنَا إِسْمَاعِيلُ، قَالَ حَدَّثَنِي مَالِكٌ، عَنِ ابْنِ شِهَابٍ، عَنْ حُمَيْدِ بْنِ عَبْدِ الرَّحْمَنِ، عَنْ أَبِي هُرَيْرَةَ، أَنَّ رَسُولَ اللَّهِ صلى الله عليه وسلم قَالَ ‏ "‏ مَنْ قَامَ رَمَضَانَ إِيمَانًا وَاحْتِسَابًا غُفِرَ لَهُ مَا تَقَدَّمَ مِنْ ذَنْبِهِ ‏"‏‏', NULL, NULL, 'ar'),
(82, 41, 'Abu Huraira', 'Allah''s Messenger (sallallahu ‘alaihi wa sallam) said: "Whoever establishes prayers during the nights of Ramadan faithfully out of sincere faith and hoping to attain Allah''s rewards (not for showing off), all his past sins will be forgiven."', NULL, 'Sahih al-Bukhari 37\r\nBook 2, Hadith 30\r\nVol. 1, Book 2, Hadith 37', 'en'),
(83, 42, NULL, 'حَدَّثَنَا ابْنُ سَلاَمٍ، قَالَ أَخْبَرَنَا مُحَمَّدُ بْنُ فُضَيْلٍ، قَالَ حَدَّثَنَا يَحْيَى بْنُ سَعِيدٍ، عَنْ أَبِي سَلَمَةَ، عَنْ أَبِي هُرَيْرَةَ، قَالَ قَالَ رَسُولُ اللَّهِ صلى الله عليه وسلم ‏ "‏ مَنْ صَامَ رَمَضَانَ إِيمَانًا وَاحْتِسَابًا غُفِرَ لَهُ مَا تَقَدَّمَ مِنْ ذَنْبِهِ', NULL, NULL, 'ar'),
(84, 42, 'Abu Huraira', 'Allah''s Messenger (sallallahu ‘alaihi wa sallam) said, "Whoever observes fasts during the month of Ramadan out of sincere faith, and hoping to attain Allah''s rewards, then all his past sins will be forgiven."', NULL, 'Sahih al-Bukhari 38\r\nBook 2, Hadith 31\r\nVol. 1, Book 2, Hadith 38', 'en'),
(85, 43, NULL, 'حَدَّثَنَا عَبْدُ السَّلاَمِ بْنُ مُطَهَّرٍ، قَالَ حَدَّثَنَا عُمَرُ بْنُ عَلِيٍّ، عَنْ مَعْنِ بْنِ مُحَمَّدٍ الْغِفَارِيِّ، عَنْ سَعِيدِ بْنِ أَبِي سَعِيدٍ الْمَقْبُرِيِّ، عَنْ أَبِي هُرَيْرَةَ، عَنِ النَّبِيِّ صلى الله عليه وسلم قَالَ ‏ "‏ إِنَّ الدِّينَ يُسْرٌ، وَلَنْ يُشَادَّ الدِّينَ أَحَدٌ إِلاَّ غَلَبَهُ، فَسَدِّدُوا وَقَارِبُوا وَأَبْشِرُوا، وَاسْتَعِينُوا بِالْغَدْوَةِ وَالرَّوْحَةِ وَشَىْءٍ مِنَ الدُّلْجَةِ', NULL, NULL, 'ar'),
(86, 43, 'Abu Huraira', 'The Prophet (sallallahu ‘alaihi wa sallam) said, "Religion is very easy and whoever overburdens himself in his religion will not be able to continue in that way. So you should not be extremists, but try to be near to perfection and receive the good tidings that you will be rewarded; and gain strength by worshipping in the mornings, the nights." (See Fath-ul-Bari, Page 102, Vol 1).', NULL, 'Sahih al-Bukhari 39\r\nBook 2, Hadith 32\r\nVol. 1, Book 2, Hadith 39', 'en'),
(87, 44, NULL, 'حَدَّثَنَا عَمْرُو بْنُ خَالِدٍ، قَالَ حَدَّثَنَا زُهَيْرٌ، قَالَ حَدَّثَنَا أَبُو إِسْحَاقَ، عَنِ الْبَرَاءِ، أَنَّ النَّبِيَّ صلى الله عليه وسلم كَانَ أَوَّلَ مَا قَدِمَ الْمَدِينَةَ نَزَلَ عَلَى أَجْدَادِهِ ـ أَوْ قَالَ أَخْوَالِهِ ـ مِنَ الأَنْصَارِ، وَأَنَّهُ صَلَّى قِبَلَ بَيْتِ الْمَقْدِسِ سِتَّةَ عَشَرَ شَهْرًا، أَوْ سَبْعَةَ عَشَرَ شَهْرًا، وَكَانَ يُعْجِبُهُ أَنْ تَكُونَ قِبْلَتُهُ قِبَلَ الْبَيْتِ، وَأَنَّهُ صَلَّى أَوَّلَ صَلاَةٍ صَلاَّهَا صَلاَةَ الْعَصْرِ، وَصَلَّى مَعَهُ قَوْمٌ، فَخَرَجَ رَجُلٌ مِمَّنْ صَلَّى مَعَهُ، فَمَرَّ عَلَى أَهْلِ مَسْجِدٍ، وَهُمْ رَاكِعُونَ فَقَالَ أَشْهَدُ بِاللَّهِ لَقَدْ صَلَّيْتُ مَعَ رَسُولِ اللَّهِ صلى الله عليه وسلم قِبَلَ مَكَّةَ، فَدَارُوا كَمَا هُمْ قِبَلَ الْبَيْتِ، وَكَانَتِ الْيَهُودُ قَدْ أَعْجَبَهُمْ إِذْ كَانَ يُصَلِّي قِبَلَ بَيْتِ الْمَقْدِسِ، وَأَهْلُ الْكِتَابِ، فَلَمَّا وَلَّى وَجْهَهُ قِبَلَ الْبَيْتِ أَنْكَرُوا ذَلِكَ‏.‏ قَالَ زُهَيْرٌ حَدَّثَنَا أَبُو إِسْحَاقَ عَنِ الْبَرَاءِ فِي حَدِيثِهِ هَذَا أَنَّهُ مَاتَ عَلَى الْقِبْلَةِ قَبْلَ أَنْ تُحَوَّلَ رِجَالٌ وَقُتِلُوا، فَلَمْ نَدْرِ مَا نَقُولُ فِيهِمْ، فَأَنْزَلَ اللَّهُ تَعَالَى ‏{‏وَمَا كَانَ اللَّهُ لِيُضِيعَ إِيمَانَكُمْ‏}‏', NULL, NULL, 'ar'),
(88, 44, 'Al-Bara'' (bin ''Azib)', 'When the Prophet (sallallahu ‘alaihi wa sallam) came to Medina, he stayed first with his grandfathers or maternal uncles from Ansar. He offered his prayers facing Baitul-Maqdis (Jerusalem) for sixteen or seventeen months, but he wished that he could pray facing the Ka''ba (at Mecca). The first prayer which he offered facing the Ka''ba was the ''Asr prayer in the company of some people. Then one of those who had offered that prayer with him came out and passed by some people in a mosque who were bowing during their prayers (facing Jerusalem). He said addressing them, "By Allah, I testify that I have prayed with Allah''s Messenger (sallallahu ‘alaihi wa sallam) facing Mecca (Ka''ba).'' Hearing that, those people changed their direction towards the Ka''ba immediately. Jews and the people of the scriptures used to be pleased to see the Prophet (sallallahu ‘alaihi wa sallam) facing Jerusalem in prayers but when he changed his direction towards the Ka''ba, during the prayers, they disapproved of it.\r\nAl-Bara'' added, "Before we changed our direction towards the Ka''ba (Mecca) in prayers, some Muslims had died or had been killed and we did not know what to say about them (regarding their prayers.) Allah then revealed: And Allah would never make your faith (prayers) to be lost (i.e. the prayers of those Muslims were valid).'' " (2:143).', NULL, 'Sahih al-Bukhari 40\r\nBook 2, Hadith 33\r\nVol. 1, Book 2, Hadith 40', 'en'),
(89, 45, NULL, 'قَالَ مَالِكٌ أَخْبَرَنِي زَيْدُ بْنُ أَسْلَمَ، أَنَّ عَطَاءَ بْنَ يَسَارٍ، أَخْبَرَهُ أَنَّ أَبَا سَعِيدٍ الْخُدْرِيَّ أَخْبَرَهُ أَنَّهُ، سَمِعَ رَسُولَ اللَّهِ صلى الله عليه وسلم يَقُولُ ‏ "‏ إِذَا أَسْلَمَ الْعَبْدُ فَحَسُنَ إِسْلاَمُهُ يُكَفِّرُ اللَّهُ عَنْهُ كُلَّ سَيِّئَةٍ كَانَ زَلَفَهَا، وَكَانَ بَعْدَ ذَلِكَ الْقِصَاصُ، الْحَسَنَةُ بِعَشْرِ أَمْثَالِهَا إِلَى سَبْعِمِائَةِ ضِعْفٍ، وَالسَّيِّئَةُ بِمِثْلِهَا إِلاَّ أَنْ يَتَجَاوَزَ اللَّهُ عَنْهَا', NULL, NULL, 'ar'),
(90, 45, 'Abu Sa''id Al Khudri', 'Allah''s Messenger (sallallahu ‘alaihi wa sallam) said, "If a person embraces Islam sincerely, then Allah shall forgive all his past sins, and after that starts the settlement of accounts, the reward of his good deeds will be ten times to seven hundred times for each good deed and one evil deed will be recorded as it is unless Allah forgives it."', NULL, 'Sahih al-Bukhari 41\r\nBook 2, Hadith 34\r\nVol. 1, Book 2, Hadith 41', 'en'),
(91, 46, NULL, 'حَدَّثَنَا إِسْحَاقُ بْنُ مَنْصُورٍ، قَالَ حَدَّثَنَا عَبْدُ الرَّزَّاقِ، قَالَ أَخْبَرَنَا مَعْمَرٌ، عَنْ هَمَّامٍ، عَنْ أَبِي هُرَيْرَةَ، قَالَ قَالَ رَسُولُ اللَّهِ صلى الله عليه وسلم ‏ "‏ إِذَا أَحْسَنَ أَحَدُكُمْ إِسْلاَمَهُ، فَكُلُّ حَسَنَةٍ يَعْمَلُهَا تُكْتَبُ لَهُ بِعَشْرِ أَمْثَالِهَا إِلَى سَبْعِمِائَةِ ضِعْفٍ، وَكُلُّ سَيِّئَةٍ يَعْمَلُهَا تُكْتَبُ لَهُ بِمِثْلِهَا', NULL, NULL, 'ar'),
(92, 46, 'Abu Huraira', 'Allah''s Messenger (sallallahu ‘alaihi wa sallam) said, "If any one of you improve (follows strictly) his Islamic religion then his good deeds will be rewarded ten times to seven hundred times for each good deed and a bad deed will be recorded as it is."', NULL, 'Sahih al-Bukhari 42\r\nBook 2, Hadith 35\r\nVol. 1, Book 2, Hadith 41', 'en'),
(93, 47, NULL, 'حَدَّثَنَا مُحَمَّدُ بْنُ الْمُثَنَّى، حَدَّثَنَا يَحْيَى، عَنْ هِشَامٍ، قَالَ أَخْبَرَنِي أَبِي، عَنْ عَائِشَةَ، أَنَّ النَّبِيَّ صلى الله عليه وسلم دَخَلَ عَلَيْهَا وَعِنْدَهَا امْرَأَةٌ قَالَ ‏"‏ مَنْ هَذِهِ ‏"‏‏.‏ قَالَتْ فُلاَنَةُ‏.‏ تَذْكُرُ مِنْ صَلاَتِهَا‏.‏ قَالَ ‏"‏ مَهْ، عَلَيْكُمْ بِمَا تُطِيقُونَ، فَوَاللَّهِ لاَ يَمَلُّ اللَّهُ حَتَّى تَمَلُّوا ‏"‏‏.‏ وَكَانَ أَحَبَّ الدِّينِ إِلَيْهِ مَا دَامَ عَلَيْهِ صَاحِبُهُ', NULL, NULL, 'ar'),
(94, 47, '''Aisha', 'Once the Prophet (sallallahu ‘alaihi wa sallam) came while a woman was sitting with me. He said, "Who is she?" I replied, "She is so and so," and told him about her (excessive) praying. He said disapprovingly, "Do (good) deeds which is within your capacity (without being overtaxed) as Allah does not get tired (of giving rewards) but (surely) you will get tired and the best deed (act of Worship) in the sight of Allah is that which is done regularly."', NULL, 'Sahih al-Bukhari 43\r\nBook 2, Hadith 36\r\nVol. 1, Book 2, Hadith 42', 'en'),
(95, 48, NULL, 'حَدَّثَنَا الْحَسَنُ بْنُ الصَّبَّاحِ، سَمِعَ جَعْفَرَ بْنَ عَوْنٍ، حَدَّثَنَا أَبُو الْعُمَيْسِ، أَخْبَرَنَا قَيْسُ بْنُ مُسْلِمٍ، عَنْ طَارِقِ بْنِ شِهَابٍ، عَنْ عُمَرَ بْنِ الْخَطَّابِ، أَنَّ رَجُلاً، مِنَ الْيَهُودِ قَالَ لَهُ يَا أَمِيرَ الْمُؤْمِنِينَ، آيَةٌ فِي كِتَابِكُمْ تَقْرَءُونَهَا لَوْ عَلَيْنَا مَعْشَرَ الْيَهُودِ نَزَلَتْ لاَتَّخَذْنَا ذَلِكَ الْيَوْمَ عِيدًا‏.‏ قَالَ أَىُّ آيَةٍ قَالَ ‏{‏الْيَوْمَ أَكْمَلْتُ لَكُمْ دِينَكُمْ وَأَتْمَمْتُ عَلَيْكُمْ نِعْمَتِي وَرَضِيتُ لَكُمُ الإِسْلاَمَ دِينًا‏}‏‏.‏ قَالَ عُمَرُ قَدْ عَرَفْنَا ذَلِكَ الْيَوْمَ وَالْمَكَانَ الَّذِي نَزَلَتْ فِيهِ عَلَى النَّبِيِّ صلى الله عليه وسلم وَهُوَ قَائِمٌ بِعَرَفَةَ يَوْمَ جُمُعَةٍ‏.‏', NULL, NULL, 'ar'),
(96, 48, '''Umar bin Al-Khattab', 'Once a Jew said to me, "O the chief of believers! There is a verse in your Holy Book Which is read by all of you (Muslims), and had it been revealed to us, we would have taken that day (on which it was revealed as a day of celebration." ''Umar bin Al-Khattab asked, "Which is that verse?" The Jew replied, "This day I have perfected your religion For you, completed My favor upon you, And have chosen for you Islam as your religion." (5:3) ''Umar replied,"No doubt, we know when and where this verse was revealed to the Prophet (sallallahu ‘alaihi wa sallam) . It was Friday and the Prophet (sallallahu ‘alaihi wa sallam) was standing at ''Arafat (i.e. the Day of Hajj)"', NULL, 'Sahih al-Bukhari 45\r\nBook 2, Hadith 38\r\nVol. 1, Book 2, Hadith 44', 'en'),
(97, 49, NULL, 'حَدَّثَنَا مُسْلِمُ بْنُ إِبْرَاهِيمَ، قَالَ حَدَّثَنَا هِشَامٌ، قَالَ حَدَّثَنَا قَتَادَةُ، عَنْ أَنَسٍ، عَنِ النَّبِيِّ صلى الله عليه وسلم قَالَ ‏"‏ يَخْرُجُ مِنَ النَّارِ مَنْ قَالَ لاَ إِلَهَ إِلاَّ اللَّهُ، وَفِي قَلْبِهِ وَزْنُ شَعِيرَةٍ مِنْ خَيْرٍ، وَيَخْرُجُ مِنَ النَّارِ مَنْ قَالَ لاَ إِلَهَ إِلاَّ اللَّهُ، وَفِي قَلْبِهِ وَزْنُ بُرَّةٍ مِنْ خَيْرٍ، وَيَخْرُجُ مِنَ النَّارِ مَنْ قَالَ لاَ إِلَهَ إِلاَّ اللَّهُ، وَفِي قَلْبِهِ وَزْنُ ذَرَّةٍ مِنْ خَيْرٍ ‏"‏‏.‏ قَالَ أَبُو عَبْدِ اللَّهِ قَالَ أَبَانُ حَدَّثَنَا قَتَادَةُ حَدَّثَنَا أَنَسٌ عَنِ النَّبِيِّ صلى الله عليه وسلم ‏"‏ مِنْ إِيمَانٍ ‏"‏‏.‏ مَكَانَ ‏"‏ مِنْ خَيْرٍ', NULL, NULL, 'ar'),
(98, 49, 'Anas', 'The Prophet (sallallahu ‘alaihi wa sallam) said, "Whoever said "None has the right to be worshipped but Allah and has in his heart good (faith) equal to the weight of a barley grain will be taken out of Hell. And whoever said: "None has the right to be worshipped but Allah and has in his heart good (faith) equal to the weight of a wheat grain will be taken out of Hell. And whoever said, "None has the right to be worshipped but Allah and has in his heart good (faith) equal to the weight of an atom will be taken out of Hell."', NULL, 'Sahih al-Bukhari 44\r\nBook 2, Hadith 37\r\nVol. 1, Book 2, Hadith 43', 'en');
INSERT INTO `hadith_translation` (`id`, `translatable_id`, `narrator`, `body`, `note`, `reference`, `locale`) VALUES
(99, 50, NULL, 'حَدَّثَنَا إِسْمَاعِيلُ، قَالَ حَدَّثَنِي مَالِكُ بْنُ أَنَسٍ، عَنْ عَمِّهِ أَبِي سُهَيْلِ بْنِ مَالِكٍ، عَنْ أَبِيهِ، أَنَّهُ سَمِعَ طَلْحَةَ بْنَ عُبَيْدِ اللَّهِ، يَقُولُ جَاءَ رَجُلٌ إِلَى رَسُولِ اللَّهِ صلى الله عليه وسلم مِنْ أَهْلِ نَجْدٍ، ثَائِرُ الرَّأْسِ، يُسْمَعُ دَوِيُّ صَوْتِهِ، وَلاَ يُفْقَهُ مَا يَقُولُ حَتَّى دَنَا، فَإِذَا هُوَ يَسْأَلُ عَنِ الإِسْلاَمِ فَقَالَ رَسُولُ اللَّهِ ـ صلى الله عليه وسلم ‏"‏ خَمْسُ صَلَوَاتٍ فِي الْيَوْمِ وَاللَّيْلَةِ ‏"‏‏.‏ فَقَالَ هَلْ عَلَىَّ غَيْرُهَا قَالَ ‏"‏ لاَ، إِلاَّ أَنْ تَطَوَّعَ ‏"‏‏.‏ قَالَ رَسُولُ اللَّهِ صلى الله عليه وسلم ‏"‏ وَصِيَامُ رَمَضَانَ ‏"‏‏.‏ قَالَ هَلْ عَلَىَّ غَيْرُهُ قَالَ ‏"‏ لاَ، إِلاَّ أَنْ تَطَوَّعَ ‏"‏‏.‏ قَالَ وَذَكَرَ لَهُ رَسُولُ اللَّهِ صلى الله عليه وسلم الزَّكَاةَ‏.‏ قَالَ هَلْ عَلَىَّ غَيْرُهَا قَالَ ‏"‏ لاَ، إِلاَّ أَنْ تَطَوَّعَ ‏"‏‏.‏ قَالَ فَأَدْبَرَ الرَّجُلُ وَهُوَ يَقُولُ وَاللَّهِ لاَ أَزِيدُ عَلَى هَذَا وَلاَ أَنْقُصُ‏.‏ قَالَ رَسُولُ اللَّهِ صلى الله عليه وسلم ‏"‏ أَفْلَحَ إِنْ صَدَقَ', NULL, NULL, 'ar'),
(100, 50, 'Talha bin ''Ubaidullah', 'A man from Najd with unkempt hair came to Allah''s Messenger (sallallahu ‘alaihi wa sallam) and we heard his loud voice but could not understand what he was saying, till he came near and then we came to know that he was asking about Islam. Allah''s Messenger (sallallahu ‘alaihi wa sallam) said, "You have to offer prayers perfectly five times in a day and night (24 hours)." The man asked, "Is there any more (praying)?" Allah''s Messenger (sallallahu ‘alaihi wa sallam) replied, "No, but if you want to offer the Nawafil prayers (you can)." Allah''s Messenger (sallallahu ‘alaihi wa sallam) further said to him: "You have to observe fasts during the month of Ramadan." The man asked, "Is there any more fasting?" Allah''s Messenger (sallallahu ‘alaihi wa sallam) replied, "No, but if you want to observe the Nawafil fasts (you can.)" Then Allah''s Messenger (sallallahu ‘alaihi wa sallam) further said to him, "You have to pay the Zakat (obligatory charity)." The man asked, "Is there any thing other than the Zakat for me to pay?" Allah''s Messenger (sallallahu ‘alaihi wa sallam) replied, "No, unless you want to give alms of your own." And then that man retreated saying, "By Allah! I will neither do less nor more than this." Allah''s Messenger (sallallahu ‘alaihi wa sallam) said, "If what he said is true, then he will be successful (i.e. he will be granted Paradise)."', NULL, 'Sahih al-Bukhari 46\r\nBook 2, Hadith 39\r\nVol. 1, Book 2, Hadith 45', 'en'),
(101, 51, NULL, 'حَدَّثَنَا أَحْمَدُ بْنُ عَبْدِ اللَّهِ بْنِ عَلِيٍّ الْمَنْجُوفِيُّ، قَالَ حَدَّثَنَا رَوْحٌ، قَالَ حَدَّثَنَا عَوْفٌ، عَنِ الْحَسَنِ، وَمُحَمَّدٍ، عَنْ أَبِي هُرَيْرَةَ، أَنَّ رَسُولَ اللَّهِ صلى الله عليه وسلم قَالَ ‏ "‏ مَنِ اتَّبَعَ جَنَازَةَ مُسْلِمٍ إِيمَانًا وَاحْتِسَابًا، وَكَانَ مَعَهُ حَتَّى يُصَلَّى عَلَيْهَا، وَيَفْرُغَ مِنْ دَفْنِهَا، فَإِنَّهُ يَرْجِعُ مِنَ الأَجْرِ بِقِيرَاطَيْنِ، كُلُّ قِيرَاطٍ مِثْلُ أُحُدٍ، وَمَنْ صَلَّى عَلَيْهَا ثُمَّ رَجَعَ قَبْلَ أَنْ تُدْفَنَ فَإِنَّهُ يَرْجِعُ بِقِيرَاطٍ ‏"‏‏.‏ تَابَعَهُ عُثْمَانُ الْمُؤَذِّنُ قَالَ حَدَّثَنَا عَوْفٌ عَنْ مُحَمَّدٍ عَنْ أَبِي هُرَيْرَةَ عَنِ النَّبِيِّ صلى الله عليه وسلم نَحْوَهُ‏.‏', NULL, NULL, 'ar'),
(102, 51, 'Abu Huraira', 'Allah''s Messenger (sallallahu ‘alaihi wa sallam) said, "(A believer) who accompanies the funeral procession of a Muslim out of sincere faith and hoping to attain Allah''s reward and remains with it till the funeral prayer is offered and the burial ceremonies are over, he will return with a reward of two Qirats. Each Qirat is like the size of the (Mount) Uhud. He who offers the funeral prayer only and returns before the burial, will return with the reward of one Qirat only."', NULL, 'Sahih al-Bukhari 47\r\nBook 2, Hadith 40\r\nVol. 1, Book 2, Hadith 46', 'en'),
(103, 52, NULL, 'حَدَّثَنَا مُحَمَّدُ بْنُ عَرْعَرَةَ، قَالَ حَدَّثَنَا شُعْبَةُ، عَنْ زُبَيْدٍ، قَالَ سَأَلْتُ أَبَا وَائِلٍ عَنِ الْمُرْجِئَةِ،، فَقَالَ حَدَّثَنِي عَبْدُ اللَّهِ، أَنَّ النَّبِيَّ صلى الله عليه وسلم قَالَ ‏ "‏ سِ باب الْمُسْلِمِ فُسُوقٌ، وَقِتَالُهُ كُفْرٌ', NULL, NULL, 'ar'),
(104, 52, '''Abdullah', 'The Prophet (sallallahu ‘alaihi wa sallam) said, "Abusing a Muslim is Fusuq (an evil doing) and killing him is Kufr (disbelief)."', NULL, 'Sahih al-Bukhari 48\r\nBook 2, Hadith 41\r\nVol. 1, Book 2, Hadith 47', 'en'),
(105, 53, NULL, 'أَخْبَرَنَا قُتَيْبَةُ بْنُ سَعِيدٍ، حَدَّثَنَا إِسْمَاعِيلُ بْنُ جَعْفَرٍ، عَنْ حُمَيْدٍ، عَنْ أَنَسٍ، قَالَ أَخْبَرَنِي عُبَادَةُ بْنُ الصَّامِتِ، أَنَّ رَسُولَ اللَّهِ صلى الله عليه وسلم خَرَجَ يُخْبِرُ بِلَيْلَةِ الْقَدْرِ، فَتَلاَحَى رَجُلاَنِ مِنَ الْمُسْلِمِينَ فَقَالَ ‏ "‏ إِنِّي خَرَجْتُ لأُخْبِرَكُمْ بِلَيْلَةِ الْقَدْرِ، وَإِنَّهُ تَلاَحَى فُلاَنٌ وَفُلاَنٌ فَرُفِعَتْ وَعَسَى أَنْ يَكُونَ خَيْرًا لَكُمُ الْتَمِسُوهَا فِي السَّبْعِ وَالتِّسْعِ وَالْخَمْسِ', NULL, NULL, 'ar'),
(106, 53, '''Ubada bin As-Samit', '"Allah''s Messenger (sallallahu ‘alaihi wa sallam) went out to inform the people about the (date of the) night of decree (Al-Qadr) but there happened a quarrel between two Muslim men. The Prophet (sallallahu ‘alaihi wa sallam) said, "I came out to inform you about (the date of) the night of Al-Qadr, but as so and so and so and so quarrelled, its knowledge was taken away (I forgot it) and maybe it was better for you. Now look for it in the 7th, the 9th and the 5th (of the last 10 nights of the month of Ramadan)."', NULL, 'Sahih al-Bukhari 49\r\nBook 2, Hadith 42\r\nVol. 1, Book 2, Hadith 47', 'en'),
(107, 54, NULL, 'حَدَّثَنَا مُسَدَّدٌ، قَالَ حَدَّثَنَا إِسْمَاعِيلُ بْنُ إِبْرَاهِيمَ، أَخْبَرَنَا أَبُو حَيَّانَ التَّيْمِيُّ، عَنْ أَبِي زُرْعَةَ، عَنْ أَبِي هُرَيْرَةَ، قَالَ كَانَ النَّبِيُّ صلى الله عليه وسلم بَارِزًا يَوْمًا لِلنَّاسِ، فَأَتَاهُ جِبْرِيلُ فَقَالَ مَا الإِيمَانُ قَالَ ‏"‏ الإِيمَانُ أَنْ تُؤْمِنَ بِاللَّهِ وَمَلاَئِكَتِهِ وَبِلِقَائِهِ وَرُسُلِهِ، وَتُؤْمِنَ بِالْبَعْثِ ‏"‏‏.‏ قَالَ مَا الإِسْلاَمُ قَالَ ‏"‏ الإِسْلاَمُ أَنْ تَعْبُدَ اللَّهَ وَلاَ تُشْرِكَ بِهِ، وَتُقِيمَ الصَّلاَةَ، وَتُؤَدِّيَ الزَّكَاةَ الْمَفْرُوضَةَ، وَتَصُومَ رَمَضَانَ ‏"‏‏.‏ قَالَ مَا الإِحْسَانُ قَالَ ‏"‏ أَنْ تَعْبُدَ اللَّهَ كَأَنَّكَ تَرَاهُ، فَإِنْ لَمْ تَكُنْ تَرَاهُ فَإِنَّهُ يَرَاكَ ‏"‏‏.‏ قَالَ مَتَى السَّاعَةُ قَالَ ‏"‏ مَا الْمَسْئُولُ عَنْهَا بِأَعْلَمَ مِنَ السَّائِلِ، وَسَأُخْبِرُكَ عَنْ أَشْرَاطِهَا إِذَا وَلَدَتِ الأَمَةُ رَبَّهَا، وَإِذَا تَطَاوَلَ رُعَاةُ الإِبِلِ الْبُهْمُ فِي الْبُنْيَانِ، فِي خَمْسٍ لاَ يَعْلَمُهُنَّ إِلاَّ اللَّهُ ‏"‏‏.‏ ثُمَّ تَلاَ النَّبِيُّ صلى الله عليه وسلم ‏{‏إِنَّ اللَّهَ عِنْدَهُ عِلْمُ السَّاعَةِ‏}‏ الآيَةَ‏.‏ ثُمَّ أَدْبَرَ فَقَالَ ‏"‏ رُدُّوهُ ‏"‏‏.‏ فَلَمْ يَرَوْا شَيْئًا‏.‏ فَقَالَ ‏"‏ هَذَا جِبْرِيلُ جَاءَ يُعَلِّمُ النَّاسَ دِينَهُمْ ‏"‏‏.‏ قَالَ أَبُو عَبْدِ اللَّهِ جَعَلَ ذَلِكَ كُلَّهُ مِنَ الإِيمَانِ‏', NULL, NULL, 'ar'),
(108, 54, 'Abu Huraira', 'One day while the Prophet (sallallahu ‘alaihi wa sallam) was sitting in the company of some people, (The angel) Gabriel came and asked, "What is faith?" Allah''s Messenger (sallallahu ‘alaihi wa sallam) replied, ''Faith is to believe in Allah, His angels, (the) meeting with Him, His Apostles, and to believe in Resurrection." Then he further asked, "What is Islam?" Allah''s Messenger (sallallahu ‘alaihi wa sallam) replied, "To worship Allah Alone and none else, to offer prayers perfectly to pay the compulsory charity (Zakat) and to observe fasts during the month of Ramadan." Then he further asked, "What is Ihsan (perfection)?" Allah''s Messenger (sallallahu ‘alaihi wa sallam) replied, "To worship Allah as if you see Him, and if you cannot achieve this state of devotion then you must consider that He is looking at you." Then he further asked, "When will the Hour be established?" Allah''s Messenger (sallallahu ‘alaihi wa sallam) replied, "The answerer has no better knowledge than the questioner. But I will inform you about its portents.\r\n1. When a slave (lady) gives birth to her master.\r\n2. When the shepherds of black camels start boasting and competing with others in the construction of higher buildings. And the Hour is one of five things which nobody knows except Allah.\r\nThe Prophet (sallallahu ‘alaihi wa sallam) then recited: "Verily, with Allah (Alone) is the knowledge of the Hour--." (31. 34) Then that man (Gabriel) left and the Prophet (sallallahu ‘alaihi wa sallam) asked his companions to call him back, but they could not see him. Then the Prophet (sallallahu ‘alaihi wa sallam) said, "That was Gabriel who came to teach the people their religion." Abu ''Abdullah said: He (the Prophet (sallallahu ‘alaihi wa sallam) ) considered all that as a part of faith.', NULL, 'Sahih al-Bukhari 50\r\nBook 2, Hadith 43\r\nVol. 1, Book 2, Hadith 48', 'en'),
(109, 55, NULL, 'حَدَّثَنَا إِبْرَاهِيمُ بْنُ حَمْزَةَ، قَالَ حَدَّثَنَا إِبْرَاهِيمُ بْنُ سَعْدٍ، عَنْ صَالِحٍ، عَنِ ابْنِ شِهَابٍ، عَنْ عُبَيْدِ اللَّهِ بْنِ عَبْدِ اللَّهِ، أَنَّ عَبْدَ اللَّهِ بْنَ عَبَّاسٍ، أَخْبَرَهُ قَالَ أَخْبَرَنِي أَبُو سُفْيَانَ، أَنَّ هِرَقْلَ، قَالَ لَهُ سَأَلْتُكَ هَلْ يَزِيدُونَ أَمْ يَنْقُصُونَ، فَزَعَمْتَ أَنَّهُمْ يَزِيدُونَ، وَكَذَلِكَ الإِيمَانُ حَتَّى يَتِمَّ‏.‏ وَسَأَلْتُكَ هَلْ يَرْتَدُّ أَحَدٌ سَخْطَةً لِدِينِهِ بَعْدَ أَنْ يَدْخُلَ فِيهِ، فَزَعَمْتَ أَنْ لاَ، وَكَذَلِكَ الإِيمَانُ حِينَ تُخَالِطُ بَشَاشَتُهُ الْقُلُوبَ، لاَ يَسْخَطُهُ أَحَدٌ‏', NULL, NULL, 'ar'),
(110, 55, '''Abdullah bin ''Abbas', 'I was informed by Abu Sufyan that Heraclius said to him, "I asked you whether they (followers of Muhammad) were increasing or decreasing. You replied that they were increasing. And in fact, this is the way of true Faith till it is complete in all respects. I further asked you whether there was anybody, who, after embracing his (the Prophet (sallallahu ‘alaihi wa sallam) s) religion (Islam) became displeased and discarded it. You replied in the negative, and in fact, this is (a sign of) true faith. When its delight enters the heart and mixes with them completely, nobody can be displeased with it."', NULL, 'Sahih al-Bukhari 51\r\nBook 2, Hadith 44\r\nVol. 1, Book 2, Hadith 49', 'en'),
(111, 56, NULL, 'حَدَّثَنَا أَبُو نُعَيْمٍ، حَدَّثَنَا زَكَرِيَّاءُ، عَنْ عَامِرٍ، قَالَ سَمِعْتُ النُّعْمَانَ بْنَ بَشِيرٍ، يَقُولُ سَمِعْتُ رَسُولَ اللَّهِ صلى الله عليه وسلم يَقُولُ ‏ "‏ الْحَلاَلُ بَيِّنٌ وَالْحَرَامُ بَيِّنٌ، وَبَيْنَهُمَا مُشَبَّهَاتٌ لاَ يَعْلَمُهَا كَثِيرٌ مِنَ النَّاسِ، فَمَنِ اتَّقَى الْمُشَبَّهَاتِ اسْتَبْرَأَ لِدِيِنِهِ وَعِرْضِهِ، وَمَنْ وَقَعَ فِي الشُّبُهَاتِ كَرَاعٍ يَرْعَى حَوْلَ الْحِمَى، يُوشِكُ أَنْ يُوَاقِعَهُ‏.‏ أَلاَ وَإِنَّ لِكُلِّ مَلِكٍ حِمًى، أَلاَ إِنَّ حِمَى اللَّهِ فِي أَرْضِهِ مَحَارِمُهُ، أَلاَ وَإِنَّ فِي الْجَسَدِ مُضْغَةً إِذَا صَلَحَتْ صَلَحَ الْجَسَدُ كُلُّهُ، وَإِذَا فَسَدَتْ فَسَدَ الْجَسَدُ كُلُّهُ‏.‏ أَلاَ وَهِيَ الْقَلْبُ ‏"‏‏', NULL, NULL, 'ar'),
(112, 56, 'An-Nu''man bin Bashir', 'I heard Allah''s Messenger (sallallahu ‘alaihi wa sallam) saying, ''Both legal and illegal things are evident but in between them there are doubtful (suspicious) things and most of the people have no knowledge about them. So whoever saves himself from these suspicious things saves his religion and his honor. And whoever indulges in these suspicious things is like a shepherd who grazes (his animals) near the Hima (private pasture) of someone else and at any moment he is liable to get in it. (O people!) Beware! Every king has a Hima and the Hima of Allah on the earth is His illegal (forbidden) things. Beware! There is a piece of flesh in the body if it becomes good (reformed) the whole body becomes good but if it gets spoilt the whole body gets spoilt and that is the heart.', NULL, 'Sahih al-Bukhari 52\r\nBook 2, Hadith 45\r\nVol. 1, Book 2, Hadith 50', 'en'),
(113, 57, NULL, 'حَدَّثَنَا عَلِيُّ بْنُ الْجَعْدِ، قَالَ أَخْبَرَنَا شُعْبَةُ، عَنْ أَبِي جَمْرَةَ، قَالَ كُنْتُ أَقْعُدُ مَعَ ابْنِ عَبَّاسٍ، يُجْلِسُنِي عَلَى سَرِيرِهِ فَقَالَ أَقِمْ عِنْدِي حَتَّى أَجْعَلَ لَكَ سَهْمًا مِنْ مَالِي، فَأَقَمْتُ مَعَهُ شَهْرَيْنِ، ثُمَّ قَالَ إِنَّ وَفْدَ عَبْدِ الْقَيْسِ لَمَّا أَتَوُا النَّبِيَّ صلى الله عليه وسلم قَالَ ‏"‏ مَنِ الْقَوْمُ أَوْ مَنِ الْوَفْدُ ‏"‏‏.‏ قَالُوا رَبِيعَةُ‏.‏ قَالَ ‏"‏ مَرْحَبًا بِالْقَوْمِ ـ أَوْ بِالْوَفْدِ ـ غَيْرَ خَزَايَا وَلاَ نَدَامَى ‏"‏‏.‏ فَقَالُوا يَا رَسُولَ اللَّهِ، إِنَّا لاَ نَسْتَطِيعُ أَنْ نَأْتِيَكَ إِلاَّ فِي شَهْرِ الْحَرَامِ، وَبَيْنَنَا وَبَيْنَكَ هَذَا الْحَىُّ مِنْ كُفَّارِ مُضَرَ، فَمُرْنَا بِأَمْرٍ فَصْلٍ، نُخْبِرْ بِهِ مَنْ وَرَاءَنَا، وَنَدْخُلْ بِهِ الْجَنَّةَ‏.‏ وَسَأَلُوهُ عَنِ الأَشْرِبَةِ‏.‏ فَأَمَرَهُمْ بِأَرْبَعٍ، وَنَهَاهُمْ عَنْ أَرْبَعٍ، أَمَرَهُمْ بِالإِيمَانِ بِاللَّهِ وَحْدَهُ‏.‏ قَالَ ‏"‏ أَتَدْرُونَ مَا الإِيمَانُ بِاللَّهِ وَحْدَهُ ‏"‏‏.‏ قَالُوا اللَّهُ وَرَسُولُهُ أَعْلَمُ‏.‏ قَالَ ‏"‏ شَهَادَةُ أَنْ لاَ إِلَهَ إِلاَّ اللَّهُ وَأَنَّ مُحَمَّدًا رَسُولُ اللَّهِ، وَإِقَامُ الصَّلاَةِ، وَإِيتَاءُ الزَّكَاةِ، وَصِيَامُ رَمَضَانَ، وَأَنْ تُعْطُوا مِنَ الْمَغْنَمِ الْخُمُسَ ‏"‏‏.‏ وَنَهَاهُمْ عَنْ أَرْبَعٍ عَنِ الْحَنْتَمِ وَالدُّبَّاءِ وَالنَّقِيرِ وَالْمُزَفَّتِ‏.‏ وَرُبَّمَا قَالَ الْمُقَيَّرِ‏.‏ وَقَالَ ‏"‏ احْفَظُوهُنَّ وَأَخْبِرُوا بِهِنَّ مَنْ وَرَاءَكُمْ ‏"‏‏.‏', NULL, NULL, 'ar'),
(114, 57, 'Abu Jamra', 'I used to sit with Ibn ''Abbas and he made me sit on his sitting place. He requested me to stay with him in order that he might give me a share from his property. So I stayed with him for two months. Once he told (me) that when the delegation of the tribe of ''Abdul Qais came to the Prophet (sallallahu ‘alaihi wa sallam) , the Prophet (sallallahu ‘alaihi wa sallam) asked them, "Who are the people (i.e. you)? (Or) who are the delegate?" They replied, "We are from the tribe of Rabi''a." Then the Prophet (sallallahu ‘alaihi wa sallam) said to them, "Welcome! O people (or O delegation of ''Abdul Qais)! Neither will you have disgrace nor will you regret." They said, "O Allah''s Messenger (sallallahu ‘alaihi wa sallam) ! We cannot come to you except in the sacred month and there is the infidel tribe of Mudar intervening between you and us. So please order us to do something good (religious deeds) so that we may inform our people whom we have left behind (at home), and that we may enter Paradise (by acting on them)." Then they asked about drinks (what is legal and what is illegal). The Prophet (sallallahu ‘alaihi wa sallam) ordered them to do four things and forbade them from four things. He ordered them to believe in Allah Alone and asked them, "Do you know what is meant by believing in Allah Alone?" They replied, "Allah and His Apostle know better." Thereupon the Prophet (sallallahu ‘alaihi wa sallam) said, "It means:\r\n1. To testify that none has the right to be worshipped but Allah and Muhammad is Allah''s Messenger (sallallahu ‘alaihi wa sallam) .\r\n2. To offer prayers perfectly\r\n3. To pay the Zakat (obligatory charity)\r\n4. To observe fast during the month of Ramadan.\r\n5. And to pay Al-Khumus (one fifth of the booty to be given in Allah''s Cause).\r\nThen he forbade them four things, namely, Hantam, Dubba,'' Naqir Ann Muzaffat or Muqaiyar; (These were the names of pots in which Alcoholic drinks were prepared) (The Prophet (sallallahu ‘alaihi wa sallam) mentioned the container of wine and he meant the wine itself). The Prophet (sallallahu ‘alaihi wa sallam) further said (to them): "Memorize them (these instructions) and convey them to the people whom you have left behind."', NULL, 'Sahih al-Bukhari 53\r\nBook 2, Hadith 46\r\nVol. 1, Book 2, Hadith 51', 'en'),
(115, 58, NULL, 'حَدَّثَنَا عَبْدُ اللَّهِ بْنُ مَسْلَمَةَ، قَالَ أَخْبَرَنَا مَالِكٌ، عَنْ يَحْيَى بْنِ سَعِيدٍ، عَنْ مُحَمَّدِ بْنِ إِبْرَاهِيمَ، عَنْ عَلْقَمَةَ بْنِ وَقَّاصٍ، عَنْ عُمَرَ، أَنَّ رَسُولَ اللَّهِ صلى الله عليه وسلم قَالَ ‏ "‏ الأَعْمَالُ بِالنِّيَّةِ، وَلِكُلِّ امْرِئٍ مَا نَوَى، فَمَنْ كَانَتْ هِجْرَتُهُ إِلَى اللَّهِ وَرَسُولِهِ، فَهِجْرَتُهُ إِلَى اللَّهِ وَرَسُولِهِ، وَمَنْ كَانَتْ هِجْرَتُهُ لِدُنْيَا يُصِيبُهَا، أَوِ امْرَأَةٍ يَتَزَوَّجُهَا، فَهِجْرَتُهُ إِلَى مَا هَاجَرَ إِلَيْهِ ‏"‏‏.', NULL, NULL, 'ar'),
(116, 58, '''Umar bin Al-Khattab', 'Allah''s Messenger (sallallahu ‘alaihi wa sallam) said, "The reward of deeds depends upon the intention and every person will get the reward according to what he has intended. So whoever emigrated for Allah and His Apostle, then his emigration was for Allah and His Apostle. And whoever emigrated for worldly benefits or for a woman to marry, his emigration was for what he emigrated for."', NULL, 'Sahih al-Bukhari 54\r\nBook 2, Hadith 47\r\nVol. 1, Book 2, Hadith 52', 'en'),
(117, 59, NULL, 'حَدَّثَنَا الْحَكَمُ بْنُ نَافِعٍ قَالَ أَخْبَرَنَا شُعَيْبٌ عَنْ الزُّهْرِيِّ قَالَ حَدَّثَنِي عَامِرُ بْنُ سَعْدٍ عَنْ سَعْدِ بْنِ أَبِي وَقَّاصٍ أَنَّهُ أَخْبَرَهُ أَنَّ رَسُولَ اللَّهِ صَلَّى اللَّهُ عَلَيْهِ وَسَلَّمَ قَالَ "إِنَّكَ لَنْ تُنْفِقَ نَفَقَةً تَبْتَغِي بِهَا وَجْهَ اللَّهِ إِلَّا أُجِرْتَ عَلَيْهَا حَتَّى مَا تَجْعَلُ فِي فَمِ امْرَأَتِكَ"', NULL, NULL, 'ar'),
(118, 59, 'Sa''d bin Abi Waqqas', 'Allah''s Messenger (sallallahu ‘alaihi wa sallam) said, "You will be rewarded for whatever you spend for Allah''s sake even if it were a morsel which you put in your wife''s mouth."', NULL, 'Sahih al-Bukhari 56\r\nBook 2, Hadith 49\r\nVol. 1, Book 2, Hadith 54', 'en'),
(119, 60, NULL, 'حَدَّثَنَا حَجَّاجُ بْنُ مِنْهَالٍ، قَالَ حَدَّثَنَا شُعْبَةُ، قَالَ أَخْبَرَنِي عَدِيُّ بْنُ ثَابِتٍ، قَالَ سَمِعْتُ عَبْدَ اللَّهِ بْنَ يَزِيدَ، عَنْ أَبِي مَسْعُودٍ، عَنِ النَّبِيِّ صلى الله عليه وسلم قَالَ ‏ "‏ إِذَا أَنْفَقَ الرَّجُلُ عَلَى أَهْلِهِ يَحْتَسِبُهَا فَهُوَ لَهُ صَدَقَةٌ ‏', NULL, NULL, 'ar'),
(120, 60, 'Abu Mas''ud', 'The Prophet (sallallahu ‘alaihi wa sallam) said, "If a man spends on his family (with the intention of having a reward from Allah) sincerely for Allah''s sake then it is a (kind of) alms-giving in reward for him.', NULL, 'Sahih al-Bukhari 55\r\nBook 2, Hadith 48\r\nVol. 1, Book 2, Hadith 53', 'en'),
(121, 61, NULL, 'حَدَّثَنَا مُسَدَّدٌ، قَالَ حَدَّثَنَا يَحْيَى، عَنْ إِسْمَاعِيلَ، قَالَ حَدَّثَنِي قَيْسُ بْنُ أَبِي حَازِمٍ، عَنْ جَرِيرِ بْنِ عَبْدِ اللَّهِ، قَالَ بَايَعْتُ رَسُولَ اللَّهِ صلى الله عليه وسلم عَلَى إِقَامِ الصَّلاَةِ، وَإِيتَاءِ الزَّكَاةِ، وَالنُّصْحِ لِكُلِّ مُسْلِمٍ‏.‏', NULL, NULL, 'ar'),
(122, 61, 'Jarir bin Abdullah', 'I gave the pledge of allegiance to Allah''s Messenger (sallallahu ‘alaihi wa sallam) for the following:\r\n1. offer prayers perfectly\r\n2. pay the Zakat (obligatory charity)\r\n3. and be sincere and true to every Muslim.', NULL, 'Sahih al-Bukhari 57\r\nBook 2, Hadith 50\r\nVol. 1, Book 2, Hadith 55', 'en'),
(123, 62, NULL, 'حَدَّثَنَا أَبُو النُّعْمَانِ، قَالَ حَدَّثَنَا أَبُو عَوَانَةَ، عَنْ زِيَادِ بْنِ عِلاَقَةَ، قَالَ سَمِعْتُ جَرِيرَ بْنَ عَبْدِ اللَّهِ، يَقُولُ يَوْمَ مَاتَ الْمُغِيرَةُ بْنُ شُعْبَةَ قَامَ فَحَمِدَ اللَّهَ وَأَثْنَى عَلَيْهِ وَقَالَ عَلَيْكُمْ بِاتِّقَاءِ اللَّهِ وَحْدَهُ لاَ شَرِيكَ لَهُ، وَالْوَقَارِ وَالسَّكِينَةِ حَتَّى يَأْتِيَكُمْ أَمِيرٌ، فَإِنَّمَا يَأْتِيكُمُ الآنَ، ثُمَّ قَالَ اسْتَعْفُوا لأَمِيرِكُمْ، فَإِنَّهُ كَانَ يُحِبُّ الْعَفْوَ‏.‏ ثُمَّ قَالَ أَمَّا بَعْدُ، فَإِنِّي أَتَيْتُ النَّبِيَّ صلى الله عليه وسلم قُلْتُ أُبَايِعُكَ عَلَى الإِسْلاَمِ‏.‏ فَشَرَطَ عَلَىَّ وَالنُّصْحِ لِكُلِّ مُسْلِمٍ‏.‏ فَبَايَعْتُهُ عَلَى هَذَا، وَرَبِّ هَذَا الْمَسْجِدِ إِنِّي لَنَاصِحٌ لَكُمْ‏.‏ ثُمَّ اسْتَغْفَرَ وَنَزَلَ‏.‏', NULL, NULL, 'ar'),
(124, 62, 'Ziyad bin''Ilaqa', 'I heard Jarir bin ''Abdullah (Praising Allah). On the day when Al-Mughira bin Shu''ba died, he (Jarir) got up (on the pulpit) and thanked and praised Allah and said, "Be afraid of Allah alone Who has none along with Him to be worshipped.(You should) be calm and quiet till the (new) chief comes to you and he will come to you soon. Ask Allah''s forgiveness for your (late) chief because he himself loved to forgive others." Jarir added, "Amma badu (now then), I went to the Prophet and said, ''I give my pledge of allegiance to you for Islam." The Prophet (sallallahu ‘alaihi wa sallam) conditioned (my pledge) for me to be sincere and true to every Muslim so I gave my pledge to him for this. By the Lord of this mosque! I am sincere and true to you (Muslims). Then Jarir asked for Allah''s forgiveness and came down (from the pulpit).', NULL, NULL, 'en');
